import request from '@/utils/request'
const url = {
  work: '/spang-safety/safety/soil-work-ticket'
}
export const saveForm = (data, method = 'post') => {
  return request({
    url: url.work,
    method: method,
		data
  })
}

export const getDetail = (id) => {
  return request({
    url: url.work + '/' + id,
    method: 'get'
  })
}

export const loadHistory = (id) => {
	return request({
	  url: url.work + '/user/history',
	  method: 'get',
    params: { id }
	})
}

export const getConfirmation = (data) => {
  return request({
    url: url.work + '/locale',
    method: 'get',
		params: data
  })
}

export const getApprovalHistory = (data) => {
	return request({
	  url: url.work + '/approvehis',
	  method: 'get',
		params: data
	})
}
