import request from '@/utils/request'
const url = {
  work: '/spang-safety/tickets/confined/space'
}
export const saveForm = (data) => {
  return request({
    url: url.work + '/insert',
    method: 'post',
		data
  })
}

export const update = (data) => {
  return request({
    url: url.work + '/update',
    method: 'post',
		data
  })
}

export const getDetail = (id) => {
  return request({
    url: url.work + '/selectById?id=' + id,
    method: 'get'
  })
}

export const loadHistory = (id) => {
	return request({
	  url: url.work + '/user/history',
	  method: 'get',
    params: { id }
	})
}