import request from '@/utils/request'
const hotworkApi = {
  HotworkMain: '/spang-safety/tickets/hotwork/main',
  PersonHistory: '/spang-safety/tickets/hotwork/peoplehis/loadPeopleHisListByMainId',
  HotworkPeopleWork: '/spang-safety/tickets/hotwork/peoplework',
  RiskIdentification: '/spang-safety/tickets/hotwork/riskfillin',
  SafetyForm: '/spang-safety/tickets/hotwork/safefillin',
  GetAnalysisHistory: '/spang-safety/tickets/hotwork/analysis/loadAnalysisHisList',
  ApprovalHistory: '/spang-safety/tickets/hotwork/approvehis/loadApproveHisList',
  GetInspectionHistory: '/spang-safety/tickets/hotwork/acceptcheck'
}

// 动火作业API Start
// 提交数据 （基础表单）
export const addHotwork = (params) => {
  return request({
    url: hotworkApi.HotworkMain,
    method: 'post',
    data: params
  })
}

// 更新数据 （基础表单）
export const updateHotwork = (params) => {
  return request({
    url: hotworkApi.HotworkMain,
    method: 'put',
    data: params
  })
}

// 根据id 查看详情 (基础信息)
export const getHotworkDetail = (params) => {
  return request({
    url: hotworkApi.HotworkMain + `?id=${params.id}`,
    method: 'get'
  })
}
// 人员更换历史  基础信息 && 作业单位
export const getPersonHistory = (params) => {
  const { mainId, current, size } = params
  return request({
    url: hotworkApi.PersonHistory + `?mainId=${mainId}&current=${current}&size=${size}`,
    method: 'get'
  })
}

 // 新增&&修改 数据 （作业单位）
export const addHotworkPeople = (params) => {
  return request({
    url: hotworkApi.HotworkPeopleWork,
    method: 'put',
    data: params
  })
}

