import request from '@/utils/request'

const resourcesApi = {
  SuppliesData: '/resources/supplies/research',
  SuppliesOptions: '/resources/supplies/options',
  SafetyAccident: '/safety/accident/list'
}

export function suppliesGet (parameter) {
  return request({
    url: resourcesApi.SuppliesData,
    method: 'post',
    data: parameter
  })
}

export function suppliesOptions () {
  return request({
    url: resourcesApi.SuppliesOptions,
    method: 'get'
  })
}

export function accidentList (parameter) {
  return request({
    url: resourcesApi.SafetyAccident,
    method: 'post',
    data: parameter
  })
}
