<template>
  <div :class="wrpCls">
    <div :class="prefixCls" class="right-title" disabled="false" v-show="false">
      <h1 class="title">胜帮科技</h1>
    </div>
    <!--    <a-auto-complete :data-source="data1" placeholder="111">-->
    <!--      <a-button slot="suffix"><a-icon  type="search"/></a-button>-->
    <!--    </a-auto-complete>-->
    <!--    <a-popover placement="bottom" @click="turnControl" :class="prefixCls">-->
    <!--      <span slot="content" style="margin-top: 8px">控制台</span>-->
    <!--      <span style="display: inline-block"><a-icon type="appstore" style="font-size: 16px;"/></span>-->
    <!--    </a-popover>-->
    <select-lang :class="prefixCls" v-if="false"/>
    <avatar-dropdown :menu="showMenu" :current-user="currentUser" :class="prefixCls" />
  </div>
</template>

<script>
import AvatarDropdown from './AvatarDropdown'
import SelectLang from '@/components/SelectLang'
import { mapGetters } from 'vuex'
import { getToken } from '@/utils/auth'
import website from '@/config/website'

export default {
  name: 'RightContent',
  components: {
    AvatarDropdown,
    SelectLang
  },
  props: {
    prefixCls: {
      type: String,
      default: 'ant-pro-global-header-index-action'
    },
    isMobile: {
      type: Boolean,
      default: () => false
    },
    topMenu: {
      type: Boolean,
      required: true
    },
    theme: {
      type: String,
      required: true
    }
  },
  data () {
    return {
      showMenu: false,
      currentUser: {},
      data1: ['111', '22']
    }
  },
  computed: {
    ...mapGetters(['addRouters']),
    wrpCls () {
      return {
        'ant-pro-global-header-index-right': true,
        [`ant-pro-global-header-index-${(this.isMobile || !this.topMenu) ? 'light' : this.theme}`]: true
      }
    }
  },
  mounted () {
    this.currentUser = {
      name: this.$store.getters.userInfo.realName
    }
  },
  methods: {
    turnControl () {
      window.location.href = website.indexPath + `?access_token=${getToken()}`
    }
  }
}
</script>
