<template>
  <a-modal
    :title="modalTitle"
    :visible="visible"
    :confirmLoading="loading"
    @ok="() => { $emit('ok') }"
    @cancel="() => { $emit('cancel') }"
    ok-text="确定"
    cancel-text="取消"
    width="50%"
  >
    <a-spin :spinning="loading">
      <a-form :form="form">
        <a-row :gutter="24">
          <a-col :md="24">
            <a-form-item label="机构名称">
              <a-input v-decorator="['company',{ rules:[{ required: true, message: '请输入机构名称' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <a-form-item label="物资名称">
              <a-input v-decorator="['name',{ rules:[{ required: true, message: '请输入物资名称' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="12">
            <a-form-item label="编号">
              <a-input v-decorator="['id',{ rules:[{ required: true, message: '请输入编号' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <a-form-item label="型号">
              <a-input v-decorator="['model',{ rules:[{ required: false, message: '请输入型号' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="12">
            <a-form-item label="类型">
              <a-select v-decorator="['type',{ rules:[{ required: false, message: '请选择类型' }]}]" :disabled="disabled">
                <a-select-option v-for="d in selectOptions" :key="d.value">
                  {{ d.text }}
                </a-select-option>
              </a-select>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <a-form-item label="经度">
              <a-input v-decorator="['longitude',{ rules:[{ required: false, message: '请输入经度' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="12">
            <a-form-item label="纬度">
              <a-input v-decorator="['latitude',{ rules:[{ required: false, message: '请输入纬度' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="24">
            <a-form-item label="存放地点">
              <a-input v-decorator="['location',{ rules:[{ required: false, message: '请输入存放地点' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="24">
            <a-form-item label="用途">
              <a-input v-decorator="['use',{ rules:[{ required: false, message: '请输入用途' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <a-form-item label="管理人">
              <a-input v-decorator="['admin',{ rules:[{ required: false, message: '请输入管理人' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="12">
            <a-form-item label="联系电话">
              <a-input v-decorator="['mobile',{ rules:[{ required: false, message: '请输入联系电话' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <a-form-item label="电话联系人">
              <a-input v-decorator="['contact',{ rules:[{ required: false, message: '请输入电话联系人' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="12">
            <a-form-item label="值班电话">
              <a-input v-decorator="['workMobile',{ rules:[{ required: false, message: '请输入值班电话' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
        </a-row>
      </a-form>
    </a-spin>
  </a-modal>
</template>

<script>
import pick from 'lodash.pick'
import { suppliesColumns } from '@/views/emergency/resources/table'

const fields = suppliesColumns.map(res => {
  return res.dataIndex
})

export default {
  name: 'SuppliesModal',
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    loading: {
      type: Boolean,
      default: () => false
    },
    model: {
      type: Object,
      default: () => null
    },
    selectOptions: {
      type: Object,
      default: () => null
    },
    modalTitle: {
      type: String,
      default: '操作'
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data () {
    return {
      form: this.$form.createForm(this)
    }
  },
  created () {
    // 防止表单未注册
    fields.forEach(v => this.form.getFieldDecorator(v))
    // 当 model 发生改变时，为表单设置值
    this.$watch('model', () => {
      this.previewImage = this.model.logo
      this.model && this.form.setFieldsValue(pick(this.model, fields))
    })
  }
}
</script>
