<template>
  <page-container :title="false">
    <a-card :bordered="false">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="6">
              <a-form-item label="专家名称">
                <a-input v-model="queryParam.name" />
              </a-form-item>
            </a-col>
            <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="$refs.expertsTable.refresh(true)">查询</a-button>
                <a-button type="second" style="margin-left: 10px" @click="expertsRefresh">重置</a-button>
              </span>
            </a-col>
          </a-row>
        </a-form>
      </div>
      <div class="table-operator">
        <a-button type="primary" icon="plus" @click="expertsAdd">新增</a-button>
        <a-button icon="delete" :disabled="!hasSelected" @click="expertsRemove">删除</a-button>
        <a-button icon="download" :disabled="!hasSelected" @click="fileDownload">导出当前</a-button>
        <a-button icon="download" @click="filesDownload">导出所有</a-button>
        <a-button icon="upload" @click="fileUpload">导入</a-button>
      </div>
      <s-table
        ref="expertsTable"
        size="default"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="true"
        :rowSelection="rowSelection"
        showPagination="auto"
      >
        <span slot="action" slot-scope="text, record">
          <template>
            <a @click="expertsCheck(record)">查看</a>
            <a-divider type="vertical"/>
            <a @click="expertsEdit(record)">编辑</a>
          </template>
        </span>
      </s-table>
      <experts-modal
        ref="expertsModal"
        :visible="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        @cancel="expertsModalCancel"
        @ok="expertsModalOk"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { expertsColumns } from '@/views/emergency/resources/table'
import ExpertsModal from '@/views/emergency/resources/components/ExpertsModal'

const mockData = {
  totalCount: 1,
  totalPage: 20,
  pageNo: 1,
  pageSize: 10,
  data: [
    {
      company: '测试',
      sex: '女',
      degree: '本科',
      profession: '教育',
      name: '张三'
    }
  ]
}

export default {
  components: {
    STable,
    ExpertsModal
  },
  data () {
    return {
      // common props
      commonTitle: '',
      commonVisible: false,
      commonData: null,
      commonMode: true,
      commonDisabled: false,
      confirmLoading: false,
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        return new Promise(resolve => {
          resolve(mockData)
        })
      }
    }
  },
  computed: {
    columns: () => expertsColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    commonInit () {
      this.commonVisible = false
      this.confirmLoading = false
      this.commonTitle = ''
      this.commonMode = true
      this.commonDisabled = false
    },
    expertsRefresh () {
      this.$message.success('重置')
      this.$refs.expertstable.refresh(true)
      this.queryParam = {}
    },
    expertsAdd () {
      // this.$message.success('新增')
      this.commonTitle = '新增数据'
      this.commonVisible = true
    },
    expertsRemove () {
      this.$message.success('删除')
    },
    expertsCheck (record) {
      // this.$message.success('查看')
      this.commonVisible = true
      this.commonTitle = '查看数据'
      this.commonDisabled = true
      this.commonData = { ...record }
    },
    expertsEdit (record) {
      // this.$message.success('编辑')
      this.commonTitle = '编辑数据'
      this.commonData = { ...record }
      this.commonVisible = true
    },
    fileDownload () {
      this.$message.success('导出当前文件')
    },
    filesDownload () {
      this.$message.success('导出所有文件')
    },
    fileUpload () {
      this.$message.success('上传文件')
    },
    expertsModalCancel () {
      this.commonInit()
      const form = this.$refs.expertsModal.form
      form.resetFields()
    },
    expertsModalOk () {
      const _this = this
      if (_this.commonDisabled) {
        _this.$message.success('查看返回')
        _this.commonInit()
      } else {
        const form = _this.$refs.expertsModal.form
        _this.confirmLoading = true
        form.validateFields((errors, values) => {
          if (!errors) {
            const formData = values
            console.log(formData)
            if (_this.commonMode) {
              // 新增接口
              // _this.$message.success('新增提交')
            } else {
              // 编辑接口
              // _this.$message.success('编辑提交')
            }
            setTimeout(function () {
              _this.commonInit()
            }, 1000)
          } else {
            _this.confirmLoading = false
          }
        })
      }
    }
  }
}
</script>
