<template>
  <page-container :title="false">
    <a-card :bordered="false">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="6">
              <a-form-item label="名称">
                <a-input v-model="queryParam.name"/>
              </a-form-item>
            </a-col>
            <a-col :md="6">
              <a-form-item label="种类">
                <a-select v-model="queryParam.type">
                  <a-select-option v-for="d in selectOptions" :key="d.value">
                    {{ d.text }}
                  </a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="$refs.table.refresh(true)">查询</a-button>
                <a-button type="second" @click="refreshRecord" style="margin-left: 10px">重置</a-button>
              </span>
            </a-col>
          </a-row>
        </a-form>
      </div>
      <div class="table-operator">
        <a-button type="primary" icon="plus" @click="addRecord">新增</a-button>
        <a-button icon="delete" @click="suppliesRemove" :disabled="!hasSelected">删除</a-button>
        <a-button icon="download" :disabled="!hasSelected" @click="fileDownload">导出当前</a-button>
        <a-button icon="download" @click="filesDownload">导出所有</a-button>
        <a-button icon="upload" @click="fileUpload">导入</a-button>
      </div>
      <s-table
        ref="table"
        size="default"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="true"
        :rowSelection="rowSelection"
        showPagination="auto"
      >
        <span slot="action" slot-scope="text, record">
          <template>
            <a @click="checkRecord(record)">查看</a>
            <a-divider type="vertical"/>
            <a @click="editRecord(record)">编辑</a>
          </template>
        </span>
      </s-table>
      <supplies-modal
        ref="suppliesModal"
        :visible="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :select-options="selectOptions"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        @cancel="modalCancel"
        @ok="modalOk"
      />
    </a-card>
  </page-container>
</template>
<script>
import TagSelectOption from '@/components/TagSelect/TagSelectOption'
import { STable } from '@/components'
import { suppliesColumns } from '@/views/emergency/resources/table'
import { suppliesGet, suppliesOptions } from '@/api/resources'
import SuppliesModal from '@/views/emergency/resources/components/SuppliesModal'
import { mapActions } from 'vuex'

export default {
  name: 'Resources',
  components: {
    TagSelectOption,
    STable,
    SuppliesModal
  },
  data () {
    return {
      // common
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: null,
      commonMode: true, // add：true edit：false
      commonDisabled: false,
      selectOptions: [],
      queryParam: {},
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        console.log(requestParameters)
        return suppliesGet(requestParameters).then(res => {
          console.log(res)
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          console.log(data)
          return data
        })
      },
      selectedRowKeys: [],
      selectedRows: []
    }
  },
  created () {
    this.getSelectOptions()
  },
  computed: {
    columns: () => suppliesColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    commonInit () {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.commonMode = true
      this.confirmLoading = false
      const form = this.$refs.suppliesModal.form
      form.resetFields()
    },
    getSelectOptions () {
      suppliesOptions().then(res => {
        this.selectOptions = res.data
    })
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    refreshRecord () {
      this.queryParam = {}
      this.$refs.table.refresh(true)
    },
    addRecord () {
      this.commonTitle = '新增数据'
      // this.$message.success('新增')
      this.commonMode = true
      this.commonVisible = true
    },
    suppliesRemove () {
      this.$message.success('删除')
    },
    checkRecord (record) {
      // this.$message.success('查看')
      this.commonTitle = '查看数据'
      this.commonVisible = true
      this.commonData = { ...record }
      this.commonDisabled = true
    },
    editRecord (record) {
      this.commonTitle = '编辑数据'
      // this.$message.success('编辑')
      this.commonMode = false
      this.commonVisible = true
      this.commonData = { ...record }
    },
      fileDownload () {
        this.$message.success('导出当前文件')
      },
      filesDownload () {
        this.$message.success('导出所有文件')
      },
      fileUpload () {
        // this.$message.success('编辑')
      },
    modalCancel () {
      this.commonInit()
    },
    modalOk () {
      const _this = this
      if (_this.commonDisabled) {
        _this.$message.success('查看返回')
        _this.commonInit()
      } else {
        const form = _this.$refs.suppliesModal.form
        _this.confirmLoading = true
        form.validateFields((errors, values) => {
          if (!errors) {
            const requestForm = values
            console.log(requestForm)
            if (_this.commonMode) {
              // 新增接口
              // _this.$message.success('新增提交')
            } else {
              // 编辑接口
              // _this.$message.success('编辑提交')
            }
            setTimeout(() => {
              _this.commonInit()
            }, 1000)
          } else {
            _this.confirmLoading = false
          }
        })
      }
    }
  }
}
</script>
