<template>
  <page-container :title="false">
    <a-card
      :bordered="false"
      :tabList="tabList"
      :activeTabKey="tabKey"
      @tabChange="key => tabChange(key, 'tabKey')"
    >
    </a-card>
    <div v-if="tabKey === 'tab1'">
      <transport></transport>
    </div>
    <div v-else-if="tabKey === 'tab2'">
      企业细查
    </div>
    <div v-else-if="tabKey === 'tab3'">
      统计分析
    </div>
  </page-container>
</template>

<script>
import transport from '@/views/environment/waste/transport'

export default {
  name: 'Waste',
  components: {
      transport
  },
  data () {
    return {
      tabList: [
        {
          key: 'tab1',
          tab: '转运联单'
        },
        {
          key: 'tab2',
          tab: '电子台账'
        },
        {
          key: 'tab3',
          tab: '预警信息'
        }
      ],
      tabKey: 'tab1'
    }
  },
  methods: {
    tabChange (key, type) {
      this.$message.success(`${ type }`)
      this[type] = key
    }
  }
}
</script>
