<template>
  <page-container :title="false">
    <a-card :bordered="false">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="4">
              <a-form-item label="关键字">
                <a-input v-model="queryParam.keyword"/>
              </a-form-item>
            </a-col>
            <!-- <a-col :md="6">
              <a-form-item label="责任单位">
                <a-tree-select
                  v-model="value"
                  style="width: 100%"
                  :tree-data="treeData"
                  :show-checked-strategy="SHOW_PARENT"
                  search-placeholder="Please select"
                />
              </a-form-item>
            </a-col>
            <a-col :md="4">
              <a-form-item label="创建人">
                <a-input v-model="queryParam.creator"/>
              </a-form-item>
            </a-col> -->
            <a-col :md="4">
              <a-form-item label="事故类别">
                <a-select v-model="queryParam.type">
                  <a-select-option v-for="d in selectOptions" :key="d.dictKey">
                    {{ d.dictValue }}
                  </a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :md="6">
              <a-form-item label="创建时间">
                <a-range-picker @change="onCreateChange" v-model="timeRange.create" :valueFormat="dateFormat"/>
              </a-form-item>
            </a-col>
            <a-col :md="6">
              <a-form-item label="发生时间">
                <a-range-picker @change="onStartChange" v-model="timeRange.start" :valueFormat="dateFormat"/>
              </a-form-item>
            </a-col>

            <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="accidentResearch">查询</a-button>
                <a-button type="second" @click="refreshRecord" style="margin-left: 10px">重置</a-button>
              </span>
            </a-col>
          </a-row>
          <a-row :gutter="24">
            <!-- <a-col :md="6">
              <a-form-item label="创建时间">
                <a-range-picker @change="onChange" />
              </a-form-item>
            </a-col>
            <a-col :md="6">
              <a-form-item label="发生时间">
                <a-range-picker @change="onChange" />
              </a-form-item>
            </a-col> -->
          </a-row>
        </a-form>
      </div>
      <div class="table-operator">
        <a-button type="primary" icon="plus" @click="addRecord" v-if="permission.accident_add">新增</a-button>
        <a-button icon="delete" @click="removeRecords" :disabled="!hasSelected" v-if="permission.accident_deletes">删除</a-button>
        <!-- <a-button icon="download" :disabled="!hasSelected" @click="fileDownload">导出当前</a-button>
        <a-button icon="download" @click="filesDownload">导出所有</a-button>
        <a-button icon="upload" @click="fileUpload">导入</a-button> -->
      </div>
      <s-table
        ref="table"
        size="default"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        :rowSelection="rowSelection"
        showPagination="true"
        :scroll="{x:500,y:`calc(100vh - 350px)`}"
      >
        <span slot="reportName" slot-scope="text, record">
          <template>
            <a-button type="link" @click="fileDownLoad(record.docUrl)" :disabled="text.length===0">{{ text || '暂无文件' }}</a-button>
          </template>
        </span>
        <span slot="action" slot-scope="text, record">
          <template>
            <a @click="checkRecord(record)" v-if="permission.accident_view">查看</a>
            <a-divider type="vertical"/>
            <a @click="editRecord(record)" v-if="permission.accident_edit">编辑</a>
            <a-divider type="vertical"/>
            <a @click="removeRecord(record)" v-if="permission.accident_delete">删除</a>
          </template>
        </span>
      </s-table>
      <accident-modal
        ref="modal"
        :visible="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :select-options="selectOptions"
        :level-options="levelOptions"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        @cancel="modalCancel"
        @ok="modalOk"
      />
      <contractor-detail
        mode="accident"
        :visible="detailVisible"
        :info="detailInfo"
        @cancel="detailCancel"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { accidentColumns } from '@/views/safety/table'
import { getAccidentList, submitAccidentItem, deleteAccident, getAccidentType, fileDownload, getAccidentLevel } from '@/api/safety'
import AccidentModal from '@/views/safety/accident/AccidentModal'
import { mapGetters } from 'vuex'
import ContractorDetail from '@/views/safety/contractor/ContractorDetail'

export default {
  name: 'AccidentManagement',
  components: {
    STable,
    AccidentModal,
    ContractorDetail
    // TreeSelect
  },
  data () {
    return {
      dateFormat: 'YYYY-MM-DD HH:mm:ss',
      // common
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonMode: true, // add：true edit：false
      commonDisabled: false,
      timeRange: {},
      queryParam: {},
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        console.log(requestParameters)
        return getAccidentList(requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          return data
        })
      },
      selectedRowKeys: [],
      selectedRows: [],
      // select
      selectOptions: [],
      levelOptions: [],
      value: [],
      detailVisible: false,
      detailInfo: {}
    }
  },
  created () {
    this.getSelectOptions()
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => accidentColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    commonInit () {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.commonMode = true
      this.confirmLoading = false
      this.commonData = {}
      const form = this.$refs.modal.form
      form.resetFields()
    },
    getSelectOptions () {
      getAccidentType().then(res => {
        this.selectOptions = res.data
      })
      getAccidentLevel().then(res => {
        this.levelOptions = res.data
      })
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    onCreateChange (date, dateString) {
      console.log(date, dateString)
      this.queryParam.startCreateTime = date[0]
      this.queryParam.endCreateTime = date[1]
      console.log(this.queryParam)
    },
    onStartChange (date, dateString) {
      console.log(date, dateString)
      this.queryParam.startOccurTime = date[0]
      this.queryParam.endOccurTime = date[1]
      console.log(this.queryParam)
    },
    accidentResearch () {
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    refreshRecord () {
      this.queryParam = {}
      this.timeRange = {}
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord () {
      this.commonTitle = '新增数据'
      // this.$message.success('新增')
      this.commonMode = true
      this.commonVisible = true
    },
    removeRecords () {
      var ids = ''
      this.selectedRows.forEach((item, index) => {
          ids = ids + item.id + ','
        })
        console.log(ids)
        this.showDeleteConfirm(ids)
    },
    removeRecord (record) {
      console.log(record)
      this.showDeleteConfirm(record.id)
    },
    checkRecord (record) {
      this.detailInfo = { ...record }
      this.detailVisible = true
    },
    editRecord (record) {
      this.commonTitle = '编辑数据'
      // this.$message.success('编辑')
      this.commonMode = false
      this.commonVisible = true
      this.commonData = { ...record }
    },
      fileDownload () {
        this.$message.success('导出当前文件')
      },
      filesDownload () {
        this.$message.success('导出所有文件')
      },
      fileUpload () {
        // this.$message.success('编辑')
      },
    modalCancel () {
      this.commonInit()
    },
    modalOk () {
      const _this = this
      if (_this.commonDisabled) {
        _this.$message.success('查看返回')
        _this.commonInit()
      } else {
        const form = _this.$refs.modal.form
        _this.confirmLoading = true
        form.validateFields((errors, values) => {
          if (!errors) {
            console.log(values)
            var requestForm = values
            requestForm.id = this.commonData.id
            submitAccidentItem(requestForm).then(res => {
              console.log(res)
                if (res.code === 200) {
                _this.$message.success('提交成功')
                _this.selectedRows = []
                _this.selectedRowKeys = []
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error('提交失败')
              }
            }).catch(error => {
                console.log('error' + error)
                _this.$message.error('提交失败')
            })
            setTimeout(() => {
              _this.commonInit()
            }, 1000)
          } else {
            console.log(errors)
            _this.confirmLoading = false
          }
        })
      }
    },
    showDeleteConfirm (params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk () {
          console.log('OK')
          deleteAccident(params).then(res => {
            console.log(res)
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh(true)
            } else {
              _this.$message.success('删除失败')
            }
            })
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    },
    fileDownLoad (url) {
      fileDownload({ link: url }).then(res => {
        window.open(res.data)
      })
    },
    detailCancel () {
      this.detailVisible = false
      // this.detailInfo = {}
    }
  }
}
</script>
<style lang="less" scoped>
/deep/.ant-table {
  height: calc(100vh - 290px)!important;
}
/deep/ .ant-card-body{
  .ant-table-wrapper {
    min-height: calc(100vh - 225px);
  }
}
</style>
