<template>
  <a-modal
    :title="modalTitle"
    :visible="visible"
    :confirmLoading="loading"
    @ok="() => { $emit('ok') }"
    @cancel="() => { $emit('cancel') }"
    ok-text="确定"
    cancel-text="取消"
    width="50%"
    :keyboard="false"
    :maskClosable="false"
  >
    <a-spin :spinning="loading">
      <a-form :form="form">
        <a-row :gutter="24">
          <a-col :md="12">
            <a-form-item label="事故名称">
              <a-input v-decorator="['name',{ rules:[{ required: true, message: '请输入事故名称' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="12">
            <a-form-item label="事故级别">
              <a-select v-decorator="['level',{ rules:[{ required: true, message: '请选择事故类别' }]}]" :disabled="disabled">
                <a-select-option v-for="d in levelOptions" :key="d.dictKey">
                  {{ d.dictValue }}
                </a-select-option>
              </a-select>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <a-form-item label="事故描述">
              <a-input v-decorator="['description',{ rules:[{ required: true, message: '请输入事故描述' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="12">
            <a-form-item label="事故类别">
              <a-select v-decorator="['type',{ rules:[{ required: true, message: '请选择事故类别' }]}]" :disabled="disabled">
                <a-select-option v-for="d in selectOptions" :key="d.dictKey">
                  {{ d.dictValue }}
                </a-select-option>
              </a-select>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <area-select label-name="事故区域" decorator-name="areaId" required :disabled="disabled"/>
          </a-col>
          <a-col :md="12">
            <department-select label-name="责任单位" decorator-name="chargerUnitId" required :disabled="disabled" />
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <a-form-item label="发生地点">
              <a-input v-decorator="['detailLocation',{ rules:[{ required: true, message: '请输入事故详细发生地点' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="12">
            <a-form-item label="发生时间">
              <a-date-picker
                show-time
                placeholder="选择时间"
                v-decorator="['occurTime',{ rules:[{ required: true, message: '请输入发生时间' }]}]"
                :disabled="disabled"
                valueFormat="YYYY-MM-DD HH:mm:ss"/>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <file-upload
              docName="reportName"
              docUrl="docUrl"
              labelName="附件"
              :form="form"
              required
              :disabled="disabled"
              :visible="visible"/>
          </a-col>
        </a-row>
      </a-form>
    </a-spin>
  </a-modal>
</template>

<script>
import pick from 'lodash.pick'
import { accidentColumns } from '@/views/safety/table'
import DepartmentSelect from '@/views/safety/components/DepartmentSelect'
import AreaSelect from '@/views/safety/components/AreaSelect'
import FileUpload from '@/views/safety/components/FileUpload'

const fields = accidentColumns.map(res => {
  return res.dataIndex
})
// 手动加入type字段，使select控件加载默认选项
fields.push('type', 'chargerUnitId', 'docUrl', 'level', 'areaId')

export default {
  name: 'AccidentModal',
  components: {
    DepartmentSelect,
    FileUpload,
    AreaSelect
  },
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    loading: {
      type: Boolean,
      default: () => false
    },
    model: {
      type: Object,
      default: () => null
    },
    selectOptions: {
      type: Object,
      default: () => null
    },
    levelOptions: {
      type: Object,
      default: () => null
    },
    modalTitle: {
      type: String,
      default: '操作'
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data () {
    return {
        headers: {
          authorization: 'authorization-text'
      },
      form: this.$form.createForm(this)
    }
  },
  created () {
    // 防止表单未注册
    fields.forEach(v => this.form.getFieldDecorator(v))
    // 当 model 发生改变时，为表单设置值
    this.$watch('model', () => {
      if (this.model && (typeof this.model.type) !== 'string') {
          this.model.type = String(this.model.type)
      }
      if (this.model && (typeof this.model.level) !== 'string') {
        this.model.level = String(this.model.level)
      }
      console.log(this.model)
      // this.previewImage = this.model.logo
      this.model && this.form.setFieldsValue(pick(this.model, fields))
    })
  },
  methods: {
  }
}
</script>
