        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  filters: {
    colorFilter: function(value) {
      const colorList = ['green', 'orange', 'blue', 'red']
      return colorList[value]
    },
    textFilter: function(value) {
      const textList = ['已审批', '未提交', '待审批', '审批拒绝']
      return textList[value]
    }
  },
  methods: {
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    onTimeChange(value, dateString) {
      this.queryParam.occurrenceBgTime = dateString[0]
      this.queryParam.occurrenceEndTime = dateString[1]
    },
    search() {
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    searchReset() {
      this.initQueryParam()
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    initQueryParam() {
      this.queryParam = {
        accidentName: '',
        status: '',
        occurrenceBgTime: '',
        occurrenceEndTime: ''
      }
      this.searchTime = ''
    },
    commonInit() {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.initFormData()
    },
    initFormData() {
      this.commonData = {
        accidentName: null,
        occurrenceAddress: null,
        occurrenceTime: null,
        dutyDeptId: undefined,
        dutyUserId: undefined,
        // reportPerson: '张三',
        accidentType: 1,
        accidentTypeIds: undefined,
        accidentReport: null,
        involvedUserIds: undefined,
        files: [],
        takeMeasures: null,
        approveId: undefined
      }
      this.rules[3].children[0].children[1].children.pop()
      this.rules[3].children[0].children[1].children.push(this.userInfo.realName)
      this.rules[5].options = checkboxOptions[0]
      this.setFileList([])
    },
    addRecord() {
      this.commonTitle = '新增数据'
      this.initFormData()
      this.rules[10].validate[0].required = false
      this.commonVisible = true
      this.commonDisabled = false
      // this.$refs.modal.fApi.disabled(true, 'reportPerson')
    },
    modalOk({ formData }) {
      let method = ''
      if (this.isNotNullOrUndefined(this.commonData.id)) {
        method = 'put'
        formData.id = this.commonData.id
        formData.status = this.commonData.status
      } else {
        method = 'post'
        formData.status = 1
        formData.reportUserName = this.userInfo.realName
        formData.reportUserId = this.userInfo.id
        formData.reportTime = new Date().getTime()
      }
      const accidentTypeNames = []
      formData.accidentTypeIds.forEach(item => {
        accidentTypeNames.push(checkboxOptions[formData.accidentType - 1][item - 1].label)
      })
      formData.accidentTypeNames = accidentTypeNames.join(',')
      const params = {
        ...this.commonData,
        ...formData,
        accidentTypeIds: formData.accidentTypeIds.join(','),
        involvedUserIds: formData.involvedUserIds.join(','),
        files: this.$refs.modal.fApi.getRule('files').props.fileList.map(item => ({
          id: item.id,
          fileUrlPath: item.filePath,
          originalFileName: item.name
        }))
      }
      if (typeof params.involvedUserNames !== 'string') {
        params.involvedUserNames = params.involvedUserNames.join(',')
      }
      this.submitForm(AccidentApi.accidentNearmiss, params, method).then(({ success }) => {
        if (success) {
          this.$message.success('操作成功')
          this.initQueryParam()
          this.commonInit()
          this.$refs.table.refresh()
          this.$refs.table.clearSelected()
        }
      })
    },
    setFileList(fileList) {
      if (this.$refs.modal && this.$refs.modal.fApi.getRule) {
        this.$refs.modal.fApi.getRule('files').props.fileList = fileList
      } else {
        this.rules[8].props.fileList = fileList
      }
    },
    editRecord(record) {
      this.commonTitle = '编辑数据'
      this.getById(AccidentApi.accidentNearmiss, record.id).then(({ data, success }) => {
        if (success) {
          data.involvedUserIds = data.involvedUserIds.split(',')
          data.accidentTypeIds = data.accidentTypeIds.split(',').map(item => {
            return parseInt(item)
          })
          if (data.approveId === -1) {
            data.approveId = undefined
          }
          this.commonData = data
          const files = data.files.map(item => ({
            uid: item.id,
            name: item.fileName,
            status: 'done',
            id: item.id,
            filePath: item.fileUrlPath
          }))
          console.log(this.rules.length)
          this.rules[5].options = checkboxOptions[data.accidentType - 1]
          this.rules[10].validate[0].required = true
          this.rules[3].children[0].children[1].children.pop()
          this.rules[3].children[0].children[1].children.push(data.reportUserName)
          this.setFileList(files)
          this.commonDisabled = false
          this.commonVisible = true
        }
      })
    },
    publishRecord(record) {
      this.getById(AccidentApi.accidentNearmiss, record.id).then(({ data, success }) => {
        if (success) {
          if (data.approveId === -1) {
            this.$message.error('未选择审批人，不可提交')
          } else {
            const url = `${AccidentApi.accidentNearmissUpdate}?id=${record.id}&status=${2}`
            this.submitForm(url, null, 'put').then(({ success }) => {
              if (success) {
                this.$message.success(`操作成功`)
                this.searchReset()
              }
            })
          }
        }
      })
    },
    viewRecord(record) {
      this.commonTitle = '查看数据'
      this.isApprove = false
      this.getById(AccidentApi.accidentNearmiss, record.id).then(({ data, success }) => {
        if (success) {
          data.involvedUserIds = data.involvedUserIds.split(',')
          data.accidentTypeIds = data.accidentTypeIds.split(',').map(item => {
            return parseInt(item)
          })
          this.commonData = data
          const files = data.files.map(item => ({
            uid: item.id,
            name: item.fileName,
            status: 'done',
            id: item.id,
            filePath: item.fileUrlPath
          }))
          this.rules[5].options = checkboxOptions[data.accidentType - 1]
          this.rules[3].children[0].children[1].children.pop()
          this.rules[3].children[0].children[1].children.push(data.reportUserName)
          this.setFileList(files)
          console.log(this.rules[3])
          console.log(this.commonData)
          this.commonVisible = true
          this.commonDisabled = true
        }
      })
    },
    removeRecord(record) {
      this.showDeleteConfirm([record.id])
    },
    showDeleteConfirm(params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk() {
          _this.deleteByIds(AccidentApi.accidentNearmiss, params).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    approveRecord(record) {
      // this.commonTitle = '审批'
      this.currentRecordId = record.id
      this.approve(0)
      // this.getById(AccidentApi.accidentNearmiss, record.id).then(({ data, success }) => {
      //   if (success) {
      //     data.involvedUserIds = data.involvedUserIds.split(',')
      //     data.accidentTypeIds = data.accidentTypeIds.split(',').map(item => {
      //       return parseInt(item)
      //     })
      //     this.commonData = data
      //     const files = data.files.map(item => ({
      //       uid: item.id,
      //       name: item.fileName,
      //       status: 'done',
      //       id: item.id,
      //       filePath: item.fileUrlPath
      //     }))
      //     this.rules[5].options = checkboxOptions[data.accidentType-1]
      //     this.setFileList(files)
      //     console.log(this.commonData)
      //     this.commonVisible = true
      //     this.commonDisabled = true
      //     this.isApprove = true
      //   }
      // })
    },
    approve(status) {
      this.submitForm(AccidentApi.accidentNearmissApprove + `?status=${status}`, [this.currentRecordId], 'put').then(
        ({ success }) => {
          if (success) {
            this.$message.success(`操作成功`)
            this.searchReset()
          }
        }
      )
    }
  }
}
</script>

