<template>
  <page-container :title="false">
    <a-card :bordered="false" style="margin: 15px" :body-style="{'padding-bottom': '0px'}">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="事故名称">
                <a-input v-model="queryParam.accidentName" placeholder="事故名称"/>
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="发生时间">
                <a-range-picker
                  style="width: 100%; min-width: 110px;"
                  :show-time="{ format: 'HH:mm:ss' }"
                  format="YYYY-MM-DD HH:mm:ss"
                  :placeholder="['开始时间', '结束时间']"
                  v-model="searchTime"
                  @change="onTimeChange"
                />
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="状态">
                <a-select
                  show-search
                  v-model="queryParam.status"
                  style="width: 100%"
                  :filter-option="filterOption"
                  option-filter-prop="children"
                   placeholder="状态"
                >
                  <a-select-option v-for="d in eventTypeOptions" :key="d.dictKey">{{ d.dictValue }}</a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="24" :sm="12" :xs="24" class="table-page-search-submitButtons">
              <a-space>
                <a-button type="primary" @click="search">查询</a-button>
                <a-button type="second" @click="searchReset">重置</a-button>
              </a-space>
            </a-col>
          </a-row>
        </a-form>
      </div>
    </a-card>
    <a-card
      :bordered="false"
      style="margin: 15px;"
      :headStyle="{border: 'none', paddingBottom: 0}"
      :bodyStyle="{paddingBottom: 0}"
      class="table-card"
    >
      <template #extra>
        <a-button
          type="primary"
          icon="plus"
          @click="addRecord"
          v-if="permission.accident_report_add"
        >新建</a-button>
      </template>
      <s-table
        ref="table"
        size="default"
        :filterMultiple="false"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        :scroll="{y:`calc(100vh - 432px)`, x: 1510}"
        showPagination="true"
      >
        <span
          slot="number"
          slot-scope="text, record, index"
        >{{ pagination.pageSize * (pagination.current - 1) + index + 1 }}</span>
        <span slot="action" slot-scope="text, record">
          <template v-if="record.status === 1">
            <a
              @click="editRecord(record)"
              v-if="userInfo.id === record.gmtCreateId || permission.accident_report_edit"
            >编辑</a>
            <a-divider
              type="vertical"
              v-if="userInfo.id === record.gmtCreateId || permission.accident_report_edit"
            />
            <a
              @click="publishRecord(record)"
              v-if="userInfo.id === record.gmtCreateId || permission.accident_report_submit"
            >提交</a>
            <a-divider
              type="vertical"
              v-if="userInfo.id === record.gmtCreateId || permission.accident_report_submit"
            />
            <a
              @click="removeRecord(record)"
              v-if="userInfo.id === record.gmtCreateId || permission.accident_report_delete"
            >删除</a>
          </template>
          <template v-if="record.status === 2">
            <a @click="approveRecord(record)" v-if="userInfo.id === record.approveId">审批</a>
            <a-divider type="vertical" v-if="userInfo.id === record.approveId" />
          </template>
          <template v-if="record.status !==1">
            <a @click="viewRecord(record)" v-if="record.status !== 1">查看</a>
          </template>
        </span>
        <template slot="status" slot-scope="text, record">
          <a-tag
            :color="record.status | colorFilter"
            style="margin-right: 0!important;"
          >{{record.status | textFilter}}</a-tag>
        </template>
      </s-table>
      <common-form-modal
        ref="modal"
        :visible.sync="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        :rule="rules"
        width="700px"
        @ok="modalOk"
      >
        <!--        <template slot="footer" v-if="isApprove">-->
        <!--          <a-button type="danger" @click="approve(0)">拒绝</a-button>-->
        <!--          <a-button type="primary" @click="approve(-1)">通过</a-button>-->
        <!--        </template>-->
      </common-form-modal>
      <!--      <descriptions-modal-->
      <!--        :visible.sync="detailVisible"-->
      <!--        :records="detailDatas"-->
      <!--        title="事故快报"-->
      <!--      />-->
      <!--      <accident-express-modal-->
      <!--        :visible.sync="followVisible"-->
      <!--        :main-id="followItemId"-->
      <!--      />-->
    </a-card>
  </page-container>
</template>

<script>
import { STable } from '@/components'
import CommonFormModal from '@/components/CommonFormModal/CommonFormModal'
import DescriptionsModal from '@/views/safety/components/DescriptionsModal'
import AccidentApi from '@/api/accident'
import { accidentReportColumns } from '@/views/safety/accident/columns'
import { getStore } from '@/utils/store'
import { getDict } from '@/api/system'
import { mapGetters } from 'vuex'

export default {
  name: 'accidentReport',
  components: {
    STable,
    CommonFormModal,
    DescriptionsModal
  },
  data() {
    return {
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonDisabled: false,
      isApprove: false,
      currentRecordId: null,
      queryParam: {
        accidentName: '',
        status: undefined,
        occurrenceBgTime: '',
        occurrenceEndTime: ''
      },
      searchTime: '',
      selectedRowKeys: [],
      selectedRows: [],
      eventTypeOptions: [
        {
          dictKey: 0,
          dictValue: '已审批'
        },
        {
          dictKey: 1,
          dictValue: '未提交'
        },
        {
          dictKey: 2,
          dictValue: '待审批'
        }
      ],
      accidentTypeOptions: [],
      accidentLevelOptions: [],
      userInfo: getStore({ name: 'info' }),
      pagination: {
        current: 1,
        pageSize: 10,
        showSizeChanger: true
      },
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        return this.getList(AccidentApi.accidentReportList, requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.pagination = {
            current: data.pageNo,
            pageSize: data.pageSize,
            showSizeChanger: true
          }
          return data
        })
      },
      rules: [
        {
          type: 'a-row',
          children: [
            {
              type: 'input',
              title: '事故名称',
              field: 'accidentName',
              props: {
                maxLength: 32,
                type: 'text',
                placeholder: '请输入事故名称'
              },
              validate: [
                {
                  required: true,
                  message: '请输入事故名称',
                  trigger: 'blur'
                }
              ],
              wrap: {
                labelCol: { span: 3 },
                wrapperCol: { span: 21 }
              }
            }
          ]
        },
        {
          type: 'a-row',
          children: [
            {
              type: 'a-col',
              props: {
                span: 24
              },
              children: [
                {
                  type: 'select',
                  title: '事故类型',
                  field: 'accidentTypeId',
                  props: {
                    placeholder: '请选择事故类型'
                  },
                  options: [
                    {
                      value: 1,
                      label: 'ceshi'
                    }
                  ],
                  col: {
                    span: 12
                  },
                  on: {
                    change: e => {
                      this.commonData.accidentTypeName = this.accidentTypeOptions[e - 1].label
                    }
                  },
                  validate: [
                    {
                      required: true,
                      message: '请选择事故类型',
                      trigger: true
                    }
                  ]
                },
                {
                  type: 'select',
                  title: '事故分级',
                  field: 'accidentLevelId',
                  props: {
                    placeholder: '请选择事故分级'
                  },
                  options: this.accidentLevelOptions,
                  col: {
                    span: 12
                  },
                  on: {
                    change: e => {
                      this.commonData.accidentLevelName = this.accidentLevelOptions[e - 1].label
                    }
                  },
                  validate: [
                    {
                      required: true,
                      message: '请选择事故分级',
                      trigger: true
                    }
                  ],
                  wrap: {
                    labelCol: { span: 6 },
                    wrapperCol: { span: 18 }
                  }
                }
              ]
            }
          ]
        },
        {
          type: 'a-row',
          children: [
            {
              type: 'input',
              title: '发生地点',
              field: 'occurrenceAddress',
              props: {
                maxLength: 32,
                type: 'text',
                placeholder: '请输入发生地点'
              },
              validate: [
                {
                  required: true,
                  message: '请输入发生地点',
                  trigger: true
                }
              ],
              wrap: {
                labelCol: { span: 3 },
                wrapperCol: { span: 21 }
              }
            }
          ]
        },
        {
          type: 'a-row',
          children: [
            {
              type: 'a-col',
              props: {
                span: 24
              },
              children: [
                {
                  type: 'DatePicker',
                  title: '发生时间',
                  field: 'occurrenceTime',
                  props: {
                    showTime: true,
                    format: 'YYYY-MM-DD HH:mm:ss',
                    placeholder: '请选择发生时间'
                  },
                  col: {
                    span: 12
                  },
                  validate: [
                    {
                      required: true,
                      message: '请选择发生时间',
                      trigger: true
                    }
                  ]
                },
                {
                  type: 'DepartmentSelect',
                  title: '责任单位',
                  field: 'dutyDeptId',
                  props: {
                    placeholder: '请选择责任单位/部门'
                  },
                  col: {
                    span: 12
                  },
                  on: {
                    change: ({ label }) => {
                      this.commonData.dutyDeptName = label
                    }
                  },
                  validate: [
                    {
                      required: true,
                      message: '请选择责任单位/部门',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 6 },
                    wrapperCol: { span: 18 }
                  }
                }
              ]
            }
          ]
        },
        {
          type: 'a-row',
          children: [
            {
              type: 'a-col',
              props: {
                span: 24
              },
              children: [
                {
                  type: 'PersonListSelect',
                  title: '责任人',
                  field: 'dutyUserId',
                  props: {
                    placeholder: '请选择责任人'
                  },
                  col: {
                    span: 12
                  },
                  on: {
                    change: ({ person }) => {
                      this.commonData.dutyUserName = person.realName
                    }
                  },
                  validate: [
                    {
                      required: true,
                      message: '请选择责任人',
                      trigger: true
                    }
                  ]
                },
                {
                  type: 'span',
                  title: '上报人',
                  field: 'reportUserName',
                  col: {
                    span: 12
                  },
                  children: []
                }
              ]
            }
          ]
        },
        {
          type: 'input',
          title: '事故发生简要经过',
          field: 'accidentReport',
          props: {
            maxLength: 256,
            type: 'textarea',
            autoSize: { minRows: 4, maxRows: 6 }
          },
          wrap: {
            labelCol: { span: 5 },
            wrapperCol: { span: 24, offset: 0 }
          },
          validate: [
            {
              required: true,
              message: '请输入事故发生的简要经过',
              trigger: true
            }
          ]
        },
        {
          type: 'input',
          title: '人员损伤情况',
          field: 'peopleDamage',
          props: {
            maxLength: 256,
            type: 'textarea',
            autoSize: { minRows: 4, maxRows: 6 }
          },
          wrap: {
            labelCol: { span: 4 },
            wrapperCol: { span: 24, offset: 0 }
          },
          validate: [
            {
              required: false,
              message: '请输入人员损伤情况',
              trigger: true
            }
          ]
        },
        {
          type: 'input',
          title: '现场处理情况',
          field: 'onsiteTreatment',
          props: {
            maxLength: 256,
            type: 'textarea',
            autoSize: { minRows: 4, maxRows: 6 }
          },
          wrap: {
            labelCol: { span: 4 },
            wrapperCol: { span: 24, offset: 0 }
          },
          validate: [
            {
              required: false,
              message: '请输入现场处理情况',
              trigger: true
            }
          ]
        },
        {
          type: 'input',
          title: '已采取的措施',
          field: 'takeMeasures',
          props: {
            maxLength: 256,
            type: 'textarea',
            autoSize: { minRows: 4, maxRows: 6 }
          },
          wrap: {
            labelCol: { span: 4 },
            wrapperCol: { span: 24, offset: 0 }
          },
          validate: [
            {
              required: true,
              message: '请输入已采取的措施',
              trigger: true
            }
          ]
        },
        {
          type: 'PersonListSelect',
          title: '审批人',
          field: 'approveId',
          props: {
            placeholder: '请选择审批人'
          },
          wrap: {
            labelCol: { span: 3 },
            wrapperCol: { span: 21 }
          },
          on: {
            change: ({ person }) => {
              this.commonData.approveName = person.realName
            }
          },
          validate: [
            {
              required: false,
              message: '请选择审批人',
              trigger: true
            }
          ]
        },
        {
          type: 'UploadFile',
          field: 'files',
          title: '附件',
          props: {
            fileList: []
          },
          wrap: {
            labelCol: { span: 3 },
            wrapperCol: { span: 18 }
          }
        }
      ]
    }
  },
  created() {
    getDict('ACCIDENTS_LEVEL').then(({ data, success }) => {
      if (success) {
        this.accidentLevelOptions = data.map(item => {
          return {
            value: item.dictKey,
            label: item.dictValue
          }
        })
        this.rules[1].children[0].children[1].options = this.accidentLevelOptions
      }
    })
    getDict('ACCIDENTS_TYPE').then(({ data, success }) => {
      if (success) {
        this.accidentTypeOptions = data.map(item => {
          return {
            value: item.dictKey,
            label: item.dictValue
          }
        })
        this.rules[1].children[0].children[0].options = this.accidentTypeOptions
      }
    })
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => accidentReportColumns,
    hasSelected() {
      return this.selectedRowKeys.length > 0
    },
    rowSelection() {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  filters: {
    colorFilter: function(value) {
      const colorList = ['green', 'orange', 'blue', 'red']
      return colorList[value]
    },
    textFilter: function(value) {
      const textList = ['已审批', '未提交', '待审批', '审批拒绝']
      return textList[value]
    }
  },
  methods: {
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    onTimeChange(value, dateString) {
      this.queryParam.occurrenceBgTime = dateString[0]
      this.queryParam.occurrenceEndTime = dateString[1]
    },
    search() {
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    searchReset() {
      this.initQueryParam()
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    initQueryParam() {
      this.queryParam = {
        accidentName: '',
        status: '',
        occurrenceBgTime: '',
        occurrenceEndTime: ''
      }
      this.searchTime = ''
    },
    commonInit() {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.initFormData()
    },
    initFormData() {
      this.commonData = {
        accidentLevelId: undefined,
        accidentName: null,
        accidentReport: null,
        accidentTypeId: undefined,
        approveId: undefined,
        dutyDeptId: undefined,
        files: [],
        occurrenceAddress: null,
        occurrenceTime: null,
        peopleDamage: null,
        onsiteTreatment: null,
        dutyUserId: undefined,
        takeMeasures: null
      }
      this.rules[4].children[0].children[1].children.pop()
      this.rules[4].children[0].children[1].children.push(this.userInfo.realName)
      this.setFileList([])
    },
    addRecord() {
      this.commonTitle = '新增数据'
      this.initFormData()
      this.rules[9].validate[0].required = false
      // this.$refs.modal.fApi.disabled(true, 'reportUserName')
      this.commonVisible = true
      this.commonDisabled = false
    },
    modalOk({ formData }) {
      let method = ''
      if (this.isNotNullOrUndefined(this.commonData.id)) {
        method = 'put'
        formData.id = this.commonData.id
        formData.status = this.commonData.status
      } else {
        method = 'post'
        formData.status = 1
        formData.reportUserName = this.userInfo.realName
        formData.reportUserId = this.userInfo.id
        formData.reportTime = new Date().getTime()
      }
      const params = {
        ...this.commonData,
        ...formData,
        files: this.$refs.modal.fApi.getRule('files').props.fileList.map(item => ({
          id: item.id,
          fileUrlPath: item.filePath,
          originalFileName: item.name
        }))
      }
      this.submitForm(AccidentApi.accidentReport, params, method).then(({ success }) => {
        if (success) {
          this.$message.success('操作成功')
          this.initQueryParam()
          this.commonInit()
          this.$refs.table.refresh()
          this.$refs.table.clearSelected()
        }
      })
    },
    setFileList(fileList) {
      if (this.$refs.modal && this.$refs.modal.fApi.getRule) {
        this.$refs.modal.fApi.getRule('files').props.fileList = fileList
      } else {
        this.rules[10].props.fileList = fileList
      }
    },
    editRecord(record) {
      this.commonTitle = '编辑数据'
      this.getById(AccidentApi.accidentReport, record.id).then(({ data, success }) => {
        if (success) {
          data.accidentLevelId = data.accidentLevelId + ''
          data.accidentTypeId = data.accidentTypeId + ''
          if (data.approveId === -1) {
            data.approveId = undefined
          }
          this.commonData = data
          const files = data.files.map(item => ({
            uid: item.id,
            name: item.fileName,
            status: 'done',
            id: item.id,
            filePath: item.fileUrlPath
          }))
          this.rules[4].children[0].children[1].children.pop()
          this.rules[4].children[0].children[1].children.push(data.reportUserName)
          this.rules[9].validate[0].required = true
          this.setFileList(files)
          this.commonDisabled = false
          this.commonVisible = true
        }
      })
    },
    publishRecord(record) {
      this.getById(AccidentApi.accidentReport, record.id).then(({ data, success }) => {
        if (data.approveId === -1) {
          this.$message.error('未选择审批人，不可提交')
        } else {
          const url = `${AccidentApi.accidentReportUpdate}?id=${record.id}&status=${2}`
          this.submitForm(url, null, 'put').then(({ success }) => {
            if (success) {
              this.$message.success(`操作成功`)
              this.searchReset()
            }
          })
        }
      })
    },
    viewRecord(record) {
      this.commonTitle = '查看数据'
      this.isApprove = false
      this.getById(AccidentApi.accidentReport, record.id).then(({ data, success }) => {
        if (success) {
          data.accidentLevelId = data.accidentLevelId + ''
          data.accidentTypeId = data.accidentTypeId + ''
          this.commonData = data
          const files = data.files.map(item => ({
            uid: item.id,
            name: item.fileName,
            status: 'done',
            id: item.id,
            filePath: item.fileUrlPath
          }))
          this.rules[4].children[0].children[1].children.pop()
          this.rules[4].children[0].children[1].children.push(data.reportUserName)
          this.setFileList(files)
          this.commonVisible = true
          this.commonDisabled = true
        }
      })
    },
    removeRecord(record) {
      this.showDeleteConfirm([record.id])
    },
    showDeleteConfirm(params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk() {
          _this.deleteByIds(AccidentApi.accidentReport, params).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    approveRecord(record) {
      // this.commonTitle = '审批'
      this.currentRecordId = record.id
      this.approve(0)
      // this.getById(AccidentApi.accidentReport, record.id).then(({ data, success }) => {
      //   if (success) {
      //     data.involvedUserIds = data.involvedUserIds.split(',')
      //     data.accidentTypeIds = data.accidentTypeIds.split(',').map(item => {
      //       return parseInt(item)
      //     })
      //     this.commonData = data
      //     const files = data.files.map(item => ({
      //       uid: item.id,
      //       name: item.fileName,
      //       status: 'done',
      //       id: item.id,
      //       filePath: item.fileUrlPath
      //     }))
      //     this.rules[5].options = checkboxOptions[data.accidentType-1]
      //     this.setFileList(files)
      //     console.log(this.commonData)
      //     this.commonVisible = true
      //     this.commonDisabled = true
      //     this.isApprove = true
      //   }
      // })
    },
    approve(status) {
      this.submitForm(AccidentApi.accidentReportApprove + `?status=${status}`, [this.currentRecordId], 'put').then(
        ({ success }) => {
          if (success) {
            this.$message.success(`操作成功`)
            this.searchReset()
          }
        }
      )
    }
  }
}
</script>

