<template>
  <page-container :title="false">
    <a-card :bordered="false">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row type="flex" justify="space-between">
            <!-- <a-row :gutter="24"> -->
            <a-col :md="4">
              <a-form-item label="关键字">
                <a-input v-model="queryParam.keyword"/>
              </a-form-item>
            </a-col>
            <!-- <a-col :md="6">
              <a-form-item label="变更单位">
                <a-tree-select
                  v-model="value"
                  style="width: 100%"
                  :tree-data="treeData"
                  :show-checked-strategy="SHOW_PARENT"
                  search-placeholder="Please select"
                />
              </a-form-item>
            </a-col> -->
            <a-col :md="4">
              <a-form-item label="变更类型">
                <a-select @change="handleChange" v-model="queryParam.type">
                  <tag-select-option v-for="d in typeOptions" :key="d.dictKey">
                    {{ d.dictValue }}
                  </tag-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :md="4">
              <a-form-item label="变更级别">
                <a-select @change="handleChange" v-model="queryParam.level">
                  <tag-select-option v-for="d in levelOptions" :key="d.dictKey">
                    {{ d.dictValue }}
                  </tag-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :md="6">
              <a-form-item label="创建时间">
                <a-range-picker
                  @change="onCreateChange"
                  v-model="timeRange.create"
                  :valueFormat="dateFormat"
                />
              </a-form-item>
            </a-col>
            <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="alterationResearch">查询</a-button>
                <a-button type="second" @click="refreshRecord" style="margin-left: 10px">重置</a-button>
              </span>
            </a-col>
          </a-row>
          <a-row :gutter="24">

            <a-col :md="5">
              <a-form-item label="起始时间">
                <a-range-picker
                  @change="onStartChange"
                  v-model="timeRange.start"
                  :valueFormat="dateFormat"
                />
              </a-form-item>
            </a-col>
            <a-col :md="6">
              <a-form-item label="截止时间">
                <a-range-picker @change="onEndChange" v-model="timeRange.end" :valueFormat="dateFormat"/>
              </a-form-item>
            </a-col>
          </a-row>
        </a-form>
      </div>
      <div class="table-operator">
        <a-button type="primary" icon="plus" @click="addRecord" v-if="permission.alteration_add">新增</a-button>
        <a-button icon="delete" @click="removeRecords" :disabled="!hasSelected" v-if="permission.alteration_deletes">删除</a-button>
        <!-- <a-button icon="download" :disabled="!hasSelected" @click="fileDownload">导出当前</a-button>
        <a-button icon="download" @click="filesDownload">导出所有</a-button>
        <a-button icon="upload" @click="fileUpload">导入</a-button> -->
      </div>
      <s-table
        ref="table"
        size="default"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        :rowSelection="rowSelection"
        showPagination="true"
        :scroll="{x:500,y:`calc(100vh - 420px)`}"
      >
        <span slot="fileName" slot-scope="text, record">
          <template>
            <a-button type="link" @click="fileDownLoad(record.docUrl)" :disabled="text.length === 0">{{ text || '暂无文件' }}</a-button>
          </template>
        </span>
        <span slot="action" slot-scope="text, record">
          <template>
            <a @click="checkRecord(record)" v-if="permission.alteration_view">查看</a>
            <a-divider type="vertical"/>
            <a @click="editRecord(record)" v-if="permission.alteration_edit">编辑</a>
            <a-divider type="vertical"/>
            <a @click="removeRecord(record)" v-if="permission.alteration_delete">删除</a>
          </template>
        </span>
      </s-table>
      <alteration-modal
        ref="modal"
        :visible="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :type-options="typeOptions"
        :level-options="levelOptions"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        @cancel="modalCancel"
        @ok="modalOk"
      />
      <contractor-detail
        mode="alteration"
        :visible="detailVisible"
        :info="detailInfo"
        @cancel="detailCancel"
      />
    </a-card>
  </page-container>
</template>
<script>

import TagSelectOption from '@/components/TagSelect/TagSelectOption'
import { STable } from '@/components'
import { alterationColumns } from '@/views/safety/table'
import { getAlterationList, submitAlterationItem, deleteAlteration, getAlterationType, getAlterationLevel, fileDownload } from '@/api/safety'
import AlterationModal from '@/views/safety/alteration/AlterationModal'
import moment from 'moment'
import { mapGetters } from 'vuex'
import ContractorDetail from '@/views/safety/contractor/ContractorDetail'

export default {
  name: 'AlterationManagement',
  components: {
    TagSelectOption,
    STable,
    AlterationModal,
    ContractorDetail
    // TreeSelect
  },
  data () {
    return {
      // common
      dateFormat: 'YYYY-MM-DD HH:mm:ss',
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: null,
      commonMode: true, // add：true edit：false
      commonDisabled: false,
      timeRange: {},
      queryParam: {},
      typeOptions: {},
      levelOptions: {},
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        console.log(requestParameters)
        return getAlterationList(requestParameters).then(res => {
          console.log(res)
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          console.log(data)
          return data
        })
      },
      selectedRowKeys: [],
      selectedRows: [],
      value: [],
      // detail modal
      detailVisible: false,
      detailInfo: {}
    }
  },
  created () {
    this.getSelectOptions()
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => alterationColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    moment,
    commonInit () {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.commonMode = true
      this.confirmLoading = false
      const form = this.$refs.modal.form
      form.resetFields()
    },
    getSelectOptions () {
      getAlterationType().then(res => {
        this.typeOptions = res.data
      })
      getAlterationLevel().then(res => {
        this.levelOptions = res.data
      })
    },
    onCreateChange (date, dateString) {
      console.log(date, dateString)
      this.queryParam.startCreateTime = date[0]
      this.queryParam.endCreateTime = date[1]
      console.log(this.queryParam)
    },
    onStartChange (date, dateString) {
      console.log(date, dateString)
      this.queryParam.startStartTime = date[0]
      this.queryParam.endStartTime = date[1]
      console.log(this.queryParam)
    },
    onEndChange (date, dateString) {
      console.log(date, dateString)
      this.queryParam.startEndTime = date[0]
      this.queryParam.endEndTime = date[1]
      console.log(this.queryParam)
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    alterationResearch () {
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    refreshRecord () {
      this.queryParam = {}
      this.timeRange = {}
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord () {
      this.commonTitle = '新增数据'
      // this.$message.success('新增')
      this.commonMode = true
      this.commonVisible = true
    },
    removeRecord (record) {
      console.log(record)
      this.showDeleteConfirm(record.id)
    },
    removeRecords () {
      var ids = ''
      this.selectedRows.forEach((item, index) => {
          ids = ids + item.id + ','
        })
        console.log(ids)
        this.showDeleteConfirm(ids)
    },
    checkRecord (record) {
      this.detailInfo = { ...record }
      this.detailVisible = true
    },
    editRecord (record) {
      this.commonTitle = '编辑数据'
      // this.$message.success('编辑')
      this.commonMode = false
      this.commonVisible = true
      this.commonData = { ...record }
    },
      fileDownload () {
        this.$message.success('导出当前文件')
      },
      filesDownload () {
        this.$message.success('导出所有文件')
      },
      fileUpload () {
        // this.$message.success('编辑')
      },
    modalCancel () {
      this.commonInit()
    },
    modalOk () {
      const _this = this
      if (_this.commonDisabled) {
        _this.$message.success('查看返回')
        _this.commonInit()
      } else {
        const form = _this.$refs.modal.form
        _this.confirmLoading = true
        form.validateFields((errors, values) => {
          if (!errors) {
            var requestForm = values
              // 日期未更新，传回的值为string，编辑后值类型为moment
              if (typeof (requestForm.startTime) !== 'string') {
                requestForm.startTime = requestForm.startTime.format('YYYY-MM-DD HH:mm:ss')
              }
              if (typeof (requestForm.endTime) !== 'string') {
                requestForm.endTime = requestForm.endTime.format('YYYY-MM-DD HH:mm:ss')
              }
            if (_this.commonMode) {
              // 新增接口

            } else {
              // 编辑接口
              requestForm.id = this.commonData.id
            }
            submitAlterationItem(requestForm).then(res => {
                if (res.code === 200) {
                _this.$message.success('提交成功')
                _this.selectedRows = []
                _this.selectedRowKeys = []
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.success('提交失败')
              }
            })
            setTimeout(() => {
              _this.commonInit()
            }, 1000)
          } else {
            _this.confirmLoading = false
          }
        })
      }
    },
    showDeleteConfirm (params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk () {
          console.log('OK')
          deleteAlteration(params).then(res => {
            console.log(res)
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh(true)
            } else {
              _this.$message.success('删除失败')
            }
            })
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    },
    fileDownLoad (url) {
      fileDownload({ link: url }).then(res => {
        window.open(res.data)
      })
    },
    detailCancel () {
      this.detailVisible = false
      // this.detailInfo = {}
    }
  }
}
</script>
<style lang="less" scoped>
/deep/ .ant-card-body {
  .ant-table-wrapper {
    min-height: calc(100vh - 280px);
  }
}
</style>
