<template>
  <page-container :title="false">
    <a-card :bordered="false" style="margin: 15px;" :bodyStyle="{ paddingBottom: 0 }">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="类型">
                <a-select
                  show-search
                  v-model="queryParam.templateTypeValue"
                  placeholder="类型"
                  style="width: 100%"
                >
                  <a-select-option
                    v-for="d in templateTypeNameOptions"
                    :key="d.dictKey"
                  >{{ d.dictValue }}</a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="岗位">
                <!-- <a-select
                  show-search
                  v-model="queryParam.postName"
                  placeholder="岗位"
                  style="width: 100%"
                >
                  <a-select-option v-for="d in postNameOptions" :key="d.id">{{ d.postName }}</a-select-option>
                </a-select>-->
                <a-input v-model="queryParam.postName" placeholder="岗位" style="width: 100%"></a-input>
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="责任人">
                <a-input v-model="queryParam.userName" placeholder="责任人" style="width: 100%"></a-input>
              </a-form-item>
            </a-col>
             <a-col :lg="6" :md="24" :sm="12" :xs="24" class="table-page-search-submitButtons">
              <a-space>
                <a-button type="primary" @click="$refs.table.refresh(true)">查询</a-button>
                <a-button type="second" @click="searchReset">重置</a-button>
              </a-space>
            </a-col>
          </a-row>
        </a-form>
      </div>
    </a-card>
    <a-card
      :bordered="false"
      style="margin: 15px;"
      :headStyle="{border: 'none', paddingBottom: 0}"
      :bodyStyle="{paddingBottom: 0, paddingTop: '10px'}"
      class="table-card"
    >
      <s-table
        ref="table"
        size="default"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        showPagination="true"
        :scroll="{y: 'calc(100vh - 380px)', x: 1360}"
      >
        <!-- 序号 -->
        <span
          slot="number"
          slot-scope="text, record, index"
        >{{ pagination.pageSize * (pagination.current - 1) + index + 1 }}</span>
        <!-- 序号 end -->
        <template slot="userIsSign" slot-scope="text, record">
          <a-tag :color="record.userIsSign | colorFilter" style="margin-right: 0 !important">
            {{
            record.userIsSign | textFilter
            }}
          </a-tag>
        </template>
        <span slot="action" slot-scope="text, record">
          <template>
            <a @click="viewRecord(record)">查看</a>
          </template>
        </span>
      </s-table>
      <a-modal
        :title="commonTitle"
        :visible="commonVisible"
        :confirm-loading="confirmLoading"
        :footer="null"
        width="700px"
        @ok="modalOk"
        @cancel="handleCancel"
        :keyboard="false"
        :maskClosable="false"
      >
        <div style="min-height: 470px">
          <div v-html="commonData.contents"></div>
        </div>
        <div class="commitment">
          <template v-if="commonData.type===0">
            <div class="commitment-item" v-if="commonData.userUrl!==''">
              <span style="width: 100px;">承诺人：</span>
              <div style="width: 130px;display: flex;justify-content: center">
                <img :src="commonData.userUrl" class="signImg"/>
              </div>
            </div>
            <div class="commitment-item" v-if="commonData.userUrl!==''">
              <span style="width: 100px;">承诺日期：</span>
              <span style="width: 130px">{{commonData.userSignDate}}</span>
            </div>
          </template>
          <template v-else>
            <div class="commitment-item" v-if="commonData.userUrl !==''">
              <span>责任人：</span>
              <img :src="commonData.userUrl" class="signImg"/>
              <span>{{commonData.userSignDate}}</span>
            </div>
            <div class="commitment-item" v-if="commonData.leaderUrl !==''">
              <span>直属主管：</span>
              <img :src="commonData.leaderUrl" class="signImg"/>
              <span>{{commonData.leaderSignDate}}</span>
            </div>
          </template>
        </div>
      </a-modal>
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { commitmentModelPersonColumns } from '@/views/safety/table'
import ApiURLs from '@/api/urls'
import { mapGetters } from 'vuex'
import { getStore } from '@/utils/store'
import { getDict } from '@/api/system'
import { getPersonnelTree } from '@/api/safety'

export default {
  name: 'commitmentModelPerson',
  components: {
    STable
  },
  data() {
    return {
      pagination: {
        current: 1,
        pageSize: 10,
        showSizeChanger: true
      },
      confirmLoadingPeople: false,
      autoExpandParent: true,

      templateTypeNameOptions: [],
      postNameOptions: [],
      labelCol: { span: 8 },
      wrapperCol: { span: 14 },
      modelPersonForm: {
        name: ''
      },
      confirmLoading: false,
      commonTitle: '查看数据',
      commonVisible: false,
      commonData: {},
      commonDisabled: false,
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        return this.getList(ApiURLs.commitmentModelPersonList, requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.pagination = {
            current: data.pageNo,
            pageSize: data.pageSize,
            showSizeChanger: true
          }
          return data
        })
      },
      userInfo: getStore({ name: 'info' })
    }
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => commitmentModelPersonColumns,
    hasSelected() {
      return this.selectedRowKeys.length > 0
    }
  },
  filters: {
    colorFilter: function(value) {
      const colorList = ['green', 'orange']
      return colorList[value]
    },
    textFilter: function(value) {
      const textList = ['已签字', '待签字']
      return textList[value]
    }
  },
  methods: {
    //重置
    searchReset() {
      this.queryParam = {}
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    //获取详情
    getRecord(id,type, callback) {
      this.getById(ApiURLs.commitmentModelPerson, id).then(({ data, success }) => {
        if (success) {
          this.commonData = data
          //对富文本进行解码
          var temp = document.createElement('div')
          temp.innerHTML = this.commonData.contents
          this.commonData.contents = temp.innerText || temp.textContent
          this.commonData.type = type
          temp = null
          callback()
        }
      })
    },
    //查看
    viewRecord(record) {
      this.getRecord(record.id, record.templateTypeValue,() => {
        this.commonDisabled = true
        this.commonVisible = true
      })
    },
    //关闭弹框
    handleCancel() {
      this.commonVisible = false
    }
  },
  created() {
    //类型
    this.getDict('TEMPLATE_TYPE').then(({ data, success }) => {
      if (success) {
        this.templateTypeNameOptions = data
      }
    })
    //岗位
    this.axios.get('/spang-system/post/select').then(rs => {
      this.postNameOptions = rs.data
    })
  }
}
</script>
