              <a-col :span="21">
                <a-form-model-item ref="oneFireNum" label="一级动火作业" prop="oneFireNum">
                  <a-input-number
                    :disabled="commonDisabled"
                    v-model="commonData.oneFireNum"
                    :min="0"
