                      }
                    "
                  />
                </a-form-model-item>
              </a-col>
              <a-col :span="2">
                <span>处</span>
              </a-col>
              <a-col :span="21">
                <a-form-model-item ref="twoFireNum" label="二级动火作业" prop="twoFireNum">
                  <a-input-number
                    :disabled="commonDisabled"
