                  </a-radio-group>
                </a-form-model-item>
              </a-col>
            </a-row>
            <a-row :gutter="24">
              <a-col :span="21">
                <span>5、</span>
                <a-form-model-item
                  ref="isSafety"
                  label="重大危险源是否处于安全状态"
                  prop="isSafety"
                  labelAlign="left"
                  :label-col="{ span: 10 }"
