        <template class="button" slot="footer">
          <a-button @click="()=>this.commonVisible = false">取消</a-button>
          <a-button
            type="primary"
            v-if="!commonDisabled"
            @click="saveData({data:commonData,type:1})"
          >保存</a-button>
          <a-button
            type="primary"
            v-if="!commonDisabled"
            @click="saveData({data:commonData,type:2})"
          >提交</a-button>
        </template>
