</template>
<script>
import { STable } from '@/components'
import { maintenanceColumns } from '@/views/safety/table'
import ApiURLs from '@/api/urls'
import { mapGetters } from 'vuex'
import { getStore } from '@/utils/store'
import { getDict } from '@/api/system'
import { getPersonnelTree } from '@/api/safety'
import PersonSelectModal from '@/views/safety/task/component/PersonSelectModal'
import { getUsers } from '@/api/system'

export default {
  name: 'maintenance',
  components: {
    STable,
    PersonSelectModal
  },
  data() {
    return {
      limitNumber(value) {
        if (typeof value === 'string') {
          return !isNaN(Number(value)) ? value.replace(/\./g, '') : 0
        } else if (typeof value === 'number') {
          return !isNaN(value) ? String(value).replace(/\./g, '') : 0
        } else {
          return 0
        }
      },

      postNameOptions: [],
      pagination: {
        current: 1,
        pageSize: 10,
        showSizeChanger: true
      },
      mainId: '',
      expandedKeys: [],
      checkedKeys: [],

      templateTypeNameOptions: [],
      templateYearNameOptions: [],
      signOrgId: [],

      labelCol: { span: 5 },
      wrapperCol: { span: 19 },
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonDisabled: false,
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      dateFormat: 'YYYY-MM-DD',
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        if (this.queryParam.time) {
          Object.assign(requestParameters, {
            bgTime: this.queryParam.time[0] + ' 00:00:00',
            endTime: this.queryParam.time[1] + ' 23:59:59'
          })
        }
        // Object.assign(requestParameters, this.queryParam)
        return this.getList(ApiURLs.maintenanceList, requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.pagination = {
            current: data.pageNo,
            pageSize: data.pageSize,
            showSizeChanger: true
          }
          this.templateVersion = res.data.records
          return data
        })
      },
      userInfo: getStore({ name: 'info' }),
      newDate: '',
      versionData: '',
      templateVersion: [],
      rules: {
        produceNum: [{ required: true, message: '请填写生产装置', trigger: 'blur' }],
        workNum: [{ required: true, message: '请填写运行装置', trigger: 'blur' }],
        stopNum: [{ required: true, message: '请填写停产装置', trigger: 'blur' }],
        overhaulNum: [{ required: true, message: '请填写检修装置', trigger: 'blur' }],
        specialFireNum: [{ required: true, message: '请填写特级动火作业', trigger: 'blur' }],
        oneFireNum: [{ required: true, message: '请填写一级动火作业', trigger: 'blur' }],
        twoFireNum: [{ required: true, message: '请填写二级动火作业', trigger: 'blur' }],
        spaceNum: [{ required: true, message: '请填写受限空间作业', trigger: 'blur' }],
        isProduce: [{ required: true, message: '请选择是否处于试生产', trigger: 'change' }],
        isStop: [{ required: true, message: '请选择是否处于开停车状态', trigger: 'change' }],
        isSafety: [{ required: true, message: '请选择重大危险源是否处于安全状态', trigger: 'change' }],
        isResp: [{ required: true, message: '请选择重大危险源是否落实安全包保责任', trigger: 'change' }],
        approveId: [{ required: true, message: '请选择审批人', trigger: 'change' }]
      }
    }
  },
  watch: {
    'commonData.specialFireNum': function(val) {
      this.commonData.specialNum =
        Number(this.commonData.oneFireNum) +
        Number(this.commonData.twoFireNum) +
        Number(this.commonData.spaceNum) +
        parseInt(val)
    },
    'commonData.oneFireNum': function(val) {
      this.commonData.specialNum =
        Number(this.commonData.specialFireNum) +
        Number(this.commonData.twoFireNum) +
        Number(this.commonData.spaceNum) +
        parseInt(val)
    },
    'commonData.twoFireNum': function(val) {
      this.commonData.specialNum =
        Number(this.commonData.specialFireNum) +
        Number(this.commonData.oneFireNum) +
        Number(this.commonData.spaceNum) +
        parseInt(val)
    },
    'commonData.spaceNum': function(val) {
      this.commonData.specialNum =
        Number(this.commonData.specialFireNum) +
        Number(this.commonData.oneFireNum) +
        Number(this.commonData.twoFireNum) +
        parseInt(val)
    },
    commonVisible(val, newVal) {
      if (val) {
        try {
          this.$refs['ruleForm'].resetFields()
        } catch (e) {}
      }
    }
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => maintenanceColumns,
    hasSelected() {
      return this.selectedRowKeys.length > 0
    },
    rowSelection() {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange,
        type: 'radio'
      }
    }
  },
  filters: {
    colorFilter: function(value) {
      const colorList = ['green', 'orange']
      return colorList[value]
    },
    textFilter: function(value) {
      const textList = ['已提交', '未提交']
      return textList[value]
    }
  },
  mounted() {},
  methods: {
    // 获取人员数据
    getSelectOptions() {
      getPersonnelTree(0).then(res => {
        this.signOrgNameOptions = res.data
      })
    },

    initFormData() {
      this.commonData = {
        approveId: '',
        approveName: '',
        approveSignPicUrl: '',
        approveTime: '',
        companySafety: '',
        id: '',
        isProduce: '',
        isResp: '',
        isSafety: '',
        isStop: '',
        madeDate: '',
        oneFireNum: '',
        overhaulNum: '',
        produceNum: '',
        spaceNum: '',
        specialFireNum: '',
        specialNum: '',
        status: '',
        stopNum: '',
        submitTime: '',
        twoFireNum: '',
        workNum: ''
      }
    },
    commonInit() {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.initFormData()
    },
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    //复制
    copyRecord() {
      if (this.selectedRows.length === 0) {
        this.$message.warning('请选择需要复制的选项')
      } else {
        const _this = this
        const url = `${ApiURLs.maintenanceCopyUpdate}?id=${_this.selectedRows[0].id}`
        _this.submitForm(url, null, 'put').then(rs => {
          if (rs.code === 200 && rs.data.isSumbit === true) {
            _this.$message.success(`操作成功`)
            _this.searchReset()
            _this.$refs.table.clearSelected()
          } else {
            _this.$message.success(rs.msg)
            _this.$refs.table.clearSelected()
          }
          _this.$refs.table.refresh()
        })
      }
    },
    searchReset() {
      this.queryParam = {}
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord() {
      this.axios.get('/spang-safety/safetyresp/companytemplate/getIfNewSave').then(rs => {
        if (rs.code === 200) {
          if (rs.data.isSumbit === false) {
            this.$message.error(rs.msg)
          } else {
            this.commonTitle = '新增数据'
            this.commonVisible = true
            this.commonDisabled = false
            this.commonData.safetyTemplateUsersList = []
            this.initFormData()
          }
        }
      })
    },
    removeRecords() {
      const ids = this.selectedRows.map(item => item.id)
      this.showDeleteConfirm(ids)
    },
    //查看详情
    getRecord(id, callback) {
      this.getById(ApiURLs.maintenance, id).then(({ data, success }) => {
        if (success) {
          this.commonData = JSON.parse(JSON.stringify(data))
          callback()
        }
      })
    },
    checkRecord(record) {
      this.mainId = record.id
      this.commonTitle = '查看数据'
      this.getRecord(record.id, () => {
        this.commonDisabled = true
        this.commonVisible = true
      })
    },
    editRecord(record) {
      this.commonTitle = '编辑数据'
      this.getRecord(record.id, () => {
        this.commonDisabled = false
        this.commonVisible = true
      })
    },
    removeRecord(record) {
      this.showDeleteConfirm([record.id])
    },
    resetData() {
      this.commonInit()
    },
    //获取当前日期
    getNowDate() {
      var date = new Date()
      var year = date.getFullYear()
      var month = date.getMonth() + 1
      month = month < 10 ? '0' + month : month
      var myDate = date.getDate()
      var nowDate = year.toString() + month.toString() + myDate.toString()
      this.commonData.madeDate = nowDate.slice(0, 4) + '-' + nowDate.slice(4, 6) + '-' + nowDate.slice(6)
    },
    //保存
    saveData(data) {
      this.commonData.status = data.type === 1 ? 1 : 2
      let method = ''
      var formData = JSON.parse(JSON.stringify(this.commonData))
      if (this.isNotNullOrUndefined(this.commonData.id)) {
        method = 'put'
      } else {
        method = 'post'
      }
      this.postNameOptions.forEach(item => {
        if (this.commonData.approveId === item.id) {
          this.commonData.approveName = item.realName
        }
      })
      // this.getNowDate()
      const params = {
        ...formData,
        madeDate: this.commonData.madeDate,
        approveName: this.commonData.approveName
      }

      this.$refs.ruleForm.validate(valid => {
        if (valid) {
          this.submitForm(ApiURLs.maintenance, params, method, {
            // headers: { 'Content-Type': 'multipart/form-data' }
          }).then(res => {
            this.$refs.table.refresh()
            this.$refs.table.clearSelected()
            this.commonInit()
          })
        } else {
          console.log('error submit!!')
          return false
        }
      })
    },
    //提交
    publishRecord(record, status) {
      const url = `${ApiURLs.maintenanceStatusUpdate}?id=${record.id}`
      this.submitForm(url, null, 'put').then(({ success }) => {
        if (success) {
          this.$message.success(`操作成功`)
          this.searchReset()
        }
      })
    },
    showDeleteConfirm(params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk() {
          _this.deleteByIds(ApiURLs.maintenance, params).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    onTimeChange(value, dateString) {},
    //关闭弹框
    handleCancel() {
      this.commonVisible = false
    }
  },
  created() {
    //审批人
    getUsers().then(res => {
      this.postNameOptions = res.data
    })
  }
}
</script>
