<template>
  <a-form-item :label="labelName">
    <a-tree-select
      v-decorator="[decoratorName,{ rules:[{ required: required, message: '请输入区域名称' }]}]"
      :disabled="disabled"
      style="width: 100%"
      :dropdownStyle="{ maxHeight:'35vh', overflow: 'auto'}"
      :tree-data="treeData"
      :replaceFields="{children:'childDirectory', title:'areaName', key:'id', value: 'id' }"
      placeholder="请选择区域"
    />
  </a-form-item>
</template>

<script>

import { getRegionTree, getRegionTreeChild } from '@/api/safety'

export default {
  name: 'AreaSelect',
  props: {
    decoratorName: {
      type: String,
      default: ''
    },
    labelName: {
      type: String,
      default: ''
    },
    disabled: {
      type: Boolean,
      default: false
    },
    required: {
      type: Boolean,
      default: false
    }
  },
  data () {
    return {
      treeData: []
    }
  },
  created () {
    this.getTreeData()
  },
  methods: {
    getTreeData () {
      getRegionTree(0).then(res => {
        this.treeData = res.data
      //   const result = res.data
      //   this.treeData = result.map(item => {
      //     return {
      //       id: item.id,
      //       title: item.areaName,
      //       key: item.id,
      //       hasChildren: item.hasChild,
      //       value: item.id
      //     }
      //   })
      //   console.log(this.treeData)
      })
    },
    onLoadData (treeNode) {
      return new Promise(resolve => {
        console.log(treeNode)
        getRegionTreeChild({ pid: treeNode.value }).then(res => {
          if (treeNode.dataRef.children) {
            resolve()
            return
          }
          const children = res.data.map(item => {
            return ({
              id: item.id,
              title: item.areaName,
              key: item.id,
              hasChildren: item.hasChild,
              value: item.id,
              isLeaf: !item.hasChild
            })
          })
          treeNode.dataRef.children = children
          this.treeData = [...this.treeData]
        })
        resolve()
      })
    }
  }
}
</script>
