<template>
  <a-tree-select
    v-model="deptId"
    :disabled="disabled"
    style="width: 100%;"
    :dropdownStyle="{ maxHeight:'35vh', overflow: 'auto'}"
    :tree-data="treeData"
    :placeholder="placeholder"
    @change="handleChange"
  />
</template>

<script>

import { getPersonnelTree } from '@/api/safety'

export default {
  name: 'DepartmentSelect',
  props: {
    value: {
      type: String,
      default: null
    },
    disabled: {
      type: Boolean,
      default: false
    },
    placeholder: {
      type: String,
      default: '请选择单位'
    }
  },
  data () {
    return {
      treeData: [],
      deptId: this.value
    }
  },
  watch: {
    value: function (val) {
      this.deptId = val
    }
  },
  created () {
    this.getTreeData()
  },
  methods: {
    getTreeData () {
      getPersonnelTree(0).then(res => {
        this.treeData = res.data
      })
    },
    handleChange (value, label) {
      this.$emit('input', value)
      this.$emit('change', { id: value, label: label[0] })
    }
  }
}
</script>
