<template>
  <page-container :title="false">
    <a-card :bordered="false" style="margin: 15px;" :body-style="{'padding-bottom': '0px'}">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="承包商名称">
                <a-input v-model="queryParam.contractorName" placeholder="承包商名称" />
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="违规类型">
                <a-select
                  show-search
                  v-model="queryParam.type"
                  placeholder="违规类型"
                  style="width: 100%"
                >
                  <a-select-option
                    v-for="d in illegalTypeOptions"
                    :key="d.dictKey"
                  >{{ d.dictValue }}</a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="违规日期">
                <a-range-picker
                  style="width: 100%; min-width: 110px;"
                  showTime
                  valueFormat="YYYY-MM-DD HH:mm:ss"
                  :placeholder="['开始时间', '结束时间']"
                  v-model="queryParam.validateDateRange"
                />
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="24" :sm="12" :xs="24" class="table-page-search-submitButtons">
              <a-space>
                <a-button type="primary" @click="$refs.table.refresh(true)">查询</a-button>
                <a-button type="second" @click="searchReset">重置</a-button>
              </a-space>
            </a-col>
          </a-row>
        </a-form>
      </div>
    </a-card>
    <a-card
      :bordered="false"
      style="margin: 15px;"
      :headStyle="{border: 'none', paddingBottom: 0}"
      :bodyStyle="{paddingBottom: 0}"
      class="table-card"
    >
      <template #title>
        <div class="text-right">
          <a-button
            type="primary"
            icon="plus"
            @click="addRecord"
            v-if="permission.contractor_illegal_add"
          >新建</a-button>
        </div>
      </template>
      <s-table
        ref="table"
        size="default"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        showPagination="true"
        :scroll="{y: `calc(100vh - 432px)`, x: 1550}"
        :pagination="pagination"
      >
        <span
          slot="serial"
          slot-scope="text, record, index"
        >{{ pagination.pageSize * (pagination.current - 1) + index + 1 }}</span>
        <span slot="resultTypeName" slot-scope="text, record">
          <a-tag v-if="record.status !== 1" color="orange">{{text}}</a-tag>
          <a-tag v-else color="green">{{text}}</a-tag>
        </span>
        <span slot="action" slot-scope="text, record">
          <template>
            <!--            <a @click="publishRecord(record, 0)" v-if="record.status === 1">撤回</a>-->
            <a @click="checkRecord(record)" v-if=" record.status === 1">查看</a>
            <a
              @click="editRecord(record)"
              v-if="(permission.contractor_illegal_edit || userInfo.id === record.gmtCreateId) && record.status !== 1"
            >编辑</a>
            <a-divider
              type="vertical"
              v-if="(permission.contractor_illegal_submit || userInfo.id === record.gmtCreateId) && record.status !== 1"
            />
            <a
              @click="publishRecord(record, 1)"
              v-if="(permission.contractor_illegal_submit || userInfo.id === record.gmtCreateId) && record.status !== 1"
            >提交</a>
            <a-divider
              type="vertical"
              v-if="(permission.contractor_illegal_delete || userInfo.id === record.gmtCreateId) && record.status !== 1"
            />
            <a
              @click="removeRecord(record)"
              v-if="(permission.contractor_illegal_delete || userInfo.id === record.gmtCreateId) && record.status !== 1"
            >删除</a>
          </template>
        </span>
        <span slot="viewAction" slot-scope="text, record">
          <template>
            <a @click="preview(record)">查看</a>
          </template>
        </span>
      </s-table>
      <lawless-record-modal
        ref="modal"
        :visible.sync="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :select-options="illegalTypeOptions"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        @ok="modalOk"
        :scroll="{y: `calc(100vh - 432px)`}"
      />
      <a-modal
        title="查看图片"
        :visible="previewVisible"
        :footer="null"
        @cancel="previewVisible = false"
        :keyboard="false"
        :maskClosable="false"
        :bodyStyle="{minHeight: '400px',display: 'flex', justifyContent: 'center', alignItems: 'center'}"
      >
        <vue-viewer
          multiple
          list-ul-class="image-list"
          v-if="imageList.length > 0"
          :thumb="imageList"
          :full="imageList"
        ></vue-viewer>
        <div v-else>
          <a-empty />
        </div>
      </a-modal>
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { contractorIllegalColumns } from '@/views/safety/table'
import LawlessRecordModal from './LawlessRecordModal'
import apiURLs from '@/api/urls'
import { mapGetters } from 'vuex'
export default {
  name: 'LawlessRecords',
  components: {
    STable,
    LawlessRecordModal
  },
  data() {
    return {
      pagination: {
        pageSize: 10,
        current: 1,
        showSizeChanger: true
      },
      imageList: '',
      previewVisible: false,
      illegalTypeOptions: [],
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonDisabled: false,
      queryParam: {
        validateDateRange: undefined,
        contractorName: ''
      },
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        if (this.queryParam.validateDateRange) {
          Object.assign(requestParameters, {
            startTime: this.queryParam.validateDateRange[0],
            endTime: this.queryParam.validateDateRange[1]
          })
        }
        delete requestParameters.validateDateRange
        return this.getList(apiURLs.contractorIllegalList, requestParameters).then(res => {
          this.pagination = { ...this.pagination, pageSize: res.data.size, current: res.data.current }
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          return data
        })
      },
      // detail modal
      detailVisible: false,
      detailDatas: []
    }
  },
  computed: {
    ...mapGetters(['permission', 'userInfo']),
    columns: () => contractorIllegalColumns,
    hasSelected() {
      return this.selectedRowKeys.length > 0
    },
    rowSelection() {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    commonInit() {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.commonData = {
        illegalPersonId: undefined,
        illegalPersonName: null,
        localePhotos: [],
        resultType: undefined,
        contractorName: null
      }
    },
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    searchReset() {
      this.queryParam = {
        contractorName: '',
        validateDateRange: undefined
      }
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord() {
      this.commonTitle = '新增数据'
      this.commonData = { illegalPersonName: null, localePhotos: [], resultType: undefined, contractorName: null }
      this.commonVisible = true
      this.commonDisabled = false
    },
    removeRecords() {
      var ids = ''
      this.selectedRows.forEach((item, index) => {
        ids = ids + item.id + ','
      })
      this.showDeleteConfirm(ids)
    },
    checkRecord(record) {
      this.commonTitle = '查看数据'
      if (record.forfeit === -1) {
        record.forfeit = undefined
      }
      this.commonData = { ...record }
      this.commonVisible = true
      this.commonDisabled = true
    },
    editRecord(record) {
      this.commonTitle = '编辑数据'
      if (record.forfeit === -1) {
        record.forfeit = undefined
      }
      this.commonData = { ...record }
      this.commonVisible = true
      this.commonDisabled = false
    },
    removeRecord(record) {
      this.showDeleteConfirm(record.id)
    },
    showFile(filename) {
      this.$message.success(`${filename}`)
    },
    modalCancel() {
      this.commonInit()
    },
    modalOk({ formData }) {
      let method = ''
      if (this.isNotNullOrUndefined(this.commonData.id)) {
        method = 'put'
        formData.id = this.commonData.id
      } else {
        method = 'post'
      }
      delete formData.status
      this.submitForm(apiURLs.contractorIllegal, formData, method, {
        // headers: { 'Content-Type': 'multipart/form-data' }
      }).then(res => {
        this.$refs.table.refresh()
        this.$refs.table.clearSelected()
        this.commonInit()
      })
    },
    publishRecord(record, status) {
      if (status === 1 && !record.resultType) {
        this.$message.error('未处理的记录不能提交')
        return
      }
      const params = {
        id: record.id,
        status: status
      }
      this.submitForm(apiURLs.contractorIllegal, params, 'put').then(res => {
        this.$message.success(`操作成功`)
        this.$refs.table.refresh()
        this.$refs.table.clearSelected()
      })
    },
    showDeleteConfirm(params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk() {
          _this.deleteById(apiURLs.contractorIllegal, params).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.commonInit()
              _this.$refs.table.refresh()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    preview(record) {
      this.imageList = record.localePhotos.map(item => this.BASE_URL + '/spang-safety/file' + item.filePath)
      this.previewVisible = true
    }
  },
  created() {
    this.getDict('ILLEGAL_TYPE').then(({ data, success }) => {
      if (success) {
        this.illegalTypeOptions = data.map(item => ({
          ...item,
          dictKey: parseInt(item.dictKey)
        }))
      }
    })
  }
}
</script>
<style lang="less" scoped>
