<template>
  <page-container :title="false">
    <a-card
      :bordered="false"
      style="margin: 15px"
      :headStyle="{ border: 'none', paddingBottom: 0 }"
      :bodyStyle="{ paddingBottom: 0 }"
      class="table-card"
    >
      <template #title>
        <div class="text-right">
          <a-button type="primary" icon="plus" @click="addRecord" style="float: right">新建</a-button>
        </div>
      </template>
      <s-table
        ref="table"
        size="default"
        :rowKey="(record) => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        showPagination="true"
        :scroll="{ y: `calc(100vh - 432px)`, x: 860 }"
      >
        <!-- 序号 -->
        <span
          slot="number"
          slot-scope="text, record, index"
        >{{ pagination.pageSize * (pagination.current - 1) + index + 1 }}</span>
        <!-- 序号 end -->
        <template slot="status" slot-scope="text, record">
          <a-tag :color="record.status | colorFilter" style="margin-right: 0 !important">
            {{
            record.status | textFilter
            }}
          </a-tag>
        </template>
        <span slot="action" slot-scope="text, record">
          <template>
            <a @click="editRecord(record)" v-if="record.status !== 1 ">编辑</a>
            <a-divider type="vertical" v-if="record.status !== 1 " />
            <a @click="publishRecord(record, 1)" v-if="record.status !== 1 ">发布</a>
            <a-divider type="vertical" v-if="record.status !== 1 " />
            <a @click="viewRecord(record)" v-if="record.status === 1">查看</a>
            <a-divider type="vertical" v-if="record.status !== 0" />
            <a @click="publishRecord(record, 0)" v-if="record.status === 1 ">撤回</a>
            <a @click="removeRecord(record)" v-if="record.status !== 1 ">删除</a>
          </template>
        </span>
      </s-table>
      <common-form-modal
        ref="modal"
        :visible.sync="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        :rule="rules"
        width="900px"
        @ok="modalOk"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { costPlanColumns } from '@/views/safety/table'
import ApiURLs from '@/api/urls'
import { mapGetters } from 'vuex'
import { getStore } from '@/utils/store'
import CommonFormModal from '@/components/CommonFormModal/CommonFormModal'
import { getDict } from '@/api/system'

export default {
  name: 'rules',
  components: {
    STable,
    CommonFormModal
  },
  data() {
    return {
      pagination: {
        current: 1,
        pageSize: 10,
        showSizeChanger: true
      },
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonDisabled: false,
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      templateYearNameOptions: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        return this.getList(ApiURLs.costPlanList, requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.pagination = {
            current: data.pageNo,
            pageSize: data.pageSize,
            showSizeChanger: true
          }
          return data
        })
      },
      userInfo: getStore({ name: 'info' }),
      rules: [
        {
          type: 'a-row',
          native: true,
          children: [
            {
              type: 'a-col',
              props: {
                span: 24
              },
              children: [
                {
                  type: 'select',
                  title: '年度',
                  field: 'year',
                  props: {
                    maxLength: '32',
                    type: 'text',
                    placeholder: '请选择年度'
                  },
                  validate: [
                    {
                      required: true,
                      message: '请选择年度',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'input',
                  title: '安全生产费用',
                  field: 'safetyProductionCost',
                  props: {
                    maxLength: '32',
                    min: 0,
                    type: 'number',
                    placeholder: '请输入安全生产费用'
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入安全生产费用',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'span',
                  title: '元',
                  native: false,
                  _fc_drag_tag: 'span',
                  wrap: {
                    labelCol: { span: 24 },
                    wrapperCol: { span: 0 }
                  }
                },
                {
                  type: 'input',
                  title: '月度计提标准',
                  field: 'monthlyProvisionStandard',
                  props: {
                    maxLength: '32',
                    min: 0,
                    type: 'number',
                    placeholder: '请输入月度计提标准'
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入月度计提标准',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'span',
                  title: '元',
                  native: false,
                  _fc_drag_tag: 'span',
                  wrap: {
                    labelCol: { span: 24 },
                    wrapperCol: { span: 0 }
                  }
                },
                {
                  type: 'UploadFile',
                  field: 'files',
                  title: '审批文件',
                  props: {
                    fileList: []
                  },
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                }
              ]
            }
          ]
        }
      ]
    }
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => costPlanColumns,
    hasSelected() {
      return this.selectedRowKeys.length > 0
    },
    rowSelection() {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  filters: {
    colorFilter: function(value) {
      const colorList = ['orange', 'green']
      return colorList[value]
    },
    textFilter: function(value) {
      const textList = ['待发布', '已发布']
      return textList[value]
    }
  },
  methods: {
    initFormData() {
      this.commonData = {
        files: [],
        id: '',
        monthlyProvisionStandard: '',
        safetyProductionCost: '',
        status: '',
        year: ''
      }
      this.setFileList([])
    },
    setFileList(fileList) {
      if (this.$refs.modal && this.$refs.modal.fApi.getRule) {
        this.$refs.modal.fApi.getRule('files').props.fileList = fileList
      } else {
        this.rules[0].children[0].children[3].props.fileList = fileList
      }
      console.log(this.rules[0].children[0].children[3])
    },
    commonInit() {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.initFormData()
    },
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    searchReset() {
      this.queryParam = {}
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord() {
      this.commonTitle = '新增数据'
      this.initFormData()
      this.commonVisible = true
      this.commonDisabled = false
    },
    removeRecords() {
      const ids = this.selectedRows.map(item => item.id)
      this.showDeleteConfirm(ids)
    },
    getRecord(id, callback) {
      this.getById(ApiURLs.costPlan, id).then(({ data, success }) => {
        if (success) {
          this.commonData = data
          const files = data.files.map(item => ({
            uid: item.id,
            name: item.fileName,
            status: 'done',
            id: item.id,
            filePath: item.fileUrlPath
          }))
          this.setFileList(files)
          callback()
        }
      })
    },
    viewRecord(record) {
      this.commonTitle = '查看数据'
      this.initFormData()
      this.getRecord(record.id, () => {
        this.commonDisabled = true
        this.commonVisible = true
      })
    },
    editRecord(record) {
      this.commonTitle = '编辑数据'
      this.initFormData()
      this.getRecord(record.id, () => {
        this.commonDisabled = false
        this.commonVisible = true
      })
    },
    removeRecord(record) {
      this.showDeleteConfirm(record.id)
    },
    modalCancel() {
      this.commonInit()
    },
    modalOk({ formData }) {
      let method = ''
      if (this.isNotNullOrUndefined(this.commonData.id)) {
        method = 'put'
        formData.id = this.commonData.id
      } else {
        method = 'post'
      }

      const params = {
        ...formData,
        documentTypeName: this.commonData.documentTypeName,
        documentValidId: formData.documentValidId,
        files: this.$refs.modal.fApi.getRule('files').props.fileList.map(item => ({
          id: item.id,
          fileUrlPath: item.filePath,
          originalFileName: item.name
        }))
      }
      this.submitForm(ApiURLs.costPlan, params, method, {
        // headers: { 'Content-Type': 'multipart/form-data' }
      })
        .then(res => {
          this.$refs.table.refresh()
          this.$refs.table.clearSelected()
          this.commonInit()
        })
        .catch(() => {
          this.$message.error('当前年份的安全费用管理计划已存在!')
        })
    },
    //发布、撤回
    publishRecord(record, status) {
      var publishData = {
        id: record.id,
        status: status
      }
      this.submitForm(ApiURLs.costPlan, publishData, 'put').then(res => {
        this.$message.success(`操作成功`)
        this.$refs.table.refresh()
        this.$refs.table.clearSelected()
      })
    },
    showDeleteConfirm(params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk() {
          _this.deleteByIds(`${ApiURLs.costPlan}?id=${params}`).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    onTimeChange(value, dateString) {}
  },
  created() {
    //年度
    this.getDict('TEMPLATE_YEAR').then(({ data, success }) => {
      if (success) {
        this.templateYearNameOptions = data
        this.rules[0].children[0].children[0].options = this.templateYearNameOptions.map(item => ({
          label: item.dictValue,
          value: item.dictKey
        }))
      }
    })
  }
}
</script>
