<template>
  <page-container :title="false">
    <a-card :bordered="false" style="margin: 15px" :body-style="{'padding-bottom': '0px'}">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col  :md="8" :sm="12" :xs="24">
              <a-form-item label="年度">
                <a-select
                  show-search
                  v-model="queryParam.year"
                  placeholder="年度"
                  style="width: 100%"
                >
                  <a-select-option v-for="d in templateYearNameOptions" :key="d.dictKey">
                    {{
                    d.dictValue
                    }}
                  </a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col  :md="8" :sm="12" :xs="24">
              <a-form-item label="状态">
                <a-select
                  show-search
                  v-model="queryParam.status"
                  placeholder="状态"
                  style="width: 100%"
                >
                  <a-select-option v-for="d in statusOptions" :key="d.dictKey">
                    {{
                    d.dictValue
                    }}
                  </a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col  :md="8" :sm="12" :xs="24" class="table-page-search-submitButtons">
              <a-space>
                <a-button type="primary" @click="$refs.table.refresh(true)">查询</a-button>
                <a-button type="second" @click="searchReset">重置</a-button>
              </a-space>
            </a-col>
          </a-row>
        </a-form>
      </div>
    </a-card>
    <a-card
      :bordered="false"
      style="margin: 15px"
      :headStyle="{ border: 'none', paddingBottom: 0 }"
      :bodyStyle="{ paddingBottom: 0 }"
      class="table-card"
    >
      <template #title>
        <div class="text-right">
          <a-button type="primary" icon="plus" @click="addRecord" style="float: right">新建</a-button>
        </div>
      </template>
      <s-table
        ref="table"
        size="default"
        :rowKey="(record) => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        showPagination="true"
        :scroll="{ y: `calc(100vh - 432px)`, x: 1060 }"
      >
        <!-- 序号 -->
        <span
          slot="number"
          slot-scope="text, record, index"
        >{{ pagination.pageSize * (pagination.current - 1) + index + 1 }}</span>
        <!-- 序号 end -->
        <template slot="status" slot-scope="text, record">
          <a-tag :color="record.status | colorFilter" style="margin-right: 0 !important">
            {{
            record.status | textFilter
            }}
          </a-tag>
        </template>
        <span slot="action" slot-scope="text, record">
          <template>
            <a @click="editRecord(record)" v-if="record.status !== 1">编辑</a>
            <a-divider type="vertical" v-if="record.status !== 1" />
            <a @click="publishRecord(record, 1)" v-if="record.status !== 1">发布</a>
            <a-divider type="vertical" v-if="record.status !== 1" />
            <a @click="checkRecord(record)" v-if="record.status === 1">查看</a>
            <a-divider type="vertical" v-if="record.status !== 0" />
            <a-divider type="vertical" v-if="userInfo.id === record.gmtCreateId" />
            <a @click="publishRecord(record, 0)" v-if="record.status === 1">撤回</a>
            <a @click="removeRecord(record)" v-if="record.status !== 1">删除</a>
          </template>
        </span>
      </s-table>
      <common-form-modal
        ref="modal"
        :visible.sync="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        :rule="rules"
        width="900px"
        @ok="modalOk"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { costReceiveColumns } from '@/views/safety/table'
import ApiURLs from '@/api/urls'
import { mapGetters } from 'vuex'
import { getStore } from '@/utils/store'
import CommonFormModal from '@/components/CommonFormModal/CommonFormModal'
import { getDict } from '@/api/system'
import { getPersonnelTree } from '@/api/safety'

export default {
  name: 'rules',
  components: {
    STable,
    CommonFormModal
  },
  data() {
    return {
      pagination: {
        current: 1,
        pageSize: 10,
        showSizeChanger: true
      },
      templateYearNameOptions: [],
      statusOptions: [
        {
          dictKey: 0,
          id: '',
          dictValue: '未发布'
        },
        {
          dictKey: 1,
          id: '',
          dictValue: '已发布'
        }
      ],
      typeOptions: [
        {
          dictKey: 0,
          id: '',
          dictValue: '支出'
        },
        {
          dictKey: 1,
          id: '',
          dictValue: '收入'
        }
      ],
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonDisabled: false,
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        return this.getList(ApiURLs.costReceiveList, requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.pagination = {
            current: data.pageNo,
            pageSize: data.pageSize,
            showSizeChanger: true
          }
          return data
        })
      },
      userInfo: getStore({ name: 'info' }),
      rules: [
        {
          type: 'a-row',
          native: true,
          children: [
            {
              type: 'a-col',
              props: {
                span: 24
              },
              children: [
                {
                  type: 'select',
                  field: 'type',
                  title: '类型',
                  options: [],
                  validate: [
                    {
                      required: true,
                      message: '请选择类型',
                      trigger: 'blur'
                    }
                  ],
                  props: {
                    placeholder: '请选择类型'
                  },
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  },
                  on: {}
                },
                {
                  type: 'DepartmentSelect',
                  title: '领用单位',
                  field: 'recipientsUnitId',
                  props: {
                    maxLength: '32',
                    type: 'text',
                    placeholder: '请选择领用单位'
                  },
                  on: {
                    change: ({ label }) => {
                      this.commonData.recipientsUnitName = label
                    }
                  },
                  validate: [
                    {
                      required: true,
                      message: '请选择领用单位',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'select',
                  field: 'useProject',
                  title: '使用项目',
                  options: [],
                  validate: [
                    {
                      required: true,
                      message: '请选择使用项目',
                      trigger: 'blur'
                    }
                  ],
                  props: {
                    placeholder: '请选择使用项目'
                  },
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  },
                  on: {
                    change: id => {
                      const options = this.$refs.modal.fApi.getRule('useProject').options
                      this.$set(this.commonData, 'useProjectName', options.find(item => item.value === id).label)
                    }
                  }
                },
                {
                  type: 'input',
                  title: '领用人',
                  field: 'recorderName',
                  props: {
                    maxLength: '32',
                    type: 'text',
                    placeholder: '请输入领用人'
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入领用人',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'input',
                  title: '费用',
                  field: 'cost',
                  props: {
                    maxLength: '32',
                    type: 'number',
                    placeholder: '请输入费用（元）'
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入费用',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },

                {
                  type: 'DatePicker',
                  title: '领用日期',
                  field: 'receivingDate',
                  props: {
                    showTime: true,
                    format: 'YYYY-MM-DD',
                    placeholder: '领用日期'
                  },

                  validate: [
                    {
                      required: true,
                      message: '请选择领用日期',
                      trigger: 'blur'
                    }
                    // {
                    //   validator: (rule, value, callback) => {
                    //     if (new Date(value).getTime() > new Date().getTime()) {
                    //       callback(new Error('颁布日期不能晚于当前时间'))
                    //     } else {
                    //       callback()
                    //     }
                    //   },
                    //   trigger: 'changed'
                    // }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },

                {
                  type: 'input',
                  title: '具体项目',
                  field: 'specificItems',
                  props: {
                    maxLength: '32',
                    type: 'text',
                    placeholder: '请输入具体项目'
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入具体项目',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'UploadFile',
                  field: 'files',
                  title: '相关附件',
                  props: {
                    fileList: []
                  },
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                }
              ]
            }
          ]
        }
      ]
    }
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => costReceiveColumns,
    hasSelected() {
      return this.selectedRowKeys.length > 0
    },
    rowSelection() {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  filters: {
    colorFilter: function(value) {
      const colorList = ['orange', 'green']
      return colorList[value]
    },
    textFilter: function(value) {
      const textList = ['未发布', '已发布']
      return textList[value]
    }
  },
  methods: {
    initFormData() {
      this.commonData = {
        cost: '',
        files: [],
        id: '',
        receivingDate: '',
        recipientsUnitId: '',
        recipientsUnitName: '',
        recorderId: '',
        recorderName: '',
        specificItems: '',
        status: '',
        type: '',
        useProject: '',
        useProjectName: ''
      }
      this.setFileList([])
    },
    setFileList(fileList) {
      if (this.$refs.modal && this.$refs.modal.fApi.getRule) {
        this.$refs.modal.fApi.getRule('files').props.fileList = fileList
      } else {
        this.rules[0].children[0].children[7].props.fileList = fileList
      }
      console.log(this.rules[0].children[0].children[7])
    },
    commonInit() {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.initFormData()
    },
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    searchReset() {
      this.queryParam = {}
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord() {
      this.commonTitle = '新增数据'
      this.initFormData()
      this.commonVisible = true
      this.commonDisabled = false
    },
    removeRecords() {
      const ids = this.selectedRows.map(item => item.id)
      this.showDeleteConfirm(ids)
    },
    getRecord(id, callback) {
      this.getById(ApiURLs.costReceive, id).then(({ data, success }) => {
        if (success) {
          this.commonData = data
          const files = data.files.map(item => ({
            uid: item.id,
            name: item.fileName,
            status: 'done',
            id: item.id,
            filePath: item.fileUrlPath
          }))
          this.commonData.useProject = this.commonData.useProjectName
          this.setFileList(files)
          callback()
        }
      })
    },
    checkRecord(record) {
      this.commonTitle = '查看数据'
      this.initFormData()
      this.getRecord(record.id, () => {
        this.commonDisabled = true
        this.commonVisible = true
      })
    },
    editRecord(record) {
      this.commonTitle = '编辑数据'
      this.initFormData()
      this.getRecord(record.id, () => {
        this.commonDisabled = false
        this.commonVisible = true
      })
    },
    removeRecord(record) {
      this.showDeleteConfirm(record.id)
    },
    modalCancel() {
      this.commonInit()
    },
    modalOk({ formData }) {
      let method = ''
      if (this.isNotNullOrUndefined(this.commonData.id)) {
        method = 'put'
        formData.id = this.commonData.id
        var useProjectOption = this.$refs.modal.fApi.getRule('useProject').options
        useProjectOption.forEach(item => {
          if (this.commonData.useProject === item.label) {
            formData.useProject = item.value
          }
        })
      } else {
        method = 'post'
        formData.status = 0
      }

      const params = {
        ...formData,
        recipientsUnitName: this.commonData.recipientsUnitName,
        useProjectName: this.commonData.useProjectName,
        files: this.$refs.modal.fApi.getRule('files').props.fileList.map(item => ({
          id: item.id,
          fileUrlPath: item.filePath,
          originalFileName: item.name
        }))
      }
      this.submitForm(ApiURLs.costReceive, params, method, {
        // headers: { 'Content-Type': 'multipart/form-data' }
      }).then(res => {
        this.$refs.table.refresh()
        this.$refs.table.clearSelected()
        this.commonInit()
      })
    },
    publishRecord(record, status) {
      this.submitForm(
        ApiURLs.costReceive,
        {
          id: record.id,
          status: status
        },
        'put'
      ).then(res => {
        this.$message.success(`操作成功`)
        this.$refs.table.refresh()
        this.$refs.table.clearSelected()
      })
    },
    showDeleteConfirm(params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk() {
          _this.deleteByIds(`${ApiURLs.costReceive}?id=${params}`).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    onTimeChange(value, dateString) {}
  },
  created() {
    //年度
    this.getDict('TEMPLATE_YEAR').then(({ data, success }) => {
      if (success) {
        this.templateYearNameOptions = data
      }
    })
    //类型
    this.rules[0].children[0].children[0].options = this.typeOptions.map(item => ({
      label: item.dictValue,
      value: item.dictKey
    }))
    //使用项目
    this.getDict('SECURITY_COST_USE_PROJECT').then(({ data, success }) => {
      if (success) {
        this.rules[0].children[0].children[2].options = data.map(item => ({
          label: item.dictValue,
          value: item.dictKey
        }))
      }
    })
  }
}
</script>
<style lang="less" scoped>
/deep/ .ant-table {
  height: auto;
}

/deep/ .table-card .ant-card-body {
  padding-top: 0;
  padding-bottom: 15px;
}

/deep/ .ant-modal-body {
  span.ant-form-item-children {
    width: 100%;
    display: block;

    .ant-checkbox-group {
      width: 100%;
    }

    .ant-calendar-picker {
      display: block;
      width: 100%;
    }
  }
}
/deep/ .ant-pagination {
  display: none;
}
/deep/ .ant-table-body {
  overflow-y: auto !important;
}
</style>
