<template>
  <a-modal
    :title="modalTitle"
    :visible="visible"
    :confirmLoading="loading"
    @ok="() => { $emit('ok') }"
    @cancel="() => { $emit('cancel') }"
    ok-text="确定"
    cancel-text="取消"
    width="50%"
    :keyboard="false"
    :maskClosable="false"
  >
    <a-spin :spinning="loading">
      <a-form :form="form">
        <a-row :gutter="24">
          <a-col :md="12">
            <a-form-item label="序号" hidden="true">
              <a-input v-decorator="['id',{ rules:[{ required: false, message: '请输入机构名称' }]}]" :disabled="disabled" />
            </a-form-item>
            <a-form-item label="机构名称">
              <a-input v-decorator="['entName',{ rules:[{ required: true, message: '请输入机构名称' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :md="12">
            <a-form-item label="资料名称">
              <a-input v-decorator="['name',{ rules:[{ required: true, message: '请输入物资名称' }]}]" :disabled="disabled"/>
            </a-form-item>
          </a-col>
        </a-row>
        <a-row :gutter="24">
          <a-col :md="12">
            <file-upload
              docName="docName"
              docUrl="docUrl"
              labelName="附件"
              :form="form"
              required
              :disabled="disabled"
              :visible="visible"/>
          </a-col>
        </a-row>
      </a-form>
    </a-spin>
  </a-modal>
</template>

<script>
import pick from 'lodash.pick'
import { cultureColumns } from '@/views/safety/table'
import FileUpload from '@/views/safety/components/FileUpload'

const fields = cultureColumns.map(res => {
  return res.dataIndex
})
fields.push('docUrl')
export default {
  components: { FileUpload },
  name: 'CultureModal',
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    loading: {
      type: Boolean,
      default: () => false
    },
    model: {
      type: Object,
      default: () => null
    },
    selectOptions: {
      type: Object,
      default: () => null
    },
    modalTitle: {
      type: String,
      default: '操作'
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data () {
    return {
      headers: {
          authorization: 'authorization-text'
      },
      form: this.$form.createForm(this)
    }
  },
  created () {
    // 手动添加detail属性的dataindex
    // fields.push('id')
    // 防止表单未注册
    fields.forEach(v => this.form.getFieldDecorator(v))
    // 当 model 发生改变时，为表单设置值
    this.$watch('model', () => {
      this.previewImage = this.model.logo
      this.model && this.form.setFieldsValue(pick(this.model, fields))
    })
  },
  methods: {

  }
}
</script>
