<template>
  <page-container :title="false">
    <a-card>
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="6">
              <a-form-item label="资料名称">
                <a-input v-model="queryParam.name"/>
              </a-form-item>
            </a-col>
            <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="$refs.table.refresh(true)">查询</a-button>
                <a-button type="second" @click="searchReset" style="margin-left: 10px">重置</a-button>
              </span>
            </a-col>
          </a-row>
        </a-form>
      </div>
      <div class="table-operator">
        <a-button type="primary" icon="plus" @click="addRecord" v-if="permission.culture_add">新增</a-button>
        <a-button type="second" icon="delete" @click="removeRecords" :disabled="!hasSelected" v-if="permission.culture_deletes">批量删除</a-button>
      </div>
      <s-table
        ref="table"
        size="default"
        rowKey="rowKey"
        :scroll="{y: `calc(100vh - 360px)`}"
        :columns="columns"
        :data="loadData"
        :alert="false"
        :rowSelection="rowSelection"
        showPagination="true"
      >
        <span slot="docName" slot-scope="text, record">
          <template>
            <a-button type="link" @click="fileDownLoad(record.docUrl)" :disabled="record.docName===''">{{ text || '暂无文件' }}</a-button>
          </template>
        </span>
        <span slot="action" slot-scope="text, record">
          <template>
            <!--            <a @click="checkRecord(record)" v-if="permission.culture_view">查看</a>-->
            <!--            <a-divider type="vertical"/>-->
            <a @click="editRecord(record)" v-if="permission.culture_edit">编辑</a>
            <a-divider type="vertical"/>
            <a @click="removeRecord(record)" v-if="permission.culture_delete" style="margin-right: 15px">删除</a>
          </template>
        </span>
      </s-table>
      <culture-modal
        ref="modal"
        :visible="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :select-options="selectOptions"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        @cancel="modalCancel"
        @ok="modalOk"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { cultureColumns } from '@/views/safety/table'
import CultureModal from '@/views/safety/culture/CultureModal'
import { getCostList, submitCostItem, deleteCost, fileDownload } from '@/api/safety'
import { mapGetters } from 'vuex'

export default {
  components: {
    STable,
    CultureModal
  },
  data () {
    return {
      pageName: '',
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: null,
      commonMode: true, // add：true edit：false
      commonDisabled: false,
      selectOptions: [],
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      data: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        console.log(')))))))))))))' + this.$route.name)
        return getCostList(requestParameters, this.$route.name).then(res => {
          console.log(res)
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.data = data.data

          return data
        })
      }
    }
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => cultureColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  mounted () {
      this.pageName = this.$route.name
      console.log('+++++++++' + this.pageName)
  },
  watch: {
      $route: { // 复用的组件再次加载触发
        // immediate: true,
        // handler (to, form) {
        //   // TODO
        //   console.log(this.$route)
        // }
        handler: 'resetData'
      }
  },
  methods: {
    resetData () {
      this.pageName = this.$route.name
      console.log('----------' + this.pageName)
      this.$refs.table.clearSelected()
      this.$refs.table.refresh(true)
      // if(this.$route.fullPath=='/strategy'){
      //   //在这里获取并处理该路由下所需要的数据。
      // }
    },
    commonInit () {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.commonMode = true
      this.confirmLoading = false
      const form = this.$refs.modal.form
      form.resetFields()
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    cultureResearch () {
      this.$message.success('查询')
      this.$refs.table.refresh(true)
    },
    searchReset () {
      this.queryParam = {}
      this.$refs.table.refresh(true)
    },
    addRecord () {
      this.commonTitle = '新增数据'
      // this.$message.success('新增')
      this.commonMode = true
      this.commonVisible = true
    },
    removeRecords () {
      var ids = ''
      this.selectedRows.forEach((item, index) => {
          ids = ids + item.id + ','
        })
        console.log(ids)
        this.showDeleteConfirm(ids)
    },
    checkRecord (record) {
      // this.$message.success('查看')
      this.commonTitle = '查看数据'
      this.commonVisible = true
      this.commonData = { ...record }
      this.commonDisabled = true
    },
    editRecord (record) {
      this.commonTitle = '编辑数据'
      // this.$message.success('编辑')
      this.commonMode = false
      this.commonVisible = true
      this.commonData = { ...record }
    },
    removeRecord (record) {
      console.log(record)
      this.showDeleteConfirm(record.id + '')
    },
    showFile (filename) {
      this.$message.success(`${filename}`)
    },
    modalCancel () {
      this.commonInit()
    },
    modalOk () {
      const _this = this
      if (_this.commonDisabled) {
        _this.$message.success('查看返回')
        _this.commonInit()
      } else {
        const form = _this.$refs.modal.form
        form.validateFields((errors, values) => {
          if (!errors) {
            const requestForm = values
            if (_this.commonMode) {
              // 新增接口
            } else {
              // 编辑接口
              requestForm.id = _this.commonData.id
            }
            _this.confirmLoading = true
            submitCostItem(requestForm, _this.$route.name).then(res => {
              _this.commonInit()
              _this.$refs.table.refresh(true)
            }).catch(error => {
              _this.$message.error(`${error.response.data.msg}`)
              _this.confirmLoading = false
            })
          }
        })
      }
    },
    showDeleteConfirm (params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk () {
          console.log('OK')
          deleteCost(params, _this.$route.name).then(res => {
            console.log(res)
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh(true)
              _this.$refs.table.clearSelected()
            } else {
              _this.$message.error('删除失败')
            }
            })
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    },
    fileDownLoad (url) {
      fileDownload({ link: url }).then(res => {
        window.open(res.data)
      })
    }
  }
}
</script>
<style lang="less" scoped>
/deep/ .ant-table {
  height: calc(100vh - 290px);
}
/deep/ .ant-card-body{
  .ant-table-wrapper {
    min-height: calc(100vh - 225px);
  }
}
</style>
