<template>
  <a-modal
    :title="title"
    width="70%"
    :body-style="{maxHeight: '700px', overflowY: 'auto'}"
    :visible="visible"
    :closable="true"
    @cancel="() => { $emit('update:visible', false) }"
    :maskClosable="false"
    :keyboard="false"
    :destroyOnClose="true"
  >
    <template slot="footer">
      <a-button @click="() => { $emit('update:visible', false) }">取消</a-button>
      <a-button type="primary" @click="save" v-if="!isEdit">确定</a-button>
    </template>
    <div class="info-title">
      <span>消防检查计划</span>
    </div>
    <a-divider/>
    <a-card
      :bordered="false"
      :headStyle="{paddingBottom: 0}"
      :bodyStyle="{paddingBottom: 0}">
      <div class="table-page-search-wrapper">
        <form-create
          ref="basicInfo"
          v-model="fApi"
          :rule="rules"
          :option="option"
          :value="form"
        />
      </div>
    </a-card>
    <div class="info-title">
      <span>消防器材</span>
      <template v-if="!isEdit">
        <a-button type="primary" @click="addEquipment" style="margin: 0 4px">
          添加
        </a-button>
        <a-button type="primary" @click="selectPerson" style="margin: 0 4px" :disabled="selectedRowKeys.length === 0">
          批量选择检查人
        </a-button>
      </template>
    </div>
    <a-divider/>
    <a-card
      class="table-card"
      :bordered="false"
      :headStyle="{paddingBottom: 0}"
    >
      <a-table
        bordered
        size="small"
        class="equipmentTable"
        :pagination="false"
        :row-selection="selectOption"
        :columns="columns"
        :data-source="equipmentData"
      >
        <template slot="action" slot-scope="text,record,index" v-if="!isEdit">
          <a @click="choosePerson(record,index)">选择排查人</a>
          <a-divider type="vertical"/>
          <a @click="removeRecord(record,index)">删除</a>
        </template>
        <template slot="planCheckPersonList" slot-scope="text,record,index">
          <a-tag color="blue" v-for="item in record.planCheckPersonList" :key="item.checkPersonId" style="margin: 2px">{{item.checkPerson}}</a-tag>
        </template>
        <template slot="checkResult" slot-scope="text,record,index">
          <a-tag :color="record.checkResult | colorFilter">{{record.checkResult | textFilter}}</a-tag>
        </template>
      </a-table>
    </a-card>
    <fire-equipment-select
      :visible="equipmentVisible"
      @cancel="() => {this.equipmentVisible = false}"
      @ok="chooseEquipment"
    ></fire-equipment-select>
    <PersonSelectModal title="选择排查人" multiple :visible="personVisible" @ok="savePerson" @cancel="()=>{this.personVisible = false}"></PersonSelectModal>
  </a-modal>
</template>

<script>
import FireApi from '@/api/fire'
import DepartmentSelect from '@/views/safety/components/DepartmentSelect'
import TestPaperManagement from '@/views/safety/orientation/testPaperManagement'
import { firePlanItemColumns } from '@/views/safety/fire/columns'
import FireEquipmentSelect from '@/views/safety/fire/FireEquipmentSelect'
import PersonSelectModal from '@/views/safety/task/component/PersonSelectModal'
import PersonListSelect from '@/views/safety/components/PersonListSelect'
import cloneDeep from 'lodash.clonedeep'

export default {
  name: 'FirePlanModal',
  components: {
    TestPaperManagement,
    PersonListSelect,
    DepartmentSelect,
    FireEquipmentSelect,
    PersonSelectModal
  },
  data () {
    return {
      planTypeOptions: [],
      // queryParam: {},
      selectedRowKeys: [],
      equipmentData: [],
      equipmentVisible: false,
      personVisible: false,
      currentRecordIndex: 0,
      selectType: 0,
      searchTime: [],
      fApi: {},
      option: {
        submitBtn: false,
        resetBtn: false,
        wrap: {
          labelCol: { span: 5 },
          wrapperCol: { span: 17 }
        },
        mounted: () => {
          this.fApi.clearValidateState()
          this.form = cloneDeep(this.queryParam)
          this.fApi.disabled(this.isEdit)
        }
      },
      basicInfo: {}, // 基本信息
      form: {},
      rules: [
        {
          type: 'input',
          field: 'name',
          title: '计划名称',
          props: {
            maxLength: 32,
            placeholder: '请输入计划名称'
          },
          col: {
            span: 12
          },
          on: {
            change: (e) => {
              this.form.name = e.target.value
            }
          },
          validate: {
            required: true,
            message: '请输入计划名称'
          }
        },
        {
          type: 'select',
          field: 'type',
          title: '计划类型',
          options: [],
          props: {
            placeholder: '请选择计划类型'
          },
          col: {
            span: 12
          },
          on:{
            change: (value) => {
              this.form.type = value
            }
          },
          validate: {
            required: true,
            message: '请选择计划类型'
          }
        },
        {
          type: 'RangePicker',
          field: 'searchTime',
          title: '检查时间',
          props: {
            disabled: false,
            showTime: true,
            valueFormat: 'YYYY-MM-DD HH:mm:ss',
            placeholder: '请选择检查时间'
          },
          on: {
            change: (value) => {
              this.form.startDate = value[0]
              this.form.endDate = value[1]
              this.form.searchTime = value
            }
          },
          col: {
            span: 12
          },
          validate: {
            required: true,
            message: '请选择检查时间'
          }
        },
        {
          type: 'span',
          field: 'organizationalUnit',
          title: '组织单位',
          children: [],
          col: {
            span: 12
          },
        },
        {
          type: 'PersonListSelect',
          field: 'chargerId',
          title: '负责人',
          props: {
            placeholder: '请选择负责人'
          },
          on: {
            change: ({ id,person }) => {
              this.form.chargerId = id
              this.form.chargerName = person.realName
            }
          },
          col: {
            span: 12
          },
          validate: {
            required: true,
            message: '请选择负责人'
          }
        },
        {
          type: 'DepartmentSelect',
          field: 'checkUnitId',
          title: '检查单位',
          props: {
            placeholder: '请选择检查单位'
          },
          on: {
            change: ({ label }) => {
              console.log(label)
              this.form.checkUnitName = label
              console.log(this.form)
            }
          },
          col: {
            span: 12
          },
          validate: {
            required: true,
            message: '请选择检查单位'
          }
        }
      ]
    }
  },
  props: {
    queryParam: {
      type: Object,
      default: {}
    },
    equipmentParam: {
      type: Array,
      default: []
    },
    isEdit: {
      type: Boolean,
      default: false
    },
    visible: {
      type: Boolean,
      default: false
    },
    title: {
      type: String,
      default: '详情'
    }
  },
  watch:{
    visible: {
      handler: function (val) {
        this.equipmentData = cloneDeep(this.equipmentParam)
        this.rules[3].children[0] = this.queryParam.organizationalUnit
      }
    }
  },
  created () {
    this.getDict('FIRE_CHECK_PLAN_TYPE').then(({ data, success}) => {
      if(success) {
        this.planTypeOptions = data
        this.rules[1].options = data.map(item => {
          return {
            value: item.dictKey,
            label: item.dictValue
          }
        })
      }
    })
  },
  computed: {
    columns () {
      if(this.isEdit) {
        if (firePlanItemColumns.length === 7) {
          firePlanItemColumns.pop()
          firePlanItemColumns.push({
            title: '检查结果',
            dataIndex: 'checkResult',
            width: '120px',
            align: 'center',
            ellipsis: true,
            scopedSlots: { customRender: 'checkResult' }
          })
          firePlanItemColumns.push({
            title: '说明',
            dataIndex: 'description',
            width: '180px',
            align: 'center',
            ellipsis: true
          })
        }
      } else {
        if(firePlanItemColumns.length === 8) {
          firePlanItemColumns.pop()
          firePlanItemColumns.pop()
          firePlanItemColumns.push({
            title: '操作',
            dataIndex: 'action',
            width: '180px',
            align: 'center',
            scopedSlots: { customRender: 'action' }
          })
        }
      }
      return firePlanItemColumns
    },
    hasSelected() {
      return this.selectedRowKeys.length > 0;
    },
    selectOption () {
      if(this.isEdit) {
        return null
      } else {
        return { selectedRowKeys: this.selectedRowKeys, onChange: this.onSelectChange }
      }
    }
  },
  filters: {
    colorFilter: function (value) {
      const colorList = ['green', 'orange','blue']
      return colorList[value]
    },
    textFilter: function (value) {
      const textList = ['正常', '异常', '异常已处理']
      return textList[value]
    }
  },
  methods: {
    // loadDetailData () {
    //   this.getById(FireApi.firePlan, this.id).then(({ data, success}) => {
    //     if(success) {
    //       this.queryParam = data
    //       this.equipmentData = data.planEquipmentList
    //     }
    //   })
    // },
    onSelectChange(selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys;
    },
    onTimeChange (value, dateString) {
      this.queryParam.startDate = dateString[0]
      this.queryParam.endDate = dateString[1]
    },
    addEquipment () {
      this.equipmentVisible = true
    },
    selectPerson () {
      this.selectType = 1
      this.personVisible = true
    },
    cancel () {
      this.$emit('cancel')
    },
    save () {
      this.$emit('saveForm')
      this.fApi.submit((formData, fApi) => {
        if(!this.equipmentData.length) {
          this.$message.error('请选择消防器材后提交')
          return
        }
        for(let item of this.equipmentData) {
          if(!item.planCheckPersonList || item.planCheckPersonList.length===0) {
            this.$message.error('请完善消防器材排查人信息')
            return
          }
        }
        let method = 'post'
        const params = {
          ...this.form,
          ...formData,
          planEquipmentList: this.equipmentData,
          status: 0
        }
        if(this.form.id) {
          method = 'put'
          delete params.status
        }
        this.submitForm(FireApi.firePlan, params, method).then(({success}) =>{
          if(success) {
            this.$message.success('操作成功')
            this.$emit('ok')
          }
        })
      })
    },
    chooseEquipment ({values}) {
      const equipmentData = []
      const equipmentDataIds = this.equipmentData.map(item => {
        return item.equipmentId
      })
      console.log(equipmentDataIds)
      for(let item of values) {
        console.log(item.id)
        if(equipmentDataIds.indexOf(item.id) <0) {
          equipmentData.push({
            address: item.address,
            equipmentId: item.id,
            models: item.models,
            name: item.name,
            number: item.number,
          })
        }
      }
      this.equipmentVisible = false
      this.equipmentData = this.equipmentData.concat(equipmentData)
    },
    organizeDept ({ id, label}) {
      this.form.setFieldsValue({
        organizationalUnitId: label
      })
      this.queryParam.organizationalUnitId = id
      this.queryParam.organizationalUnit = label
    },
    checkDept ({ id, label}) {
      this.queryParam.checkUnitId = id
      this.queryParam.checkUnitName = label
    },
    chargeSelect ({ id, person}) {
      console.log(id, person)
      this.queryParam.chargerId = id
      this.queryParam.chargerName = person.realName
    },
    removeRecord (record, index) {
      console.log(index)
      this.equipmentData.splice(index, 1)
      // this.showDeleteConfirm(record.id)
    },
    showDeleteConfirm (params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk () {
          _this.deleteByIds(FireApi.firePlan + `?id=${params}`, null).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.$refs.table.refresh(true)
              _this.$refs.table.clearSelected()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    },
    choosePerson (record, index) {
      console.log(index)
      this.selectType = 0
      this.currentRecordIndex = index
      this.personVisible = true
    },
    savePerson(data) {
      const personList = []
      data.forEach(item => {
        personList.push({
          checkPerson: item.userName,
          checkPersonId: item.userId
        })
      })
      if(this.selectType === 0) {
        this.equipmentData[this.currentRecordIndex].planCheckPersonList	 = personList
        console.log(this.equipmentData)
        this.personVisible = false
      } else {
        for(let item of this.selectedRowKeys) {
          this.equipmentData[item].planCheckPersonList = personList
        }
        this.personVisible = false
      }
    },
    typeChange (e) {
      console.log(e)
      this.queryParam.typeName = this.planTypeOptions[e].dictValue
    }
  }
}
</script>

<style lang="less" scoped>
.info-title {
  font-size: 16px;
  color: blue;

  span {
    margin-right: 10px;
  }
}

/deep/ .ant-divider-horizontal {
  margin: 7px 0;
}

.equipmentTable {
  margin-top: 0px;
  /deep/.ant-table-body {
    margin: 0!important;
  }
}

.fire-item-footer {
  margin: 10px;
  display: flex;
  justify-content: center;
  flex-direction: row
}

/deep/.ant-calendar-picker {
  width: 100%!important;
}

/deep/.ant-table-header {
  //overflow-y: auto!important;
  //padding-right: 17px;
}
</style>