<template>
  <page-container :title="false">
    <a-card :bordered="false" style="margin: 15px"  :body-style="{'padding-bottom': '0px'}">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="位置">
                <a-input v-model="queryParam.address" maxLength="32" placeholder="位置"/>
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="生产负责人">
                <a-input v-model="queryParam.productChargerName" maxLength="32" placeholder="生产负责人"/>
              </a-form-item>
            </a-col>
            <a-col :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="消防负责人">
                <a-input v-model="queryParam.chargePerson" maxLength="32" placeholder="消防负责人"/>
              </a-form-item>
            </a-col>
              <a-col  :lg="6" :md="24" :sm="12" :xs="24" class="table-page-search-submitButtons">
              <a-space>
                <a-button type="primary" @click="search">查询</a-button>
                <a-button type="second" @click="searchReset">重置</a-button>
              </a-space>
            </a-col>
          </a-row>
        </a-form>
      </div>
    </a-card>
    <a-card
      :bordered="false"
      style="margin: 15px;"
      :headStyle="{border: 'none', paddingBottom: 0}"
      :bodyStyle="{paddingBottom: 0}"
      class="table-card"
    >
      <template #extra>
        <a-button type="primary" icon="plus" @click="addRecord" v-if="permission.fire_important_add">
          新建
        </a-button>
      </template>
      <s-table
        ref="table"
        size="default"
        :filterMultiple="false"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        :scroll="{y:`calc(100vh - 432px)`, x: 1100}"
        showPagination="true"
      >
        <span slot="number" slot-scope="text, record, index">
          {{ pagination.pageSize * (pagination.current - 1) + index + 1 }}
        </span>
        <span slot="action" slot-scope="text, record">
          <template v-if="record.status === 0">
            <a @click="editRecord(record)" v-if="permission.fire_important_edit">编辑</a>
            <a-divider type="vertical" v-if="permission.fire_important_edit"/>
            <a @click="publishRecord(record,1)" v-if="permission.fire_important_publish">发布</a>
            <a-divider type="vertical" v-if="permission.fire_important_publish"/>
            <a @click="removeRecord(record)" v-if="permission.fire_important_delete">删除</a>
          </template>
          <template v-if="record.status === 1">
            <a @click="publishRecord(record,0)" v-if="permission.fire_important_recall">撤回</a>
            <a-divider type="vertical" v-if="permission.fire_important_recall"/>
            <a @click="viewRecord(record)">查看</a>
          </template>
        </span>
        <template slot="statusName" slot-scope="text, record">
          <a-tag :color="record.status | colorFilter" style="margin-right: 0!important;">{{record.status | textFilter}}</a-tag>
        </template>
      </s-table>
      <common-form-modal
        ref="modal"
        :visible.sync="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        :rule="rules"
        width="700px"
        @ok="modalOk"
      />
    </a-card>
  </page-container>
</template>

<script>
import { STable } from '@/components'
import CommonFormModal from '@/components/CommonFormModal/CommonFormModal'
import { getStore } from '@/utils/store'
import { fireImportantPositionColumns } from '@/views/safety/fire/columns'
import FireApi from '@/api/fire'
import { mapGetters } from 'vuex'

export default {
  name: 'fireImportantPosition',
  components: {
    STable,
    CommonFormModal
  },
  data () {
    return {
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonDisabled: false,
      queryParam: {
        address: '',
        chargePerson: '',
        productChargerName: ''
      },
      selectedRowKeys: [],
      selectedRows: [],
      userInfo: getStore({ name: 'info'}),
      pagination: {
        current: 1,
        pageSize: 10,
        showSizeChanger: true,
      },
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        return this.getList(FireApi.fireImportantPositionList, requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.pagination = {
            current: data.pageNo,
            pageSize: data.pageSize,
            showSizeChanger: true,
          }
          return data
        })
      },
      rules: [
        {
          type: 'input',
          title: '位置信息',
          field: 'address',
          props: {
            maxLength: 32,
            placeholder: '请输入位置信息'
          },
          validate: [{
            required: true,
            message: '请输入位置信息',
            trigger: 'blur'
          }],
          wrap: {
            labelCol: { span: 4 },
            wrapperCol: { span: 19 }
          }
        },
        {
          type: 'DepartmentSelect',
          title: '所属部门',
          field: 'departmentId',
          props: {
            placeholder: '请选择所属部门'
          },
          on: {
            change: ({ label }) => {
              this.commonData.departmentName = label
            }
          },
          validate: [{
            required: true,
            message: '请选择所属部门',
            trigger: true
          }],
          wrap: {
            labelCol: { span: 4 },
            wrapperCol: { span: 19 }
          }
        },
        {
          type: 'PersonListSelect',
          title: '消防负责人',
          field: 'chargePersonId',
          props: {
            placeholder: '请选择消防负责人'
          },
          on: {
            change: ({ person }) => {
              this.commonData.chargePerson = person.realName
            }
          },
          validate: [{
            required: true,
            message: '请选择消防负责人',
            trigger: true
          }],
          wrap: {
            labelCol: { span: 4 },
            wrapperCol: { span: 19 }
          }
        },
        {
          type: 'PersonListSelect',
          title: '生产负责人',
          field: 'productChargerId',
          props: {
            placeholder: '请选择生产负责人'
          },
          on: {
            change: ({ person }) => {
              this.commonData.productChargerName = person.realName
            }
          },
          validate: [{
            required: true,
            message: '请选择生产负责人',
            trigger: true
          }],
          wrap: {
            labelCol: { span: 4 },
            wrapperCol: { span: 19 }
          }
        },
        {
          type: 'input',
          title: '配备消防器材及设施',
          field: 'otherFacilities',
          props: {
            maxLength: 255,
            type: 'textarea',
            autoSize: { minRows: 4, maxRows: 6},
            placeholder: '请输入配备消防器材及设施'
          },
          validate: [{
            required: true,
            message: '请输入配备消防器材及设施',
            trigger: 'blur'
          }],
          wrap: {
            labelCol: { span: 6 },
            wrapperCol: { span: 19,offset: 4 }
          }
        }
      ]
    }
  },
  created () {

  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => fireImportantPositionColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  filters: {
    colorFilter: function (value) {
      const colorList = ['orange', 'green']
      return colorList[value]
    },
    textFilter: function (value) {
      const textList = ['未发布', '已发布']
      return textList[value]
    }
  },
  methods: {
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    search () {
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    searchReset () {
      this.initQueryParam()
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    initQueryParam () {
      this.queryParam = {
        address: '',
        chargePerson: '',
        productChargerName: ''
      }
    },
    commonInit () {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.initFormData()
    },
    initFormData () {
      this.commonData = {
        address: null,
        chargePersonId: undefined,
        otherFacilities: null,
        departmentId: undefined,
        productChargerId: undefined,
      }
    },
    addRecord (record) {
      this.commonTitle = '新增数据'
      this.initFormData()
      this.commonVisible = true
      this.commonDisabled = false
    },
    modalOk({ formData }) {
      let method = ''
      const params = {
        ...this.commonData,
        ...formData,
      }
      if (this.isNotNullOrUndefined(this.commonData.id)) {
        method = 'put'
        delete params.status
      } else {
        method = 'post'
      }
      this.submitForm(FireApi.fireImportantPosition, params, method).then(({ success }) => {
        if(success) {
          this.$message.success('操作成功')
          this.initQueryParam()
          this.commonInit()
          this.$refs.table.refresh()
          this.$refs.table.clearSelected()
        }
      })
    },
    editRecord (record) {
      this.commonTitle = '编辑数据'
      this.getById(FireApi.fireImportantPosition, record.id).then(({ data, success}) => {
        if(success) {
          this.commonData = data
          this.commonDisabled = false
          this.commonVisible = true
        }
      })
    },
    viewRecord (record) {
      this.commonTitle = '查看数据'
      this.getById(FireApi.fireImportantPosition, record.id).then(({ data, success}) => {
        if(success) {
          this.commonVisible = true
          this.commonDisabled = true
          this.commonData = data
        }
      })
    },
    removeRecord (record) {
      this.showDeleteConfirm(record.id)
    },
    showDeleteConfirm (params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk () {
          _this.deleteByIds(FireApi.fireImportantPosition + `?id=${params}`, null).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.$refs.table.refresh()
              _this.$refs.table.clearSelected()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    },
    publishRecord (record, status) {
      const params = {
        id: record.id,
        status: status
      }
      this.submitForm(FireApi.fireImportantPosition, params, 'put').then(({ success }) => {
        if(success) {
          this.$message.success('操作成功')
          this.initQueryParam()
          this.$refs.table.refresh()
          this.$refs.table.clearSelected()
        }
      })
    }
  }
}
</script>

<style lang="less" scoped>
/deep/ .ant-table {
  height: auto;
}

/deep/ .table-card .ant-card-body {
  padding-top: 0;
  padding-bottom: 15px;
}

.card-title {
  display: flex;
  justify-content: space-between;
}
/deep/ .table-page-search-wrapper .ant-form-inline .ant-form-item > .ant-form-item-label {
  width: 80px;
}
</style>
