<template>
  <a-modal
    ref="foreModal"
    :title="title"
    :visible="visible"
    :confirm-loading="Loading"
    width="600px"
    @ok="() => { $emit('ok') }"
    @cancel="() => { $emit('cancel') }"
    okText="保存"
    cancelText="取消"
    :keyboard="false"
    :maskClosable="false"
  >
    <a-form :form="form">
      <a-row :gutter="24" v-if="modalMode">
        <a-col :span="24">
          <a-form-item label="沟通方式">
            <a-select v-decorator="['mode', { rules:[{ required: true, message: '请选择沟通方式' }]}]">
              <a-select-option v-for="d in alarmCommunicationOptions" :key="d.dictKey">
                {{ d.dictValue }}
              </a-select-option>
            </a-select>
          </a-form-item>
        </a-col>
        <a-col :span="24">
          <a-form-item label="沟通内容">
            <a-textarea size="lager" v-decorator="['content', { rules:[{ required: true, message: '请输入沟通内容' }]}]"/>
          </a-form-item>
        </a-col>
      </a-row>
      <a-row v-else-if="!modalMode">
        <a-col>
          <a-form-item label="闭环方式">
            <a-select v-decorator="['endMode', { rules:[{ required: true, message: '请选择闭环方式' }]}]">
              <a-select-option v-for="d in alarmEndModeOptions" :key="d.dictKey">
                {{ d.dictValue }}
              </a-select-option>
            </a-select>
          </a-form-item>
        </a-col>
        <a-col>
          <a-form-item label="报警原因">
            <a-select v-decorator="['alarmReason', { rules:[{ required: true, message: '请选择报警原因' }]}]">
              <a-select-option v-for="d in alarmEndReasonOptions" :key="d.dictKey">
                {{ d.dictValue }}
              </a-select-option>
            </a-select>
          </a-form-item>
        </a-col>
        <a-col>
          <a-form-item label="报警详情">
            <a-textarea v-decorator="['remark', { rules:[{ required: true, message: '请输入报警详情' }]}]"/>
          </a-form-item>
        </a-col>
      </a-row>
    </a-form>
  </a-modal>
</template>

<script>
// import pick from 'lodash.pick'
import { alarmSelectOptions } from '@/api/safety'

export default {
  name: 'DetailModal',
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    loading: {
      type: Boolean,
      default: () => false
    },
    modalTitle: {
      type: String,
      default: '操作'
    },
    disabled: {
      type: Boolean,
      default: false
    },
    modalMode: {
      type: Boolean,
      default: true
    },
    title: {
      type: String,
      default: '沟通记录'
    }
  },
  data () {
    return {
      form: this.$form.createForm(this),
      alarmEndReasonOptions: [],
      alarmEndModeOptions: [],
      alarmCommunicationOptions: []
    }
  },
  created () {
    this.selectOptionsInit()
    // 防止表单未注册
    // fields.forEach(v => this.form.getFieldDecorator(v))
    // 当 model 发生改变时，为表单设置值
    // this.$watch('model', () => {
    //   console.log(fields)
    //   this.model && this.form.setFieldsValue(pick(this.model, fields))
    // })
  },
  methods: {
    selectOptionsInit () {
      alarmSelectOptions.getAlarmEndMode().then(res => {
        this.alarmEndModeOptions = res.data
      })
      alarmSelectOptions.getAlarmEndReason().then(res => {
        this.alarmEndReasonOptions = res.data
      })
      alarmSelectOptions.getAlarmCommunicationMode().then(res => {
        this.alarmCommunicationOptions = res.data
      })
    }
  }
}
</script>
