<template>
  <a-modal
    title="劳保物资信息"
    :bodyStyle="{maxHeight:'600px',overflow:'auto',paddingTop: '5px'}"
    width="60%"
    :visible="visible"
    @cancel="() => { $emit('update:visible', false) }"
    @ok="() => { $emit('ok', { values: selectedRows}) }"
    :keyboard="false"
    :maskClosable="false"
  >
    <div class="table-page-search-wrapper" style="margin: 10px 0 20px">
      <a-form layout="inline">
        <a-row :gutter="24">
          <a-col :md="6">
            <a-form-item label="物资名称">
              <a-input v-model="queryParam.name"/>
            </a-form-item>
          </a-col>
          <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="search">查询</a-button>
                <a-button type="second" @click="searchReset" style="margin-left: 10px">重置</a-button>
              </span>
          </a-col>
        </a-row>
      </a-form>
    </div>
    <div>
      <s-table
        ref="table"
        size="default"
        :filterMultiple="false"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        showPagination="true"
        :rowSelection="rowSelection"
      >
        <span slot="number" slot-scope="text, record, index">
          {{ pagination.pageSize * (pagination.current - 1) + index + 1 }}
        </span>
      </s-table>
    </div>
  </a-modal>
</template>

<script>
import { STable } from '@/components'
import HealthApi from '@/api/health'

export default {
  name: 'MaterialSelect',
  components: {
    STable
  },
  data () {
    return {
      pagination: {
        current: 1,
        pageSize: 10,
        showSizeChanger: true,
      },
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        return this.getList(HealthApi.materialInfoList, requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.pagination = {
            current: data.pageNo,
            pageSize: data.pageSize,
            showSizeChanger: true,
          }
          return data
        })
      },
      columns:[
        {
          title: '序号',
          dataIndex: 'number',
          width: '60px',
          align: 'center',
          scopedSlots: { customRender: 'number' }
        },
        {
          title: '代码',
          dataIndex: 'number',
          width: '200px',
          align: 'center',
          ellipsis: true
        },
        {
          title: '物资名称',
          dataIndex: 'name',
          width: '180px',
          align: 'center',
          ellipsis: true
        },
        {
          title: '计量单位',
          dataIndex: 'measurementUnit',
          width: '180px',
          align: 'center',
          ellipsis: true
        },
        {
          title: '备注',
          dataIndex: 'remark',
          // width: '120px',
          align: 'center',
          ellipsis: true
        }
      ],
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: []
    }
  },
  props: {
    visible: {
      type: Boolean,
      default: false
    }
  },
  watch: {
    visible: {
      handler: function (value) {
        if(value) {
          this.queryParam = {}
          this.$refs.table.refresh(true)
          this.$refs.table.clearSelected()
        }
      }
    }
  },
  computed: {
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    search () {
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    searchReset () {
      this.initQueryParam()
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
  }
}
</script>

