  created() {
    this.getDict('HEALTH_SEX').then(({ data, success }) => {
      if (success) {
        this.sexOptions = data
        this.basicRules[2].options = data.map(item => {
          return {
            value: item.dictKey,
            label: item.dictValue
          }
        })
      }
    })
    this.getDict('HEALTH_CULTURE').then(({ data, success }) => {
      if (success) {
        this.educationLevelOptions = data
        this.basicRules[5].options = data.map(item => {
          return {
            value: item.dictKey,
            label: item.dictValue
          }
        })
      }
    })
    this.getDict('HEALTH_STATUS').then(({ data, success }) => {
      if (success) {
        this.workStatusOptions = data
        this.basicRules[10].options = data.map(item => {
          return {
            value: item.dictKey,
            label: item.dictValue
          }
        })
      }
    })
  },
  filters: {
    colorFilter: function(value) {
      const colorList = ['orange', 'green']
      return colorList[value]
    },
    textFilter: function(value) {
      const textList = ['未发布', '已发布']
      return textList[value]
    }
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => healthPersonalProfileColumns,
    occupationHistoryColumns: () => occupationHistoryColumns,
    anamnesisColumns: () => anamnesisColumns,
    diseaseDiagnosisColumns: () => diseaseDiagnosisColumns,
    commonTitle() {
      const titleList = ['职业史及职业病接触史', '既往病史', '职业病诊断']
      return titleList[this.currentModalIndex]
    },
    rules() {
      return this.rulesList[this.currentModalIndex]
    }
  },
  watch: {
    basicRules: {
      handler: function(val) {
        if (this.fApi.updateRule) {
          this.fApi.updateRule(val)
        }
      },
      deep: true
    }
  },
  methods: {
    search() {
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    searchReset() {
      this.initQueryParam()
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    initQueryParam() {
      this.queryParam = {}
    },
    addRecord() {
      this.basicInfo = {
        name: null,
        fileNumber: null,
        sex: undefined,
        marriage: null,
        nativePlace: null,
        cultureStandard: null,
        joinWorkTime: null,
        hobby: null,
        entryWorkTime: null,
        idNumber: null,
        status: undefined
      }
      this.anamnesis = []
      this.diseaseDiagnosis = []
      this.occupationHistory = []
      this.modalTitle = '新增数据'
      this.profileModalDisabled = false
      this.profileModalVisible = true
    },
    editRecord(record) {
      this.getById(HealthApi.personalProfile + `?id=${record.id}`).then(({ data, success }) => {
        if (success) {
          this.anamnesis = data.anamnesisList
          this.diseaseDiagnosis = data.diseaseDiagnosisList
          this.occupationHistory = data.occupationalHistoryList
          delete data.anamnesisList
          delete data.diseaseDiagnosisList
          delete data.occupationalHistoryList
          this.basicInfo = data
          data.sex = data.sex + ''
          data.cultureStandard = data.cultureStandard + ''
          data.status = data.status + ''
          this.modalTitle = '编辑数据'
          this.profileModalDisabled = false
          this.profileModalVisible = true
        }
      })
    },
    viewRecord(record) {
      this.getById(HealthApi.personalProfile + `?id=${record.id}`).then(({ data, success }) => {
        if (success) {
          this.anamnesis = data.anamnesisList
          this.diseaseDiagnosis = data.diseaseDiagnosisList
          this.occupationHistory = data.occupationalHistoryList
          delete data.anamnesisList
          delete data.diseaseDiagnosisList
          delete data.occupationalHistoryList
          this.basicInfo = data
          data.sex = data.sex + ''
          data.cultureStandard = data.cultureStandard + ''
          data.status = data.status + ''
          this.modalTitle = '查看数据'
          this.profileModalDisabled = true
          this.profileModalVisible = true
        }
      })
    },
    submitProfile() {
      this.fApi.submit((formData, fApi) => {
        const params = {
          ...this.basicInfo,
          ...formData,
          anamnesisList: this.anamnesis,
          diseaseDiagnosisList: this.diseaseDiagnosis,
          occupationalHistoryList: this.occupationHistory
        }
        let method
        if (this.isNotNullOrUndefined(params.id)) {
          method = 'put'
          formData.id = this.commonData.id
        } else {
          method = 'post'
        }
        this.submitForm(HealthApi.personalProfile, params, method).then(({ data, success }) => {
          if (success) {
            this.$message.success('操作成功')
            this.profileModalVisible = false
            this.initQueryParam()
            this.$refs.table.refresh()
            this.$refs.table.clearSelected()
            // this.basicInfo = {}
            // this.anamnesis = []
            // this.diseaseDiagnosis = []
            // this.occupationHistory = []
          }
        })
      })
    },
    initCommonData(index) {
      const initDataList = [
        {
          time: null,
          workUnitId: undefined,
          workType: undefined,
          examiningOrganization: null,
          examiningTime: null,
          hazardFactors: null,
          safeguardProcedures: null
        },
        {
          diseasesName: null,
          diagnosisTime: null,
          diagnosisHospital: null,
          therapeuticOutcome: null,
          remark: null
        },
        {
          diseaseName: null,
          diagnosisTime: null,
          diagnosisHospital: null,
          diagnosisLevel: null,
          remark: null
        }
      ]
      this.commonData = initDataList[index]
    },
    addInfo(index) {
      this.currentModalIndex = index
      this.initCommonData(index)
      // this.commonData = {}
      this.commonDisabled = false
      this.commonVisible = true
    },
    editInfo(record, index, recordIndex) {
      // this.initCommonData(index)
      this.key = record.id
      this.currentModalIndex = index
      this.currentRecordIndex = recordIndex
      this.commonData = record
      if (index === 0) {
        this.commonData.time = [record.startTime, record.endTime]
      }
      this.commonDisabled = false
      this.commonVisible = true
    },
    viewInfo(record, index) {
      // this.initCommonData(index)
      this.key = record.id
      this.currentModalIndex = index
      this.commonData = record
      if (index === 0) {
        this.commonData.time = [record.startTime, record.endTime]
      }
      this.commonDisabled = true
      this.commonVisible = true
    },
    removeInfo(record, index, recordIndex) {
      this.currentModalIndex = index
      if (this.currentModalIndex === 0) {
        this.occupationHistory.splice(recordIndex, 1)
      } else if (this.currentModalIndex === 1) {
        this.anamnesis.splice(recordIndex, 1)
      } else {
        this.diseaseDiagnosis.splice(recordIndex, 1)
      }
    },
    modalOk({ formData }) {
      const params = {
        ...this.commonData,
        ...formData
      }
      const recordIndex = this.currentRecordIndex,
        hasId = this.isNotNullOrUndefined(params.id)
      if (this.currentModalIndex === 0) {
        if (hasId) {
          this.occupationHistory.splice(recordIndex, 1, params)
        } else {
          this.occupationHistory.push(params)
        }
      } else if (this.currentModalIndex === 1) {
        if (hasId) {
          this.anamnesis.splice(recordIndex, 1, params)
        } else {
          this.anamnesis.push(params)
        }
      } else {
        if (hasId) {
          this.diseaseDiagnosis.splice(recordIndex, 1, params)
        } else {
          this.diseaseDiagnosis.push(params)
        }
      }
      this.commonVisible = false
    }
  }
}
</script>

