<template>
  <div style="margin-top: 2px">
    <a-row :gutter="12">
      <a-col :span="4">
        <a-card>
          <a-tree
            :tree-data="treeData"
            :replaceFields="{children:'childDirectory', title:'areaName', key:'id', value: 'id' }"
            style="height: calc(100vh - 175px)"
            @select="OnSelect"/>
        </a-card>
      </a-col>
      <a-col :span="20">
        <a-card :bordered="false">
          <div class="table-page-search-wrapper">
            <a-form layout="inline">
              <a-row :gutter="24">
                <a-col :span="6">
                  <a-form-item label="报警级别">
                    <a-select v-model="queryParam.alarmLevel">
                      <a-select-option v-for="d in alarmLevelOptions" :key="d.dictKey">
                        {{ d.dictValue }}
                      </a-select-option>
                    </a-select>
                  </a-form-item>
                </a-col>
                <a-col :span="6">
                  <a-form-item label="状态">
                    <a-select v-model="queryParam.status">
                      <a-select-option v-for="d in alarmStatusOptions" :key="d.dictKey">
                        {{ d.dictValue }}
                      </a-select-option>
                    </a-select>
                  </a-form-item>
                </a-col>
                <a-col :span="6">
                  <a-form-item label="发现时间">
                    <a-range-picker v-model="queryParam.time"/>
                  </a-form-item>
                </a-col>
                <a-col :md="2">
                  <span class="table-page-search-submitButtons">
                    <a-button type="primary" @click="$refs.alarmTable.refresh(true)">查询</a-button>
                    <a-button type="second" style="margin-left: 10px" @click="alarmRefresh">重置</a-button>
                  </span>
                </a-col>
              </a-row>
            </a-form>
          </div>
          <s-table
            ref="alarmTable"
            size="middle"
            :rowKey="record => record.id"
            :columns="columns"
            :scroll="{x:1000,y:`calc(100vh - 360px`}"
            :data="loadData"
            :alert="false"
            :rowSelection="false"
            showPagination="true"
          >
            <!--            <span slot="action" slot-scope="text, record">-->
            <!--              <template>-->
            <!--                <a @click="alarmCheck(record)" disabled="true">查看</a>-->
            <!--              </template>-->
            <!--            </span>-->
          </s-table>
        </a-card>
      </a-col>
    </a-row>
  </div>
</template>
<script>
import { STable } from '@/components'
import { getRegionTree, getRegionMajorTreeChild, foreWarning, alarmSelectOptions } from '@/api/safety'
import { importantForeColumns } from '@/views/safety/table'

export default {
  components: {
    STable
  },
  data () {
    return {
      treeData: [],
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = {
          current: parameter.pageNo,
          size: parameter.pageSize
        }
        if (this.queryParam.time) {
          this.queryParam.startTime = this.queryParam.time[0].format('YYYY-MM-DD hh:mm:ss')
          this.queryParam.endTime = this.queryParam.time[0].format('YYYY-MM-DD hh:mm:ss')
          delete this.queryParam.time
        }
        Object.assign(requestParameters, this.queryParam)
        return foreWarning.getForewarningList(requestParameters).then(res => {
          return {
            totalCount: res.data.total,
            totalPage: res.data.pages,
            pageNo: res.data.current,
            pageSize: res.data.size,
            data: res.data.records
          }
        })
      },
      // select options
      alarmLevelOptions: [],
      alarmStatusOptions: []
    }
  },
  created () {
    this.selectOptionsInit()
    this.treeOptionsInit()
  },
  computed: {
    columns: () => importantForeColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    treeOptionsInit () {
      getRegionTree({ isMajor: 1 }).then(res => {
        this.treeData = res.data
        // const result = res.data
        // this.treeData = result.map(item => {
        //   return {
        //     id: item.id,
        //     title: item.areaName,
        //     key: item.id,
        //     hasChildren: item.hasChild,
        //     value: item.id
        //   }
        // })
        // console.log(this.treeData)
      })
    },
    selectOptionsInit () {
      alarmSelectOptions.getAlarmStatus().then(res => {
        this.alarmStatusOptions = res.data
      })
      alarmSelectOptions.getAlarmLevel().then(res => {
        this.alarmLevelOptions = res.data
      })
    },
    onLoadData (treeNode) {
      return new Promise(resolve => {
        getRegionMajorTreeChild({ pid: treeNode.value }).then(res => {
          if (treeNode.dataRef.children) {
            resolve()
            return
          }
          const children = res.data.map(item => {
            return ({
              id: item.id,
              title: item.areaName,
              key: item.id,
              hasChildren: item.hasChild,
              value: item.id,
              isLeaf: !item.hasChild
            })
          })
          treeNode.dataRef.children = children
          this.treeData = [...this.treeData]
        })
        resolve()
      })
    },
    OnSelect (selectedKeys, info) {
      console.log(selectedKeys[0])
      this.queryParam.blurry = selectedKeys[0]
      this.$refs.table.refresh(true)
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    alarmRefresh () {
      this.queryParam = {}
      this.$refs.alarmTable.refresh(true)
    },
    alarmCheck (record) {
      this.$message.success('查看')
    }
  }
}
</script>
<style lang="less" scoped>
/deep/ .ant-table {
  height: calc(100vh - 288px)
}
/deep/ .ant-table-wrapper{
  min-height: calc(100vh - 232px);
}
</style>
