<template>
  <a-modal
    :title="modalTitle"
    :width="700"
    :visible="visible"
    :confirmLoading="loading"
    @ok="() => { $emit('ok') }"
    @cancel="() => { $emit('cancel') }"
    ok-text="提交"
    cancel-text="取消"
    :keyboard="false"
    :maskClosable="false"
  >
    <a-spin :spinning="loading">
      <a-form :form="form" :label-col="{ span: 7 }" :wrapper-col="{ span: 12 }">
        <a-form-item label="文件名">
          <a-input v-decorator="['name',{ rules:[{ required: true, message: '请填写项目名称' }]}]" :disabled="disabled"/>
        </a-form-item>
        <a-form-item label="文号">
          <a-input v-decorator="['code',{ rules:[{ required: true, message: '请填写文号' }]}]" :disabled="disabled"/>
        </a-form-item>
        <a-form-item label="发文单位">
          <a-input v-decorator="['sendingUnit',{ rules:[{ required: true, message: '请填写发文单位' }]}]" :disabled="disabled"/>
        </a-form-item>
        <a-form-item label="文件有效性">
          <a-select v-decorator="['isEffective',{ rules:[{ required: true, message: '请选择文件有效性' }]}]" :disabled="disabled">
            <a-select-option v-for="val in fileEffective" :key="val.dictKey">
              {{ val.dictValue }}
            </a-select-option>
          </a-select>
        </a-form-item>
        <a-form-item label="文件类别">
          <a-select v-decorator="['type',{ rules:[{ required: true, message: '请选择文件类别' }]}]" :disabled="disabled">
            <a-select-option v-for="val in fileType" :key="val.dictKey">
              {{ val.dictValue }}
            </a-select-option>
          </a-select>
        </a-form-item>
        <a-form-item label="颁布时间">
          <a-date-picker
            v-decorator="['startTime',{ rules:[{ required: true, message: '请选择颁布日期' }]}]"
            :disabled="disabled"
            :valueFormat="dateFormat"
            :show-time="timeSelect"
            style="width: 100%"/>
        </a-form-item>
        <a-form-item label="实施时间">
          <a-date-picker
            v-decorator="['excuteTime',{ rules:[{ required: true, message: '请选择实施日期' }]}]"
            :disabled="disabled"
            :valueFormat="dateFormat"
            :show-time="timeSelect"
            style="width: 100%"/>
        </a-form-item>
        <a-form-item label="创建时间">
          <a-date-picker
            v-decorator="['createTime',{ rules:[{ required: true, message: '请选择创建日期' }]}]"
            :disabled="disabled"
            :valueFormat="dateFormat"
            :show-time="timeSelect"
            style="width: 100%"/>
        </a-form-item>
        <person-select label-name="创建人" decorator-name="author" required/>
        <file-upload doc-name="docName" doc-url="docUrl" :form="form" :visible="visible"/>
      </a-form>
    </a-spin>
  </a-modal>
</template>

<script>
import pick from 'lodash.pick'
import FileUpload from '@/views/safety/components/FileUpload'
import PersonSelect from '@/views/safety/components/PersonSelect'

const fields = ['userName', 'createTime', 'excuteTime', 'startTime', 'type', 'isEffective', 'name', 'code', 'sendingUnit'] // 字段

export default {
  name: 'LawModal',
  components: {
    PersonSelect,
    FileUpload
  },
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    loading: {
      type: Boolean,
      default: () => false
    },
    model: {
      type: Object,
      default: () => null
    },
    modalTitle: {
      type: String,
      default: '操作'
    },
    disabled: {
      type: Boolean,
      default: false
    },
    fileType: {
      type: Object,
      default: () => null
    },
    fileEffective: {
      type: Object,
      default: () => null
    }
  },
  data () {
    return {
      form: this.$form.createForm(this),
      dateFormat: 'YYYY-MM-DD HH:mm:ss',
      timeSelect: true
    }
  },
  created () {
    // 防止表单未注册
    fields.forEach(v => this.form.getFieldDecorator(v))
    // 当 model 发生改变时，为表单设置值
    this.$watch('model', () => {
      if (Object.keys(this.model).length > 0) {
        this.model.isEffective = typeof this.model.isEffective === 'string' ? this.model.isEffective : this.model.isEffective.toString()
        this.model.type = typeof this.model.type === 'string' ? this.model.type : this.model.type.toString()
      }
      this.model && this.form.setFieldsValue(pick(this.model, fields))
    })
  },
  methods: {
    onChange (e) {
      this.form.sex = e.target.value
    }
  }
}
</script>
