<template>
  <page-container :title="false">
    <a-card :bordered="false" style="margin: 15px;" :body-style="{'padding-bottom': '0px'}">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="8" :sm="12" :xs="24">
              <a-form-item label="会议名称">
                <a-input v-model="queryParam.meetingsName" placeholder="会议名称" />
              </a-form-item>
            </a-col>
            <a-col :md="8" :sm="12" :xs="24">
              <a-form-item label="会议时间">
                <a-range-picker
                  style="width: 100%; min-width: 110px;"
                  showTime
                  valueFormat="YYYY-MM-DD HH:mm:ss"
                  :placeholder="['开始时间', '结束时间']"
                  v-model="queryParam.validateDateRange"
                />
              </a-form-item>
            </a-col>
            <a-col :md="8" :sm="24" :xs="24" class="table-page-search-submitButtons">
              <a-space>
                <a-button type="primary" @click="$refs.table.refresh(true)">查询</a-button>
                <a-button type="second" @click="searchReset">重置</a-button>
              </a-space>
            </a-col>
          </a-row>
        </a-form>
      </div>
    </a-card>
    <a-card
      :bordered="false"
      style="margin: 15px;"
      :headStyle="{border: 'none', paddingBottom: 0}"
      :bodyStyle="{paddingBottom: 0}"
      class="table-card"
    >
      <template #title>
        <div class="text-right">
          <a-button type="primary" icon="plus" @click="addRecord" v-if="permission.meeting_add">新建</a-button>
        </div>
      </template>
      <s-table
        ref="table"
        size="default"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        showPagination="true"
        :scroll="{y: `calc(100vh - 432px)`, x: 850}"
        :pagination="pagination"
      >
        <span
          slot="serial"
          slot-scope="text, record, index"
        >{{ pagination.pageSize * (pagination.current - 1) + index + 1 }}</span>
        <span slot="action" slot-scope="text, record">
          <template>
            <a
              @click="publishRecord(record, 1)"
              v-if="record.status !== 1 && userInfo.id === record.gmtCreateId"
            >发布</a>
            <a
              @click="publishRecord(record, 0)"
              v-if="record.status === 1 && userInfo.id === record.gmtCreateId && false"
            >撤回</a>
            <a-divider
              type="vertical"
              v-if="record.status === 1 && userInfo.id === record.gmtCreateId && false"
            />
            <a @click="checkRecord(record)" v-if=" record.status === 1">查看</a>
            <a-divider
              type="vertical"
              v-if="record.status !== 1 && userInfo.id === record.gmtCreateId"
            />
            <a
              @click="editRecord(record)"
              v-if=" record.status !== 1 && userInfo.id === record.gmtCreateId"
            >编辑</a>
            <a-divider
              type="vertical"
              v-if="record.status !== 1 && userInfo.id === record.gmtCreateId"
            />
            <a
              @click="removeRecord(record)"
              v-if=" record.status !== 1 && userInfo.id === record.gmtCreateId"
            >删除</a>
          </template>
        </span>
      </s-table>
      <common-form-modal
        ref="modal"
        :visible.sync="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        :rule="rules"
        width="700px"
        @ok="modalOk"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { meetingColumns } from '@/views/safety/table'
import ApiURLs from '@/api/urls'
import { mapGetters } from 'vuex'
import { getStore } from '@/utils/store'
import CommonFormModal from '@/components/CommonFormModal/CommonFormModal'

export default {
  name: 'Meeting',
  components: {
    STable,
    CommonFormModal
  },
  data() {
    return {
      pagination: {
        pageSize: 10,
        current: 1,
        showSizeChanger: true
      },
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonDisabled: false,
      queryParam: {
        validateDateRange: undefined,
        meetingsName: ''
      },
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)

        if (this.queryParam.validateDateRange) {
          Object.assign(requestParameters, {
            meetingsBgTime: this.queryParam.validateDateRange[0],
            meetingsEndTime: this.queryParam.validateDateRange[1]
          })
        }
        delete requestParameters.validateDateRange
        return this.getList(ApiURLs.meetingList, requestParameters).then(res => {
          this.pagination = { ...this.pagination, pageSize: res.data.size, current: res.data.current }
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          return data
        })
      },
      userInfo: getStore({ name: 'info' }),
      rules: [
        {
          type: 'a-row',
          native: true,
          children: [
            {
              type: 'a-col',
              props: {
                span: 24
              },
              children: [
                {
                  type: 'input',
                  title: '会议名称',
                  field: 'meetingsName',
                  props: {
                    type: 'text',
                    placeholder: '请输入会议名称',
                    maxLength: 32
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入会议名称',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 3 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'a-col',
                  props: {
                    span: 24
                  },
                  children: [
                    {
                      type: 'DatePicker',
                      title: '会议时间',
                      field: 'meetingsTime',
                      props: {
                        showTime: true,
                        format: 'YYYY-MM-DD HH:mm:ss',
                        placeholder: '会议时间'
                      },
                      col: {
                        span: 24
                      },
                      style: {
                        width: '100%'
                      },
                      wrap: {
                        labelCol: { span: 3 },
                        wrapperCol: { span: 18 }
                      },
                      validate: [
                        {
                          required: true,
                          message: '请选择会议时间',
                          trigger: 'blur'
                        },
                        {
                          validator: (rule, value, callback) => {
                            if (new Date(value).getTime() < new Date().getTime()) {
                              callback(new Error('会议时间不能早于当前时间'))
                            } else {
                              callback()
                            }
                          },
                          trigger: 'changed'
                        }
                      ]
                    },
                    {
                      type: 'PersonListSelect',
                      title: '主持人',
                      field: 'meetingsHosterId',
                      props: {
                        placeholder: '请选择主持人'
                      },
                      col: {
                        span: 24
                      },
                      wrap: {
                        labelCol: { span: 3 },
                        wrapperCol: { span: 18 }
                      },
                      on: {
                        change: ({ person }) => {
                          this.commonData.meetingsHosterName = person.realName
                        }
                      },
                      validate: [
                        {
                          required: true,
                          message: '请选择主持人',
                          trigger: 'blur'
                        }
                      ]
                    }
                  ]
                },
                {
                  type: 'input',
                  title: '会议地点',
                  field: 'meetingsAddress',
                  props: {
                    type: 'text',
                    placeholder: '请输入会议地点',
                    maxLength: 32
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入会议地点',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 3 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'PersonListSelect',
                  title: '参会人员',
                  field: 'meetingsParticipantsIds',
                  props: {
                    placeholder: '请选择参会人员',
                    multiple: 'multiple'
                  },
                  wrap: {
                    labelCol: { span: 3 },
                    wrapperCol: { span: 18 }
                  },
                  on: {
                    change: ({ person }) => {
                      this.commonData.meetingsParticipantsIds = person.map(item => item.id)
                      this.commonData.meetingsParticipantsNames = person.map(item => item.realName)
                    }
                  },
                  validate: [
                    {
                      required: true,
                      message: '请选择参会人员',
                      trigger: 'blur'
                    },
                    {
                      validator: (rule, value, callback) => {
                        if (!value || value.length < 1) {
                          callback(new Error('请选择参会人员'))
                        } else {
                          callback()
                        }
                      },
                      trigger: 'changed'
                    }
                  ]
                },
                {
                  type: 'input',
                  title: '会议内容',
                  field: 'meetingsDetails',
                  props: {
                    type: 'textarea',
                    autoSize: { minRows: 3, maxRows: 5 },
                    placeholder: '请输入会议内容',
                    maxLength: 256
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入会议内容',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 3 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'UploadFile',
                  field: 'files',
                  title: '附件',
                  props: {
                    fileList: []
                  },
                  wrap: {
                    labelCol: { span: 3 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'select',
                  title: '公布范围',
                  field: 'announcedRange',
                  props: {
                    placeholder: 'commonDisabled'
                  },
                  value: 1,
                  options: [
                    { value: 0, label: '全公司' },
                    { value: 1, label: '本单位' }
                  ],
                  wrap: {
                    labelCol: { span: 3 },
                    wrapperCol: { span: 18 }
                  }
                }
              ]
            }
          ]
        }
      ]
    }
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => meetingColumns,
    hasSelected() {
      return this.selectedRowKeys.length > 0
    },
    rowSelection() {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    initFormData() {
      this.commonData = {
        meetingsName: null,
        meetingsTime: null,
        meetingsHosterId: undefined,
        meetingsAddress: null,
        meetingsDetails: null,
        announcedRange: 1,
        meetingsHosterName: null,
        files: [],
        meetingsParticipantsNames: null,
        meetingsParticipantsIds: undefined
      }
      this.setFileList([])
    },
    setFileList(fileList) {
      if (this.$refs.modal && this.$refs.modal.fApi.getRule) {
        this.$refs.modal.fApi.getRule('files').props.fileList = fileList
      } else {
        this.rules[0].children[0].children[6].props.fileList = fileList
      }
    },
    commonInit() {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.initFormData()
    },
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    searchReset() {
      this.queryParam = {
        validateDateRange: undefined,
        meetingsName: ''
      }
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord() {
      this.commonTitle = '新增数据'
      this.initFormData()
      this.commonVisible = true
      this.commonDisabled = false
    },
    removeRecords() {
      const ids = this.selectedRows.map(item => item.id)
      this.showDeleteConfirm(ids)
    },
    getRecord(id, callback) {
      this.getById(ApiURLs.meeting, id).then(({ data, success }) => {
        if (success) {
          data.meetingsParticipantsIds = data.meetingsParticipantsIds.split(',')
          data.meetingsParticipantsNames = data.meetingsParticipantsNames.split(',')
          this.commonData = data
          const files = data.files.map(item => ({
            uid: item.id,
            name: item.fileName,
            status: 'done',
            id: item.id,
            filePath: item.fileUrlPath
          }))
          this.setFileList(files)
          callback()
        }
      })
    },
    checkRecord(record) {
      this.commonTitle = '查看数据'
      this.getRecord(record.id, () => {
        this.commonDisabled = true
        this.commonVisible = true
      })
    },
    editRecord(record) {
      this.commonTitle = '编辑数据'
      this.getRecord(record.id, () => {
        this.commonDisabled = false
        this.commonVisible = true
      })
    },
    removeRecord(record) {
      this.showDeleteConfirm([record.id])
    },
    modalCancel() {
      this.commonInit()
    },
    modalOk({ formData }) {
      let method = ''
      if (this.isNotNullOrUndefined(this.commonData.id)) {
        method = 'put'
        formData.id = this.commonData.id
        formData.status = this.commonData.status
      } else {
        method = 'post'
        formData.status = 0
      }
      const params = {
        ...formData,
        meetingsParticipantsIds: this.commonData.meetingsParticipantsIds.join(','),
        meetingsParticipantsNames: this.commonData.meetingsParticipantsNames.join(','),
        meetingsHosterName: this.commonData.meetingsHosterName,
        meetingsTypeName: this.commonData.meetingsTypeName,
        files: this.$refs.modal.fApi.getRule('files').props.fileList.map(item => ({
          id: item.id,
          fileUrlPath: item.filePath,
          originalFileName: item.name
        }))
      }
      this.submitForm(ApiURLs.meeting, params, method, {
        // headers: { 'Content-Type': 'multipart/form-data' }
      }).then(res => {
        this.$refs.table.refresh()
        this.$refs.table.clearSelected()
        this.commonInit()
      })
    },
    publishRecord(record, status) {
      const url = `${ApiURLs.meetingStatusUpdate}?id=${record.id}&status=${status}`
      this.submitForm(url, null, 'put').then(res => {
        this.$message.success(`操作成功`)
        this.$refs.table.refresh()
        this.$refs.table.clearSelected()
      })
    },
    showDeleteConfirm(params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk() {
          _this.deleteByIds(ApiURLs.meeting, params).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    }
  },
  created() {}
}
</script>
<style lang="less" scoped>
/deep/ .ant-table {
  height: auto;
}

/deep/ .table-card .ant-card-body {
  padding-top: 0;
  padding-bottom: 15px;
}

.card-title {
  display: flex;
  justify-content: space-between;
}
</style>
