<template>
  <a-modal
    :title="modalTitle"
    :visible="visible"
    :confirm-loading="loading"
    @ok="() => { $emit('ok') }"
    @cancel="() => { $emit('cancel') }"
    ok-text="确定"
    cancel-text="取消"
    width="1000px"
    :keyboard="false"
    :maskClosable="false"
  >
    <a-spin :spinning="loading">
      <a-form :form="form" :label-col="{ span: 8 }" :wrapper-col="{ span: 14 }">
        <a-row :gutter="24">
          <a-col :span="12">
            <a-form-item label="危险源名称">
              <a-input v-decorator="['dangsrcName', { rules:[{ required: true, message: '请输入机构名称' }]}]" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="危险源级别" >
              <a-select style="width: 180px" v-decorator="['levelName',{ rules:[{ required: false, message: '请选择级别' }]}]" :disabled="disabled">
                <a-select-option v-for="d in levelOptions" :key="d.value">
                  {{ d.text }}
                </a-select-option>
              </a-select>
            </a-form-item>
            <a-form-item label="安全距离">
              <a-input-number style="min-width: 100%" v-decorator="['safeDistance']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="压力容器总数">
              <a-input-number v-decorator="['pressureVesselCount']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="生产经营活动类型">
              <a-input v-decorator="['productionType']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="R值">
              <a-input v-decorator="['rvalue']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="经度">
              <a-input v-decorator="['longitude']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="备案期限起始时间">
              <a-date-picker show-time v-decorator="['recodeStartTime']" :disabled="disabled" placeholder="" />
            </a-form-item>
            <a-form-item label="易发生事故类型">
              <a-input v-decorator="['accidentType']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="主要产品">
              <a-input v-decorator="['majorProduction']" :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :span="12">
            <a-form-item label="区域">
              <a-input v-decorator="['regionId']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="危险源类型">
              <a-select style="width: 180px" v-decorator="['dangsrcType',{ rules:[{ required: false, message: '请选择危险源类型' }]}]" :disabled="disabled">
                <a-select-option v-for="d in dangerTypeOptions" :key="d.dictKey">
                  {{ d.dictValue }}
                </a-select-option>
              </a-select>
            </a-form-item>
            <a-form-item label="500米内人数">
              <a-input-number v-decorator="['personNum']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="三类压力容器总数">
              <a-input-number v-decorator="['threePressureVesselCount']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="生产存储场所产权">
              <a-input v-decorator="['placeRight']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="详细地址">
              <a-input v-decorator="['detailAddr']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="纬度">
              <a-input v-decorator="['latitude']" :disabled="disabled" placeholder/>
            </a-form-item>
            <a-form-item label="备案期限终止时间">
              <a-date-picker show-time v-decorator="['recodeEndTime']" :disabled="disabled" placeholder=""/>
            </a-form-item>
            <a-form-item label="可能引发事故死亡人数">
              <a-input v-decorator="['deadNum']" :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="备注">
              <a-input v-decorator="['remark']" :disabled="disabled"/>
            </a-form-item>
          </a-col>
        </a-row>
      </a-form>
    </a-spin>
  </a-modal>
</template>

<script>
import pick from 'lodash.pick'
import { dangerColumns } from '@/views/safety/table'
import { getDangerLevel, getDangerType } from '@/api/safety'

const fields = dangerColumns.map(res => {
  return res.dataIndex
})
const levelOptions = [
  {
    text: '一级',
    value: '一级'
  },
  {
    text: '二级',
    value: '二级'
  },
  {
    text: '三级',
    value: '三级'
  },
  {
    text: '四级',
    value: '四级'
  }
]

const typeOptions = [
  {
    text: '贮罐区',
    value: 0
  },
  {
    text: '库区',
    value: 1
  },
  {
    text: '生产场所',
    value: 2
  }
]

export default {
  name: 'DangerModal',
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    loading: {
      type: Boolean,
      default: () => false
    },
    model: {
      type: Object,
      default: () => null
    },
    modalTitle: {
      type: String,
      default: '操作'
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data () {
    return {
      form: this.$form.createForm(this),
      dangerTypeOptions: [],
      dangerLevelOptions: []
    }
  },
  created () {
    // 防止表单未注册
    fields.forEach(v => this.form.getFieldDecorator(v))
    // 当 model 发生改变时，为表单设置值
    this.$watch('model', () => {
      console.log(fields)
      this.model && this.form.setFieldsValue(pick(this.model, fields))
    })
    this.selectOptionsInit()
  },
  methods: {
    selectOptionsInit () {
      getDangerLevel().then(res => {
        this.dangerLevelOptions = res.data
      })
      getDangerType().then(res => {
        this.dangerTypeOptions = res.data
      })
    }
  }
}
</script>
