// 线上考试列表
import Vue from 'vue'
import moment from 'moment'
const h = new Vue().$createElement
export const OnlineExamColumns = [
  {
    title: '考试名称',
    dataIndex: 'examName',
    align: 'center',
    width: '250px',
    ellipsis: true
  },
  {
    title: '考试类型',
    dataIndex: 'examType',
    width: '120px',
    align: 'center',
    ellipsis: true,
    scopedSlots: { customRender: 'examType' }
  },
  {
    title: '考试时间',
    dataIndex: 'typeName',
    align: 'center',
    width: '300px',
    ellipsis: true,
    customRender: (text, record) => {
      const time = [record.examStartTime, record.examEndTime].filter(item => !!(item)).join('至')
      return time || '-'
    }
  },
  {
    title: '单位',
    dataIndex: 'deptName',
    align: 'center',
    width: '120px',
    ellipsis: true
  },
  {
    title: '创建人',
    dataIndex: 'createName',
    width: '120px',
    ellipsis: true,
    align: 'center'
  },
  {
    title: '创建时间',
    dataIndex: 'createTime',
    width: '150px',
    ellipsis: true,
    align: 'center',
    customRender: (text, record) => {
      return moment(record.createTime).format('YYYY-MM-DD')
    }
  },
  {
    title: '状态',
    dataIndex: 'status',
    width: '100px',
    align: 'center',
    customRender: (text, record) => {
      if (!record.status) {
        return <a-tag color="green">已发布</a-tag>
      }
      return <a-tag color="orange">未发布</a-tag>
    }
  },
  {
    title: '操作',
    dataIndex: 'action',
    width: '200px',
    align: 'center',
    // fixed: 'right',
    scopedSlots: { customRender: 'action' }
  }
]

