// 个人考试计划查询列表
export const PersonalExamPlan = [
  {
    title: '考试名称',
    dataIndex: 'examName',
    ellipsis: true,
    align: 'center',
    width: '150px'
  },
  {
    title: '考试类型',
    dataIndex: 'examType',
    ellipsis: true,
    align: 'center',
    width: '150px',
    scopedSlots: { customRender: 'examType' }
  },
  {
    title: '考试时间',
    align: 'center',
    width: '300px',
    ellipsis: true,
    customRender: (text, record) => {
      const times = [record.examStartDate, record.examEndDate]
      return times.filter(times => times).join('-')
    }
  },
  {
    title: '考试成绩',
    dataIndex: 'score',
    ellipsis: true,
    align: 'center',
    width: '100px',
    customRender: (text, record) => {
      if (record.examResult != 0) {
        return text
      }
      return ''
    }
  },
  {
    title: '考试结果',
    dataIndex: 'examResult',
    ellipsis: true,
    align: 'center',
    width: '100px',
    scopedSlots: { customRender: 'examResult' }
  },
  {
    title: '查看',
    dataIndex: 'view',
    align: 'center',
    width: '100px',
    scopedSlots: { customRender: 'viewRecord' }
  },
  {
    title: '状态',
    dataIndex: 'examStatus',
    ellipsis: true,
    align: 'center',
    width: '100px',
    customRender: (text, record) => {
      if (record.examStatus === '0') {
        return <a-tag color="orange">未开始</a-tag>
      } else if (record.examStatus === '1') {
        return <a-tag color="green">已开始</a-tag>
      } else if (record.examStatus === '2') {
        return <a-tag color="blue">已考试</a-tag>
      } else if (record.examStatus === '3') {
        return <a-tag color="red">已过期</a-tag>
      }
      return ''
    }
  },
  {
    title: '操作',
    dataIndex: 'action',
    width: '200px',
    align: 'center',
    // fixed: 'right',
    scopedSlots: { customRender: 'action' }
  }
]
