<template>
  <a-modal
    :title="modalTitle"
    :width="1000"
    :visible="visible"
    :confirmLoading="loading"
    @ok="() => { $emit('ok') }"
    @cancel="() => { $emit('cancel') }"
    ok-text="确定"
    cancel-text="取消"
    :keyboard="false"
    :maskClosable="false"
  >
    <a-spin :spinning="loading">
      <a-form :form="form" :label-col="{ span: 6 }" :wrapper-col="{ span: 15 }">
        <a-row :gutter="24">
          <a-col :span="12">
            <a-form-item label="培训名称">
              <a-input
                v-decorator="['trainingName',{ rules:[{ required: true, message: '请输入项目名称' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="培训人数">
              <a-input
                v-decorator="['trainingNumber',{ rules:[{ required: false, message: '请输入培训人数' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="培训地点">
              <a-input
                v-decorator="['trainingAddress',{ rules:[{ required: true, message: ' 请输入培训地点' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="培训形式">
              <a-input
                v-decorator="['trainingType',{ rules:[{ required: true, message: '请输入培训形式' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="培训时间" style="margin-bottom:0;">
              <a-form-item
                :style="{ display: 'inline-block', width: 'calc(50% - 12px)' }"
              >
                <a-date-picker
                  style="width: 100%"
                  v-decorator="['startTime',{ rules:[{ required: false, message: '请选择开始时间' }]}]"
                  :disabled="disabled"
                  :valueFormat="dateFormat"/>
              </a-form-item>
              <span :style="{ display: 'inline-block', width: '24px', textAlign: 'center' }">至</span>
              <a-form-item :style="{ display: 'inline-block', width: 'calc(50% - 12px)' }">
                <a-date-picker
                  style="width: 100%"
                  v-decorator="['endTime',{ rules:[{ required: false, message: '请选择结束时间' }]}]"
                  :disabled="disabled"
                  :valueFormat="dateFormat"/>
              </a-form-item>
            </a-form-item>
            <a-form-item label="培训学时">
              <a-input
                v-decorator="['trainingPeriod',{ rules:[{ required: true, message: '请输入培训学时' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="培训内容">
              <a-textarea
                :rows="3"
                v-decorator="['trainingContent',{ rules:[{ required: false, message: '请输入培训内容' }]}]"
                :disabled="disabled"/>
            </a-form-item>
          </a-col>
          <a-col :span="12">
            <a-form-item label="培训主管单位">
              <a-input
                v-decorator="['competentUnitId',{ rules:[{ required: true, message: '请输入培训主管单位' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="培训对象">
              <a-input
                v-decorator="['trainingObject',{ rules:[{ required: false, message: '请输入培训对象' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="责任单位">
              <a-input
                v-decorator="['dutyUnitId',{ rules:[{ required: false, message: '请输入责任单位' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="创建人">
              <a-input
                v-decorator="['creatorId',{ rules:[{ required: false, message: '请输入创建人' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="主讲人">
              <a-input
                v-decorator="['keynoteSpeaker',{ rules:[{ required: true, message: '请输入主讲人' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="记录整理人">
              <a-input
                v-decorator="['recordArranger',{ rules:[{ required: false, message: '请输入输入记录管理人' }]}]"
                :disabled="disabled"/>
            </a-form-item>
            <a-form-item label="外部培训对象">
              <a-input
                v-decorator="['outTrainObject',{ rules:[{ required: false, message: '请输入外部培训对象' }]}]"
                :disabled="disabled"/>
            </a-form-item>
          </a-col>
        </a-row>
      </a-form>
    </a-spin>
  </a-modal>
</template>

<script>
import pick from 'lodash.pick'

const fields = ['outTrainObject', 'recordArranger', 'keynoteSpeaker', 'creatorId', 'dutyUnitId', 'competentUnitId', 'trainingObject', 'trainingContent', 'trainingPeriod', 'startTime', 'trainingType', 'trainingAddress', 'trainingNumber', 'trainingName'] // 字段

export default {
  name: 'ExpertsModal',
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    loading: {
      type: Boolean,
      default: () => false
    },
    model: {
      type: Object,
      default: () => null
    },
    modalTitle: {
      type: String,
      default: '操作'
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data () {
    return {
      dateFormat: 'YYYY-MM-DD HH:mm:ss',
      form: this.$form.createForm(this)
    }
  },
  created () {
    // 防止表单未注册
    fields.forEach(v => this.form.getFieldDecorator(v))
    // 当 model 发生改变时，为表单设置值
    this.$watch('model', () => {
      this.model && this.form.setFieldsValue(pick(this.model, fields))
    })
  },
  methods: {
    onChange (e) {
      this.form.sex = e.target.value
    }
  }
}
</script>
