<template>
  <page-container :title="false">
    <a-card :bordered="false">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="4">
              <a-form-item label="关键字">
                <a-input v-model="queryParam.blurry"/>
              </a-form-item>
            </a-col>
            <a-col :md="4">
              <a-form-item label="培训主管单位">
                <a-input v-model="queryParam.componentUnitId"/>
              </a-form-item>
            </a-col>
            <a-col :md="4">
              <a-form-item label="责任单位">
                <a-input v-model="queryParam.dutyUnitId"/>
              </a-form-item>
            </a-col>
            <a-col :md="4">
              <a-form-item label="主讲人">
                <a-input v-model="queryParam.keynoteSpeaker"/>
              </a-form-item>
            </a-col>
            <a-col :md="5">
              <a-form-item label="培训时间">
                <a-range-picker v-model="queryParam.trainStartTime" style="width: 100%" :valueFormat="dateFormat"/>
              </a-form-item>
            </a-col>
            <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="recordResearch">查询</a-button>
                <a-button type="second" style="margin-left: 10px" @click="recordRefresh">重置</a-button>
              </span>
            </a-col>
          </a-row>
        </a-form>
      </div>
      <div class="table-operator">
        <a-button type="primary" icon="plus" @click="recordAdd">新增</a-button>
        <!--        <a-button icon="delete" :disabled="!hasSelected" @click="recordRemove">删除</a-button>-->
      </div>
      <s-table
        ref="recordTable"
        size="default"
        :scroll="{x:1000,y:`calc(100vh - 360px)`}"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        :rowSelection="false"
        showPagination="true"
      >
        <span slot="action" slot-scope="text, record">
          <template>
            <a @click="recordCheck(record)">查看</a>
            <!--            <a-divider type="vertical"/>-->
            <!--            <a @click="recordRemove(record)">删除</a>-->
          </template>
        </span>
      </s-table>
      <record-modal
        ref="recordModal"
        :modal-title="commonTitle"
        :visible="commonVisible"
        :disabled="commonDisabled"
        :model="commonData"
        @cancel="recordCancel"
        @ok="recordOk"
      />
      <orientation-detail
        mode="record"
        :visible="detailVisible"
        :info="detailInfo"
        @cancel="() => {this.detailVisible = false}"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { record } from '@/api/safety'
import { recordColumns } from '@/views/safety/table'
import RecordModal from '@/views/safety/orientation/component/RecordModal'
import OrientationDetail from '@/views/safety/orientation/component/OrientationDetail'

export default {
  components: {
    STable,
    RecordModal,
    OrientationDetail
  },
  data () {
    return {
      dateFormat: 'YYYY-MM-DD HH:mm:ss',
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        this.queryParam.trainStartTime && (this.queryParam.trainStartTime = typeof this.queryParam.trainStartTime === 'string' ? this.queryParam.trainStartTime : this.queryParam.trainStartTime.join())
        Object.assign(requestParameters, this.queryParam)
        return record.getRecordList(requestParameters).then(res => {
          return {
            totalCount: res.data.total,
            totalPage: res.data.pages,
            pageNo: res.data.current,
            pageSize: res.data.size,
            data: res.data.records
          }
        })
      },
      queryParam: {},
      // modal
      commonDisabled: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      // detail modal
      detailVisible: false,
      detailInfo: {}
    }
  },
  computed: {
    columns: () => recordColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    modalInit () {
      this.commonVisible = false
      this.commonTitle = ''
      this.commonData = {}
      this.commonDisabled = false
      const form = this.$refs.recordModal.form
      form.resetFields()
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    recordResearch () {
      this.$refs.recordTable.refresh(true)
      this.$refs.recordTable.clearSelected()
    },
    recordRefresh () {
      this.queryParam = {}
      this.$refs.recordTable.refresh(true)
      this.$refs.recordTable.clearSelected()
    },
    recordAdd () {
      this.commonTitle = '新增记录'
      this.commonVisible = true
    },
    recordRemoves () {
      const ids = this.selectedRows.map(item => {
        return item.id
      })
      this.removeConfirm(ids)
    },
    recordRemove (record) {
      this.removeConfirm([record.id])
    },
    removeConfirm (ids) {
      const _this = this
      this.$confirm({
        title: '提示',
        content: '将删除选中数据，是否确认？',
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          record.removeRecordList(ids).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.$refs.recordTable.refresh(true)
              _this.$refs.recordTable.clearSelected()
            } else {
              _this.$message.success('删除失败')
            }
          })
        }
      })
    },
    recordCheck (record) {
      this.detailInfo = { ...record }
      this.detailVisible = true
    },
    // recordEdit (record) {
    //   this.commonTitle = '编辑记录'
    //   this.commonData = { ...record }
    //   this.commonVisible = true
    // },
    recordCancel () {
      this.modalInit()
    },
    recordOk () {
      const _this = this
      const form = _this.$refs.recordModal.form
      form.validateFields((errors, values) => {
        if (!errors) {
          record.submitRecordList(values).then(res => {
            if (res.code === 200) {
              _this.$message.success('新增培训记录成功')
              _this.modalInit()
              _this.$refs.recordTable.refresh(true)
              _this.$refs.recordTable.clearSelected()
            }
          })
        } else {
          _this.$message.error('新增失败，请重新填写并提交')
        }
      })
    }
  }
}
</script>
<style lang="less" scoped>
/deep/ .ant-input[disabled] {
  color: #5c5a5a;
  background-color: #f5f4f4;
}
/deep/ .ant-table-wrapper {
  min-height: calc(100vh - 225px);
}
</style>
