
  <template>
  <!-- 隐患整改验收管理 -->
  <page-container :title="false">
    <a-card :bordered="false" style="margin: 15px" :body-style="{'padding-bottom': '0px'}">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col  :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="隐患名称">
                <a-input v-model="queryParam.name" placeholder="隐患名称"></a-input>
              </a-form-item>
            </a-col>
            <a-col  :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="上报日期">
                <a-range-picker
                  v-model="dateOfInvestigation"
                  :valueFormat="dateFormat"
                  @change="dataChange"
                  style="width: 100%; min-width: 110px;"
                />
              </a-form-item>
            </a-col>
            <a-col   :lg="6" :md="8" :sm="12" :xs="24">
              <a-form-item label="状态">
                <a-select style="width: 100%" v-model="queryParam.status" placeholder="状态">
                  <a-select-option v-for="d in inspectionStatusOptions" :key="d.value">{{ d.label }}</a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col  :lg="6" :md="24" :sm="12" :xs="24" class="table-page-search-submitButtons">
              <a-space>
                <a-button type="primary" @click="queryFormData">查询</a-button>
                <a-button type="reset" @click="resetQueryForm">重置</a-button>
              </a-space>
            </a-col>
          </a-row>
        </a-form>
      </div>
    </a-card>
    <a-card
      :bordered="false"
      style="margin: 15px"
      :headStyle="{ border: 'none', paddingBottom: 0 }"
      :bodyStyle="{ paddingBottom: 0 }"
      class="table-card"
    >
      <s-table
        style="
           {
            word-break: break-all;
          }
        "
        ref="table"
        size="default"
        :rowKey="(record) => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        showPagination="true"
        :scroll="{ y: `calc(100vh - 395px)`, x: 1380 }"
        :showIndexColumn="true"
      >
        <!-- 状态  -->
        <template slot="status" slot-scope="text, record">
          <a-tag
            :color="record.status | colorFilter"
            style="margin-right: 0 !important"
          >{{ record.status | textFilter }}</a-tag>
        </template>
        <!-- 状态 end -->

        <span slot="action" slot-scope="text, record">
          <template>
            <template>
              <a @click="viewReport(record)" v-if="record.status == 4">查看</a>
              <a @click="editReport(record)" v-if="record.status == 3">验收</a>
            </template>
          </template>
        </span>
      </s-table>
    </a-card>

    <!-- 弹出整改信息窗 -->
    <acceptanceModal
      :visible.sync="acceptanceModalVisible"
      :model="acceptanceModal"
      :modalTitle="'验收信息'"
      @cancel="reformViewcancel"
      @submitok="reformViewsubmit"
    ></acceptanceModal>

    <!-- 隐患详情查看弹窗弹框 -->
    <assessDetailView
      :visible.sync="riskViewModalVisible"
      :model="reportViewData"
      :isNewReport="true"
      :modalTitle="''"
      @cancel="cancelView"
    ></assessDetailView>
    <!-- / 隐患详情查看弹窗弹框 -->
  </page-container>
</template>

<script>
import { STable } from '@/components'
import RiskDetailModal from '@/views/safety/potentialrisk/components/riskDetail'
import { getDict } from '@/api/system'

import acceptanceApi from '@/api/acceptance'
import { potentialPlanColumns } from './acceptanceMgr/table'
import acceptanceModal from './acceptanceMgr/acceptanceModal.vue'
import assessDetailView from './assessMgr/assessDetailView'
import { mapGetters } from 'vuex'

export default {
  components: { STable, RiskDetailModal, acceptanceModal, assessDetailView },
  data() {
    return {
      dateFormat: 'YYYY-MM-DD',
      dateOfInvestigation: [],
      inspectionOptions: [],
      inspectionStatusOptions: [],
      modalTitle: '',
      isView: false,
      riskDetailModalVisible: false,
      checkinModalVisible: false,
      planDisabled: false,
      potentialRiskVisible: false,
      queryParam: {
        name: '',
        endTime: '',
        startTime: '',
        status: undefined
      },
      loadData: parameter => {
        let requesturl = acceptanceApi.acceptanceList
        let method = 'GET'
        let requestparams = {
          params: { current: parameter.pageNo, size: parameter.pageSize, ...this.queryParam },
          data: {}
        }
        let config = {
          headers: {
            'Content-Type': 'application/json;'
          }
        }
        return this.submitFormPost(requesturl, requestparams, method, config).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          // data.data = [{}]
          return data
        })
      },
      reportData: {},

      // 验收信息
      acceptanceModalVisible: false,
      acceptanceModal: {},

      // 隐患详情查看弹窗弹框
      riskViewModalVisible: false,
      reportViewData: {}
    }
  },
  created() {
    this.selectOptionsInit()
  },
  computed: {
    ...mapGetters(['permission', 'userInfo']),
    columns: () => potentialPlanColumns
  },
  filters: {
    colorFilter: function(value) {
      let key = value
      let color = ''
      switch (key) {
        case 3:
          color = 'orange'
          break
        case 4:
          color = 'green'
          break
        case 5:
          color = 'orange'
          break
        default:
          color = ''
          break
      }
      return color
    },
    textFilter: function(value) {
      let key = value
      let desc = ''
      switch (key) {
        case 3:
          desc = '未验收'
          break
        case 4:
          desc = '通过'
          break
        case 5:
          desc = '验收未通过'
          break
        default:
          desc = ''
          break
      }
      return desc
    }
  },
  methods: {
    okRiskDetail() {
      this.riskDetailModalVisible = false
    },
    cancelRiskDetail() {
      this.riskDetailModalVisible = false
    },
    selectOptionsInit() {
      getDict('PERIL_ACCEPT_STATUS').then(({ data, success }) => {
        if (success) {
          this.inspectionStatusOptions = data.map(row => {
            return {
              label: row.dictValue,
              value: row.dictKey
            }
          })
        }
      })
    },
    dataChange() {
      this.queryParam.startTime = this.dateOfInvestigation[0] + ' 00:00:00' // 计划开始时间
      this.queryParam.endTime = this.dateOfInvestigation[1] + ' 23:59:59' // 计划结束时间
    },
    queryFormData() {
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    resetQueryForm() {
      this.queryParam = {
        name: '',
        endTime: '',
        startTime: '',
        status: undefined
      }
      this.dateOfInvestigation = []
      this.acceptanceModalVisible = false
      this.riskViewModalVisible = false
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    openRiskModal(record, isView = false) {
      this.riskDetailModalVisible = true
      this.reportData = record
    },
    viewReport(record) {
      this.getAcceptanceData(record)
    },
    openViewModal(data) {
      this.riskViewModalVisible = true
      this.reportViewData = {
        process: 4,
        ...data
      }
    },
    getAcceptanceData(record) {
      let requesturl = acceptanceApi.acceptancesubmit
      let method = 'get'
      let requestparams = {
        params: { id: record.id },
        data: {}
      }
      let config = {
        headers: {
          'Content-Type': 'application/json'
        }
      }
      this.submitFormPost(requesturl, requestparams, method, config).then(res => {
        let data = res.data
        this.openViewModal(data)
      })
    },
    editReport(record) {
      this.getById(acceptanceApi.acceptancesubmit, record.id).then(({ data, success }) => {
        if (success) {
          if (Object.keys(data.acceptInformationVO).length > 1) {
            this.acceptanceModal = data.acceptInformationVO
          } else {
            this.acceptanceModal = {
              reportInformationId: record.id
            }
          }
          this.acceptanceModalVisible = true
        }
      })
    },
    edit(record) {
      this.openRiskModal(record)
      // this.isView = false
      // this.reportData = record
      // this.riskDetailModalVisible = true
      console.log('==== edit', record)
    },
    reportRisk(record) {
      console.log('==== reportRisk', record)
      this.$confirm({
        title: '你确定要提交吗？',
        // content: 'When clicked the OK button, this dialog will be closed after 1 second',
        onOk() {
          return new Promise((resolve, reject) => {
            resolve(true)
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {}
      })
    },
    revert(record) {
      console.log('==== revert', record)
      this.$confirm({
        title: '你确定要撤回吗？',
        // content: 'When clicked the OK button, this dialog will be closed after 1 second',
        onOk() {
          return new Promise((resolve, reject) => {
            resolve(true)
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {}
      })
    },

    reformViewcancel() {
      this.acceptanceModalVisible = false
    },
    reformViewsubmit(formData) {
      let requesturl = acceptanceApi.acceptancesubmit
      let method = 'post'
      let requestparams = {
        params: {},
        data: formData
      }
      let config = {
        headers: {
          'Content-Type': 'application/json'
        }
      }
      this.submitFormPost(requesturl, requestparams, method, config).then(res => {
        this.$message.success(`${res.msg}`)
        this.acceptanceModalVisible = false
        this.resetQueryForm()
      })
    }
  }
}
</script>


