<template>
  <page-container :title="false">
    <a-card :bordered="false" style="margin: 15px" :body-style="{'padding-bottom': '0px'}">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="8">
              <a-form-item label="危化品名称">
                <a-input v-model="queryParam.name" placeholder="危化品名称" />
              </a-form-item>
            </a-col>
            <a-col :md="16" class="table-page-search-submitButtons">
              <a-space>
                <a-button type="primary" @click="$refs.table.refresh(true)">查询</a-button>
                <a-button type="second" @click="searchReset">重置</a-button>
              </a-space>
            </a-col>
          </a-row>
        </a-form>
      </div>
    </a-card>
    <a-card
      :bordered="false"
      style="margin: 15px"
      :headStyle="{ border: 'none', paddingBottom: 0 }"
      :bodyStyle="{ paddingBottom: 0 }"
      class="table-card"
    >
      <template #title>
        <div class="text-right">
          <a-button
            type="primary"
            icon="plus"
            @click="addRecord"
            style="float: right"
            v-if="permission.chemicals_add"
          >新建</a-button>
        </div>
      </template>
      <s-table
        ref="table"
        size="default"
        :rowKey="(record) => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        showPagination="true"
        :scroll="{ y: `calc(100vh - 432px)`, x: 940 }"
      >
        <!-- 序号 -->
        <span
          slot="number"
          slot-scope="text, record, index"
        >{{ pagination.pageSize * (pagination.current - 1) + index + 1 }}</span>
        <!-- 序号 end -->
        <template slot="status" slot-scope="text, record">
          <a-tag :color="record.status | colorFilter" style="margin-right: 0 !important">
            {{
            record.status | textFilter
            }}
          </a-tag>
        </template>
        <span slot="action" slot-scope="text, record">
          <template>
            <a
              @click="editRecord(record)"
              v-if="record.status !== 1 && permission.chemicals_edit"
            >编辑</a>
            <a-divider type="vertical" v-if="record.status !== 1 && permission.chemicals_edit" />
            <a
              @click="publishRecord(record, 1)"
              v-if="record.status !== 1 && permission.chemicals_publish"
            >发布</a>
            <a-divider type="vertical" v-if="record.status !== 1 && permission.chemicals_publish" />
            <a @click="viewRecord(record)" v-if="record.status === 1">查看</a>
            <a-divider type="vertical" v-if="record.status !== 0" />
            <a
              @click="publishRecord(record, 0)"
              v-if="record.status === 1 && permission.chemicals_recall"
            >撤回</a>
            <a
              @click="removeRecord(record)"
              v-if="record.status !== 1 && permission.chemicals_remove"
            >删除</a>
          </template>
        </span>
      </s-table>
      <common-form-modal
        ref="modal"
        :visible.sync="commonVisible"
        :loading="confirmLoading"
        :model="commonData"
        :modal-title="commonTitle"
        :disabled="commonDisabled"
        :rule="rules"
        width="900px"
        @ok="modalOk"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { chemicalsColumns } from '@/views/safety/table'
import ApiURLs from '@/api/urls'
import { mapGetters } from 'vuex'
import { getStore } from '@/utils/store'
import CommonFormModal from '@/components/CommonFormModal/CommonFormModal'
import { getDict } from '@/api/system'

export default {
  name: 'rules',
  components: {
    STable,
    CommonFormModal
  },
  data() {
    return {
      pagination: {
        current: 1,
        pageSize: 10,
        showSizeChanger: true
      },
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      commonDisabled: false,
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        return this.getList(ApiURLs.chemicalsList, requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.pagination = {
            current: data.pageNo,
            pageSize: data.pageSize,
            showSizeChanger: true
          }
          return data
        })
      },
      userInfo: getStore({ name: 'info' }),
      rules: [
        {
          type: 'a-row',
          native: true,
          children: [
            {
              type: 'a-col',
              props: {
                span: 24
              },
              children: [
                {
                  type: 'input',
                  title: '危化品名称',
                  field: 'chemicalName',
                  props: {
                    maxLength: '32',
                    type: 'text',
                    placeholder: '请输入危化品名称'
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入危化品名称',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'a-col',
                  props: {
                    span: 24
                  },
                  children: [
                    {
                      type: 'input',
                      title: '存储方式',
                      field: 'storageMode',
                      props: {
                        maxLength: '32',
                        type: 'text',
                        placeholder: '请输入存储方式'
                      },
                      validate: [
                        {
                          required: true,
                          message: '请输入存储方式',
                          trigger: 'blur'
                        }
                      ],
                      wrap: {
                        labelCol: { span: 8 },
                        wrapperCol: { span: 14 }
                      },
                      col: {
                        span: 12
                      },
                      style: {
                        width: '100%'
                      }
                    },
                    {
                      type: 'input',
                      title: '存储地点',
                      field: 'storageAddress',
                      props: {
                        maxLength: '32',
                        type: 'text',
                        placeholder: '请输入存储地点'
                      },
                      validate: [
                        {
                          required: true,
                          message: '请输入存储地点',
                          trigger: 'blur'
                        }
                      ],
                      wrap: {
                        labelCol: { span: 6 },
                        wrapperCol: { span: 14 }
                      },
                      col: {
                        span: 12
                      },
                      style: {
                        width: '100%'
                      }
                    }
                  ]
                },
                {
                  type: 'textarea',
                  title: '理化性质',
                  field: 'physicochemicalProperty',
                  props: {
                    maxLength: '256',
                    type: 'text',
                    placeholder: '请输入理化性质'
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入理化性质',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'textarea',
                  title: '急救方式',
                  field: 'emergencyWay',
                  props: {
                    maxLength: '300',
                    type: 'text',
                    placeholder: '请输入急救方式'
                  },
                  validate: [
                    {
                      required: true,
                      message: '请输入急救方式',
                      trigger: 'blur'
                    }
                  ],
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                },
                {
                  type: 'UploadFile',
                  field: 'fileAttachmentIds',
                  title: 'MSDS',
                  props: {
                    fileList: []
                  },
                  wrap: {
                    labelCol: { span: 4 },
                    wrapperCol: { span: 18 }
                  }
                }
              ]
            }
          ]
        }
      ]
    }
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => chemicalsColumns,
    hasSelected() {
      return this.selectedRowKeys.length > 0
    },
    rowSelection() {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  filters: {
    colorFilter: function(value) {
      const colorList = ['orange', 'green']
      return colorList[value]
    },
    textFilter: function(value) {
      const textList = ['待发布', '已发布']
      return textList[value]
    }
  },
  methods: {
    initFormData() {
      this.commonData = {
        chemicalName: '',
        emergencyWay: '',
        fileAttachmentIds: [],
        id: '',
        physicochemicalProperty: '',
        storageAddress: '',
        storageMode: ''
      }
      this.setFileList([])
    },
    setFileList(fileList) {
      if (this.$refs.modal && this.$refs.modal.fApi.getRule) {
        this.$refs.modal.fApi.getRule('fileAttachmentIds').props.fileList = fileList
      } else {
        this.rules[0].children[0].children[4].props.fileList = fileList
      }
      console.log(this.rules[0].children[0].children[4])
    },
    commonInit() {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.initFormData()
    },
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    searchReset() {
      this.queryParam = {}
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord() {
      this.commonTitle = '新增数据'
      this.initFormData()
      this.commonVisible = true
      this.commonDisabled = false
    },
    removeRecords() {
      const ids = this.selectedRows.map(item => item.id)
      this.showDeleteConfirm(ids)
    },
    getRecord(id, callback) {
      this.getById(ApiURLs.chemicals, id).then(({ data, success }) => {
        if (success) {
          this.commonData = data
          const msdsList = data.msdsList.map(item => ({
            uid: item.id,
            name: item.fileName,
            status: 'done',
            id: item.id,
            filePath: item.fileUrlPath
          }))
          this.setFileList(msdsList)
          callback()
        }
      })
    },
    viewRecord(record) {
      this.commonTitle = '查看数据'
      this.initFormData()
      this.getRecord(record.id, () => {
        this.commonDisabled = true
        this.commonVisible = true
      })
    },
    editRecord(record) {
      this.commonTitle = '编辑数据'
      this.initFormData()
      this.getRecord(record.id, () => {
        this.commonDisabled = false
        this.commonVisible = true
      })
    },
    removeRecord(record) {
      this.showDeleteConfirm(record.id)
    },
    modalCancel() {
      this.commonInit()
    },
    modalOk({ formData }) {
      let method = ''
      if (this.isNotNullOrUndefined(this.commonData.id)) {
        method = 'put'
        formData.id = this.commonData.id
      } else {
        method = 'post'
      }

      const params = {
        ...formData,
        documentTypeName: this.commonData.documentTypeName,
        documentValidId: formData.documentValidId,
        fileAttachmentIds: this.$refs.modal.fApi.getRule('fileAttachmentIds').props.fileList.map(item => ({
          id: item.id,
          fileUrlPath: item.filePath,
          originalFileName: item.name
        }))
      }
      this.submitForm(ApiURLs.chemicals, params, method, {
        // headers: { 'Content-Type': 'multipart/form-data' }
      }).then(res => {
        this.$refs.table.refresh()
        this.$refs.table.clearSelected()
        this.commonInit()
      })
    },
    //发布、撤回
    publishRecord(record, status) {
      var publishData = {
        id: record.id,
        status: status
      }
      this.submitForm(ApiURLs.chemicals, publishData, 'put').then(res => {
        this.$message.success(`操作成功`)
        this.$refs.table.refresh()
        this.$refs.table.clearSelected()
      })
    },
    showDeleteConfirm(params) {
      const _this = this
      this.$confirm({
        title: '确定执行删除操作?',
        content: ' ',
        okText: '是',
        okType: 'danger',
        cancelText: '否',
        onOk() {
          _this.deleteByIds(`${ApiURLs.chemicals}?id=${params}`).then(res => {
            if (res.code === 200) {
              _this.$message.success('删除成功')
              _this.selectedRows = []
              _this.selectedRowKeys = []
              _this.$refs.table.refresh()
            } else {
              _this.$message.error('删除失败')
            }
          })
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    onTimeChange(value, dateString) {}
  },
  created() {}
}
</script>
<style lang="less" scoped>
/deep/ .ant-table {
  height: auto;
}

/deep/ .table-card .ant-card-body {
  padding-top: 0;
  padding-bottom: 15px;
}

/deep/ .ant-modal-body {
  span.ant-form-item-children {
    width: 100%;
    display: block;

    .ant-checkbox-group {
      width: 100%;
    }

    .ant-calendar-picker {
      display: block;
      width: 100%;
    }
  }
}

/deep/ .ant-table-body {
  overflow-y: auto !important;
}
</style>
