                    "
                  />
                </a-form-model-item>
              </a-col>
              <a-col :span="24">
                <a-form-model-item ref="postName" label="三违照片" prop="postName">
                  <a-button type="primary" @click="commonVisibleImg1 = true">查看</a-button>
                  <a-modal
                    title="查看附件"
                    class="commonVisibleImg"
