                  prop="processorName"
                  :label-col="{ span: 6 }"
                  :wrapper-col="{ span: 18 }"
                >
                  <a-input
                    :disabled="commonDisabled"
                    v-model="commonData.processorName"
                    @blur="
                      () => {
                        $refs.processorName.onFieldBlur()
                      }
                    "
                  />
                </a-form-model-item>
              </a-col>
            </a-row>
          </div>
          <div v-if="commonDisabled">
            <div class="contentTitle">处理信息</div>
            <div>
              <a-row :gutter="24">
                <a-col :span="24">
                  <a-form-model-item
                    ref="result"
