                    >
                      <a-radio :value="0">关闭</a-radio>
                      <a-radio :value="1">处罚</a-radio>
                    </a-radio-group>
                  </a-form-model-item>
                </a-col>
                <div v-if="this.publishData.result === 1">
                  <a-col :span="12">
                    <a-form-model-item
                      class="dealType"
                      ref="type"
                      label="处理方式"
                      prop="type"
                      :label-col="{ span: 6 }"
