                    >
                      <a-row :gutter="4" style="margin-right: 0!important;margin-left: 0!important;">
                        <a-col :span="this.publishData.type === 1?14:24">
                          <a-select
                            style="width: 100%"
                            :disabled="commonDisabled && canClick"
                            v-model="publishData.type"
