                          }
                        "
                          />
                          <span
                            v-if="this.publishData.type === 1"
                            style="width:5%;display:inline-block;padding-left:2%;"
                          >元</span>-->
                        </a-col>
                        <a-col :span="10" v-if="this.publishData.type === 1">
                          <a-input
                            style="width: 100%"
                            :disabled="commonDisabled && canClick"
                            v-model="publishData.penaltyAmount"
                            suffix="元"
                            @blur="
                          () => {
