</template>
<script>
import { STable } from '@/components'
import { dealColumns } from '@/views/safety/table'
import ApiURLs from '@/api/urls'
import { mapGetters } from 'vuex'
import { getStore } from '@/utils/store'
import { getDict } from '@/api/system'
import { getPersonnelTree } from '@/api/safety'
import PersonSelectModal from '@/views/safety/task/component/PersonSelectModal'

export default {
  name: 'registry',
  components: {
    STable,
    PersonSelectModal
  },
  data() {
    return {
      pagination: {
        current: 1,
        pageSize: 10,
        showSizeChanger: true
      },
      peopleVisible: false,
      confirmLoadingPeople: false,
      autoExpandParent: true,
      commonVisibleImg: false,
      commonVisibleImg1: false,
      imgList: [],

      templateTypeNameOptions: [],
      trainWayNameOptions: [
        {
          dictKey: 0,
          id: '',
          dictValue: '未提交'
        },
        {
          dictKey: 1,
          id: '',
          dictValue: '已提交'
        },
        {
          dictKey: 2,
          id: '',
          dictValue: '已处理'
        }
      ],

      labelCol: { span: 3 },
      wrapperCol: { span: 21 },
      confirmLoading: false,
      commonTitle: '',
      commonVisible: false,
      commonData: {},
      publishData: {
        id: '',
        penaltyAmount: '',
        processGist: '',
        result: '',
        status: 1,
        type: '',
        violationId: ''
      },
      commonDisabled: false,
      canClick: false,
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        return this.getList(ApiURLs.dealList, requestParameters).then(res => {
          const data = {}
          data.totalCount = res.data.total
          data.totalPage = res.data.pages
          data.pageNo = res.data.current
          data.pageSize = res.data.size
          data.data = res.data.records
          this.pagination = {
            current: data.pageNo,
            pageSize: data.pageSize,
            showSizeChanger: true
          }
          return data
        })
      },
      userInfo: getStore({ name: 'info' }),
      newDate: '',
      versionData: '',
      rules: {
        result: [{ required: true, message: '请选择处理结果', trigger: 'change' }],
        type: [{ required: true, message: '请选择处理方式', trigger: 'change' }],
        penaltyAmount: [{ required: true, message: '请填写处理金额', trigger: 'blur' }],
        processGist: [{ required: true, message: '请填写处理依据', trigger: 'blur' }]
      }
    }
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => dealColumns,
    hasSelected() {
      return this.selectedRowKeys.length > 0
    },
    rowSelection() {
      return {
        selectedRowKeys: this.selectedRowKeys
      }
    }
  },
  filters: {
    colorFilter: function(value) {
      const colorList = ['', 'orange', 'green', 'red']
      return colorList[value]
    },
    textFilter: function(value) {
      const textList = ['', '未处理', '已处理', '关闭']
      return textList[value]
    }
  },
  mounted() {},
  methods: {
    //时间格式转换
    onChange(date, dateString) {
      this.commonData.violationTime = dateString
    },
    //关闭弹框
    handleCancelModal() {
      this.commonVisibleImg = false
      this.commonVisibleImg1 = false
    },
    // 获取人员数据
    getSelectOptions() {
      getPersonnelTree(0).then(res => {
        this.signOrgNameOptions = res.data
      })
    },

    initFormData() {
      this.commonData = {
        description: '',
        files: [],
        id: '',
        processTime: '',
        processorId: '',
        processorName: '',
        registerTime: '',
        registrantName: '',
        status: '',
        submitTime: '',
        submitterName: '',
        violationAddress: '',
        violationDeptId: '',
        violationDeptName: '',
        violationPersonId: '',
        violationPersonName: '',
        violationTime: '',
        violationType: ''
      }
    },
    commonInit() {
      this.commonVisible = false
      this.commonDisabled = false
      this.commonTitle = ''
      this.confirmLoading = false
      this.initFormData()
    },
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    searchReset() {
      this.queryParam = {}
      this.$refs.table.refresh(true)
      this.$refs.table.clearSelected()
    },
    addRecord() {
      this.commonTitle = '新增数据'
      this.commonVisible = true
      this.commonDisabled = false
      this.initFormData()
    },
    viewRecord(record) {
      this.commonTitle = '查看数据'
      this.getRecord(record.id, () => {
        this.commonDisabled = true
        this.canClick = true
        this.commonVisible = true
      })
    },
    //查看详情
    getRecord(id, callback) {
      this.getById(ApiURLs.deal, id).then(({ data, success }) => {
        if (success) {
          this.commonData = JSON.parse(JSON.stringify(data.threeViolateVO))
          if (this.commonTitle === '查看数据') {
            this.publishData = JSON.parse(JSON.stringify(data))
          }
          this.commonData.violationType = this.commonData.violationTypeName
          this.imgList = []
          this.imgList = this.commonData.files.map(
            item => process.env.VUE_APP_API_BASE_URL + '/spang-safety/file' + item.filePath
          )
          console.log(this.imgList, 999)
          callback()
        }
      })
    },
    viewFiles(record) {
      this.commonVisibleImg = true
      this.commonDisabled = true
      this.filesList = true
      this.commonTitle = '查看附件'
      this.imgList = []
      this.imgList = record.files.map(item => process.env.VUE_APP_API_BASE_URL + '/spang-safety/file' + item.filePath)
    },
    //撤回
    withdrawRecord(record) {
      const requestParameters = { violationId: record.id }
      this.submitForm(ApiURLs.deal, {}, 'put', { params: requestParameters }).then(({ success }) => {
        if (success) {
          this.$message.success(`操作成功`)
          this.searchReset()
        }
      })
    },
    //处理
    publishRecord(record, status) {
      this.commonTitle = '处理数据'
      this.publishData.violationId = record.id
      this.getRecord(record.id, () => {
        this.commonDisabled = true
        this.commonVisible = true
        this.canClick = false
      })
    },
    modalCancel() {
      this.commonInit()
    },
    //保存
    modalOk(data) {
      this.$refs.ruleForm.validate(valid => {
        if (valid) {
          if (this.publishData.type === '1' && this.publishData.penaltyAmount === '') {
            this.$message.warning('请填写处罚金额')
          } else {
            console.log(this.publishData)
            this.submitForm(ApiURLs.deal, this.publishData, 'post').then(({ success }) => {
              if (success) {
                this.$message.success(`操作成功`)
                this.searchReset()
                this.commonVisible = false
              }
            })
          }
        } else {
          console.log('error submit!!')
          return false
        }
      })
    },
    //根据单位ID查询单位名称
    getTreeName(list, id) {
      let _this = this
      for (let i = 0; i < list.length; i++) {
        let a = list[i]
        if (a.id == id) {
          this.commonData.violationDeptName = a.title
        } else {
          if (a.children && a.children.length > 0) {
            let res = _this.getTreeName(a.children, id)
            if (res) {
              return res
            }
          }
        }
      }
    },

    onTimeChange(value, dateString) {},
    //关闭弹框
    handleCancel() {
      this.commonVisible = false
    }
  },
  created() {
    //三违行为类型
    this.getDict('PERIL_VIOLATE_TYPE').then(({ data, success }) => {
      if (success) {
        this.templateTypeNameOptions = data
      }
    })
    //违规人部门
    this.getSelectOptions()
  },
  watch: {
    commonVisible(val, newVal) {
      if (val) {
        try {
          this.$refs['ruleForm'].resetFields()
        } catch (e) {}
      }
    }
  }
}
</script>
