<template>
  <page-container :title="false">
    <a-card :bordered="false">
      <div class="table-page-search-wrapper">
        <a-form layout="inline">
          <a-row :gutter="24">
            <a-col :md="4">
              <a-form-item label="关键字">
                <a-input v-model="queryParam.keyword"/>
              </a-form-item>
            </a-col>
            <a-col :md="4">
              <a-form-item label="评价级别">
                <a-select v-model="queryParam.evaluationLevel">
                  <a-select-option v-for="val in riskEvaluationLevel" :key="val.dictKey">
                    {{ val.dictValue }}
                  </a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :md="4">
              <a-form-item label="管控级别">
                <a-select v-model="queryParam.controlLevel">
                  <a-select-option v-for="val in riskControlLevel" :key="val.dictKey">
                    {{ val.dictValue }}
                  </a-select-option>
                </a-select>
              </a-form-item>
            </a-col>
            <a-col :md="6">
              <a-form-item label="辨识时间">
                <a-range-picker v-model="queryParam.findingTime" :valueFormat="dateFormat"/>
              </a-form-item>
            </a-col>
            <a-col :md="2">
              <span class="table-page-search-submitButtons">
                <a-button type="primary" @click="technologyResearch">查询</a-button>
                <a-button type="second" style="margin-left: 10px" @click="technologyRefresh">重置</a-button>
              </span>
            </a-col>
          </a-row>
        </a-form>
      </div>
      <div class="table-operator">
        <a-button type="primary" icon="plus" @click="technologyAdd" v-if="permission.risk_technology_add">新增</a-button>
        <a-button icon="delete" :disabled="!hasSelected" @click="technologyRemove" v-if="permission.risk_technology_delete">删除</a-button>
      </div>
      <s-table
        ref="technologyTable"
        size="default"
        :scroll="{x: 500, y: `calc(100vh - 350px)`}"
        :rowKey="record => record.id"
        :columns="columns"
        :data="loadData"
        :alert="false"
        :rowSelection="rowSelection"
        showPagination="false"
      >
        <span slot="action" slot-scope="text, record">
          <template>
            <a @click="technologyCheck(record)" v-if="permission.risk_technology_view">查看</a>
            <a-divider type="vertical"/>
            <a @click="technologyEdit(record)" v-if="permission.risk_technology_edit">编辑</a>
          </template>
        </span>
      </s-table>
      <risk-modal
        ref="modal"
        mode="technology"
        :modal-title="title"
        :visible="visible"
        :disabled="modalDisabled"
        :model="modalData"
        :risk-control-level="riskControlLevel"
        :risk-evaluation-level="riskEvaluationLevel"
        @cancel="technologyCancel"
        @ok="technologyOk"
      />
      <risk-detail
        mode="technology"
        :visible="detailVisible"
        :info="detailInfo"
        @cancel="() => {this.detailVisible = false}"
      />
    </a-card>
  </page-container>
</template>
<script>
import { STable } from '@/components'
import { technologyColumns } from '@/views/safety/table'
import { technology, getRiskControlLevel, getRiskEvaluationLevel } from '@/api/safety'
import RiskModal from '@/views/safety/risk/component/RiskModal'
import { mapGetters } from 'vuex'
import RiskDetail from '@/views/safety/risk/component/RiskDetail'

export default {
  components: {
    STable,
    RiskModal,
    RiskDetail
  },
  data () {
    return {
      dateFormat: 'YYYY-MM-DD HH:mm:ss',
      queryParam: {},
      selectedRowKeys: [],
      selectedRows: [],
      loadData: parameter => {
        const requestParameters = { current: parameter.pageNo, size: parameter.pageSize }
        Object.assign(requestParameters, this.queryParam)
        return technology.getTechnologyList(requestParameters).then(res => {
          return {
            totalCount: res.data.total,
            totalPage: res.data.pages,
            pageNo: res.data.current,
            pageSize: res.data.size,
            data: res.data.records
          }
        })
      },
      // select options
      riskControlLevel: [],
      riskEvaluationLevel: [],
      // modal
      title: '',
      visible: false,
      modalData: {},
      modalDisabled: false,
      // detail modal
      detailVisible: false,
      detailInfo: {}
    }
  },
  created () {
    this.selectOptionsInit()
  },
  computed: {
    ...mapGetters(['permission']),
    columns: () => technologyColumns,
    hasSelected () {
      return this.selectedRowKeys.length > 0
    },
    rowSelection () {
      return {
        selectedRowKeys: this.selectedRowKeys,
        onChange: this.onSelectChange
      }
    }
  },
  methods: {
    selectOptionsInit () {
      getRiskEvaluationLevel().then(res => {
        this.riskEvaluationLevel = res.data
      })
      getRiskControlLevel().then(res => {
        this.riskControlLevel = res.data
      })
    },
    modalInit () {
      this.visible = false
      this.modalData = {}
      this.modalDisabled = false
      this.$refs.modal.form.resetFields()
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    technologyResearch () {
      this.$refs.technologyTable.refresh(true)
      this.$refs.technologyTable.clearSelected()
    },
    technologyRefresh () {
      this.queryParam = {}
      this.$refs.technologyTable.refresh(true)
      this.$refs.technologyTable.clearSelected()
    },
    technologyRemove () {
      const ids = this.selectedRows.map(item => {
        return item.id
      })
      this.removeConfirm(ids.join())
    },
    removeConfirm (ids) {
      const _this = this
      this.$confirm({
        title: '提示',
        content: '将要删除选中数据，是否确认',
        okText: '确认',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          technology.removeTechnologyList(ids).then(res => {
            if (res.code === 200) {
              _this.$message.success(`${ res.msg }`)
              _this.modalInit()
              _this.$refs.technologyTable.refresh(true)
              _this.$refs.technologyTable.clearSelected()
            } else {
              _this.$message.error(`${ res.msg }`)
            }
          })
        }
      })
    },
    technologyAdd () {
      this.title = '新增工艺风险'
      this.visible = true
    },
    technologyCheck (record) {
      // technology.detailTechnologyList(record.id).then(res => {
      //   this.detailInfo = { ...res.data }
      //   this.detailVisible = true
      // })
      this.detailInfo = { ...record }
      this.detailVisible = true
    },
    technologyEdit (record) {
      this.title = '编辑'
      this.modalData = { ...record }
      this.visible = true
    },
    technologyCancel () {
      this.modalInit()
    },
    technologyOk () {
      const _this = this
      const form = this.$refs.modal.form
      form.validateFields((errors, values) => {
        if (!errors) {
          values.id = this.modalData.id
          technology.submitTechnologyList(values).then(res => {
            if (res.code === 200) {
              _this.$message.success(`${ res.msg }`)
              _this.modalInit()
              _this.$refs.technologyTable.refresh(true)
              _this.$refs.technologyTable.clearSelected()
            } else {
              _this.$message.error(`${ res.msg }`)
            }
          })
        } else {
          _this.$message.error('请检查表单格式后重试')
        }
      })
    }
  }
}
</script>
<style lang="less" scoped>
/deep/ .ant-card-body{
  .ant-table-wrapper {
    min-height: calc(100vh - 225px)
  }
}
/deep/ .ant-table{
  height: calc(100vh - 290px)!important;
}
</style>
