<template>
  <a-modal
    :title="modalTitle"
    :width="650"
    :visible="visible"
    :confirmLoading="loading"
    @ok="() => { $emit('ok') }"
    @cancel="() => { $emit('cancel') }"
    ok-text="提交"
    cancel-text="取消"
    :keyboard="false"
    :maskClosable="false"
  >
    <a-spin :spinning="loading">
      <a-form :form="form" :label-col="{ span: 7 }" :wrapper-col="{ span: 12 }">
        <a-form-item label="文件编号">
          <a-input
            v-decorator="['documentCode',{ rules:[{ required: true, message: '请填写文件编号' }]}]"
            :disabled="disabled"
          />
        </a-form-item>
        <a-form-item label="文件名称">
          <a-input
            v-decorator="['documentName',{ rules:[{ required: true, message: '请填写文件名称' }]}]"
            :disabled="disabled"
          />
        </a-form-item>
        <a-form-item label="文件类别">
          <a-select
            v-decorator="['documentTypeName',{ rules:[{ required: true, message: '请选择文件类别' }]}]"
            :disabled="disabled"
          >
            <a-select-option v-for="d in docTypes" :key="d.dictKey">{{ d.dictValue }}</a-select-option>
          </a-select>
        </a-form-item>
        <a-form-item label="发布机构">
          <a-input
            v-decorator="['releaseOrgName',{ rules:[{ required: true, message: '请填写发布机构' }]}]"
            :disabled="disabled"
          />
        </a-form-item>
        <a-form-item label="发布日期">
          <a-date-picker
            style="width: 100%"
            v-decorator="['releaseDate',{ rules:[{ required: true, message: '请选择发布日期' }]}]"
            :disabled="disabled"
            valueFormat="YYYY-MM-DD hh:mm:ss"
          />
        </a-form-item>
        <a-form-item label="文件有效性">
          <a-select
            v-decorator="['documentValidType',{ rules:[{ required: true, message: '请选择文件有效性' }]}]"
            :disabled="disabled"
          >
            <a-select-option
              v-for="val in docValids"
              :key="val.dictKey"
            >{{ val.dictValue }}</a-select-option>
          </a-select>
        </a-form-item>
        <a-form-item label="标签">
          <a-input
            v-decorator="['notes',{ rules:[{ required: true, message: '请填写标签' }]}]"
            :disabled="disabled"
          />
        </a-form-item>
        <file-upload v-model="form.fileList" :uploadResult="getFileData"/>
      </a-form>
    </a-spin>
  </a-modal>
</template>

<script>
import pick from 'lodash.pick'
import FileUpload from '@/components/UploadFile'
import { getDict } from '@/api/system'
// 字段
const fields = ['fileName', 'type', 'deptName', 'personName', 'id', 'publishUnit', 'publishTime', 'isEffective', 'tag']

export default {
  name: 'RulesModal',
  components: {
    FileUpload
  },
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    loading: {
      type: Boolean,
      default: () => false
    },
    model: {
      type: Object,
      default: () => null
    },
    modalTitle: {
      type: String,
      default: '操作'
    },
    disabled: {
      type: Boolean,
      default: false
    },
    docTypes:{
      type: Object,
      default: () => null
    },
    docValids: {
      type: Object,
      default: () => null
    }
  },
  data() {
    return {
      form: this.$form.createForm(this),
    }
  },
  created() {
    this.getDict()
    // 防止表单未注册
    fields.forEach(v => this.form.getFieldDecorator(v))
    // 当 model 发生改变时，为表单设置值
    this.$watch('model', () => {
      if (Object.keys(this.model).length > 0) {
        this.model.type = typeof this.model.type === 'string' ? this.model.type : this.model.type.toString()
        this.model.isEffective =
          typeof this.model.isEffective === 'string' ? this.model.isEffective : this.model.isEffective.toString()
      }
      this.model && this.form.setFieldsValue(pick(this.model, fields))
    })
  },
  methods: {
    getFileData(data){
      console.log(data,111);
    },
    //获取字典数据
    getDict() {
      //文件类型下拉框数据
      getDict('DOC_TYPE').then(({ data }) => {
        this.docTypes = data
      })
      //文件有效性下拉框数据
      getDict('DOC_VALID').then(({ data }) => {
        this.docValids = data
      })
    }
  }
}
</script>
